import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
    // address: "",
    // date_of_birth: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    const initializeUser = async () => {
      const storedUser = localStorage.getItem("user");
      const token = localStorage.getItem("token");

      console.log("ðŸ” Auth check:", {
        hasUser: !!storedUser,
        hasToken: !!token,
        token: token ? "Exists" : "Missing"
      });

      if (!storedUser || !token) {
        setMessage({ 
          type: "error", 
          text: "Please login to edit profile. Redirecting to login..." 
        });
        setTimeout(() => navigate("/login"), 2000);
        return;
      }

      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        const userId = parsedUser.userId || parsedUser.user_id;
        if (!userId) {
          setMessage({ type: "error", text: "User ID not found" });
          return;
        }

        console.log("ðŸ†” Loading profile for user ID:", userId);
        setLoading(true);

        // Kiá»ƒm tra token trÆ°á»›c
        const isValidToken = await userService.validateToken();
        if (!isValidToken) {
          setMessage({ 
            type: "error", 
            text: "Session expired. Please login again." 
          });
          setTimeout(() => {
            localStorage.removeItem("user");
            localStorage.removeItem("token");
            navigate("/login");
          }, 2000);
          return;
        }

        const userData = await userService.getUserProfile(userId);
        console.log("âœ… User profile loaded:", userData);

        setFormData({
          first_name: userData.first_name || "",
          last_name: userData.last_name || "",
          email: userData.email || "",
          mobile_number: userData.mobile_number || "",
          address: userData.address || "",
          date_of_birth: userData.date_of_birth || ""
        });

      } catch (error) {
        console.error("âŒ Error loading user profile:", error);

        if (error.response?.status === 401) {
          setMessage({ 
            type: "error", 
            text: "Authentication failed. Please login again." 
          });
          // Clear storage vÃ  redirect
          localStorage.removeItem("user");
          localStorage.removeItem("token");
          setTimeout(() => navigate("/login"), 2000);
        } else {
          setMessage({ 
            type: "error", 
            text: "Failed to load profile data. Please try again." 
          });
        }
      } finally {
        setLoading(false);
      }
    };

    initializeUser();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setMessage({ type: "error", text: "User not found" });
      return;
    }

    const userId = user.userId || user.user_id;
    if (!userId) {
      setMessage({ type: "error", text: "User ID not found" });
      return;
    }

    // Kiá»ƒm tra required fields
    if (!formData.first_name.trim() || !formData.last_name.trim() || !formData.email.trim()) {
      setMessage({ type: "error", text: "Please fill in all required fields" });
      return;
    }

    setSaving(true);
    setMessage({ type: "", text: "" });

    try {
      console.log("ðŸ“¤ Submitting form data:", formData);

      // Sá»­ dá»¥ng updateUserProfile thay vÃ¬ updateUser trá»±c tiáº¿p
      const response = await userService.updateUserProfile(userId, formData);
      console.log("âœ… Update response:", response);

      setMessage({ 
        type: "success", 
        text: "Profile updated successfully! Redirecting..." 
      });

      // Cáº­p nháº­t localStorage vá»›i thÃ´ng tin má»›i
      const updatedUser = { 
        ...user, 
        ...formData,
        userId: userId,
        user_id: userId
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      // Reload page sau 2 giÃ¢y Ä‘á»ƒ cáº­p nháº­t header
      setTimeout(() => {
        window.location.reload();
      }, 2000);

    } catch (error) {
      console.error("âŒ Error updating profile:", error);

      let errorMessage = "Failed to update profile";

      if (error.response) {
        switch (error.response.status) {
          case 401:
            errorMessage = "Authentication failed. Please login again.";
            // Clear storage vÃ  redirect
            localStorage.removeItem("user");
            localStorage.removeItem("token");
            setTimeout(() => navigate("/login"), 2000);
            break;
          case 403:
            errorMessage = "You don't have permission to update this profile.";
            break;
          case 404:
            errorMessage = "User not found.";
            break;
          case 422:
            errorMessage = "Invalid data. Please check your input.";
            break;
          default:
            errorMessage = error.response.data?.message || errorMessage;
        }
      } else if (error.message) {
        errorMessage = error.message;
      }

      setMessage({ 
        type: "error", 
        text: errorMessage 
      });
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    navigate(-1);
  };

  if (loading) {
    return (
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-8">
            <div className="card">
              <div className="card-body text-center py-5">
                <div className="spinner-border text-primary mb-3" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
                <p>Loading your profile...</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h2 className="mb-0">Edit Profile</h2>
            <button 
              className="btn btn-outline-secondary"
              onClick={handleCancel}
              disabled={saving}
            >
              <i className="fas fa-arrow-left me-2"></i>
              Back
            </button>
          </div>

          <div className="card shadow-sm">
            <div className="card-header bg-primary text-white">
              <h5 className="card-title mb-0">
                <i className="fas fa-user-edit me-2"></i>
                Personal Information
              </h5>
            </div>

            <div className="card-body">
              {message.text && (
                <div 
                  className={`alert ${
                    message.type === "success" ? "alert-success" : "alert-danger"
                  } alert-dismissible fade show`} 
                  role="alert"
                >
                  <i className={`fas ${
                    message.type === "success" ? "fa-check-circle" : "fa-exclamation-triangle"
                  } me-2`}></i>
                  {message.text}
                  <button 
                    type="button" 
                    className="btn-close" 
                    onClick={() => setMessage({ type: "", text: "" })}
                  ></button>
                </div>
              )}

              <form onSubmit={handleSubmit}>
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label htmlFor="first_name" className="form-label">
                      <i className="fas fa-user me-1 text-muted"></i>
                      First Name *
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="first_name"
                      name="first_name"
                      value={formData.first_name}
                      onChange={handleChange}
                      required
                      disabled={saving}
                      placeholder="Enter your first name"
                    />
                  </div>

                  <div className="col-md-6 mb-3">
                    <label htmlFor="last_name" className="form-label">
                      <i className="fas fa-user me-1 text-muted"></i>
                      Last Name *
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="last_name"
                      name="last_name"
                      value={formData.last_name}
                      onChange={handleChange}
                      required
                      disabled={saving}
                      placeholder="Enter your last name"
                    />
                  </div>
                </div>

                <div className="mb-3">
                  <label htmlFor="email" className="form-label">
                    <i className="fas fa-envelope me-1 text-muted"></i>
                    Email Address *
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    disabled={saving}
                    placeholder="your.email@example.com"
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="mobile_number" className="form-label">
                    <i className="fas fa-phone me-1 text-muted"></i>
                    Mobile Number
                  </label>
                  <input
                    type="tel"
                    className="form-control"
                    id="mobile_number"
                    name="mobile_number"
                    value={formData.mobile_number}
                    onChange={handleChange}
                    disabled={saving}
                    placeholder="+84 123 456 789"
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="address" className="form-label">
                    <i className="fas fa-home me-1 text-muted"></i>
                    Address
                  </label>
                  <textarea
                    className="form-control"
                    id="address"
                    name="address"
                    rows="3"
                    value={formData.address}
                    onChange={handleChange}
                    disabled={saving}
                    placeholder="Enter your full address"
                  />
                </div>

                <div className="mb-4">
                  <label htmlFor="date_of_birth" className="form-label">
                    <i className="fas fa-birthday-cake me-1 text-muted"></i>
                    Date of Birth
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    id="date_of_birth"
                    name="date_of_birth"
                    value={formData.date_of_birth}
                    onChange={handleChange}
                    disabled={saving}
                  />
                </div>

                <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                  <button
                    type="button"
                    className="btn btn-outline-secondary me-md-2"
                    onClick={handleCancel}
                    disabled={saving}
                  >
                    <i className="fas fa-times me-2"></i>
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={saving}
                  >
                    {saving ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </span>
                        Saving...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-save me-2"></i>
                        Save Changes
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>

            <div className="card-footer bg-light">
              <small className="text-muted">
                <i className="fas fa-info-circle me-1"></i>
                Fields marked with * are required
              </small>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
