import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import Swal from "sweetalert2";
import { Eye, Edit3, CheckCircle, XCircle, Search } from "lucide-react";

const OrderListAD = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filterStatus, setFilterStatus] = useState("");
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0, totalElements: 0 });

  // ================= FETCH ORDERS =================
  const fetchOrders = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
      
      // G·ªçi API GET /api/admin/orders (M√†y c√≥ th·ªÉ th√™m param status n·∫øu BE h·ªó tr·ª£)
      const res = await api.get(`/admin/orders?pageNumber=${page}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setOrders(res.data.content || []);
      setPageConfig({
        pageNumber: res.data.pageNumber,
        totalPages: res.data.totalPages,
        totalElements: res.data.totalElements
      });
    } catch (err) {
      console.error("‚ùå L·ªói l·∫•y ƒë∆°n h√†ng:", err);
      Swal.fire("L·ªói", "Kh√¥ng th·ªÉ t·∫£i danh s√°ch ƒë∆°n h√†ng", "error");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchOrders(0);
  }, [fetchOrders]);

  // ================= C·∫¨P NH·∫¨T TR·∫†NG TH√ÅI (Duy·ªát/H·ªßy) =================
  const updateOrderStatus = async (orderId, newStatus) => {
    const confirm = await Swal.fire({
      title: `X√°c nh·∫≠n chuy·ªÉn ƒë∆°n sang [${newStatus}]?`,
      icon: "question",
      showCancelButton: true,
      confirmButtonText: "ƒê·ªìng √Ω",
      cancelButtonText: "H·ªßy"
    });

    if (confirm.isConfirmed) {
      try {
        const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
        // API: PUT /api/admin/orders/{orderId}/status
        await api.put(`/admin/orders/${orderId}/status?status=${newStatus}`, {}, {
          headers: { Authorization: `Bearer ${token}` }
        });

        Swal.fire("Th√†nh c√¥ng!", `ƒê∆°n h√†ng ƒë√£ ƒë∆∞·ª£c chuy·ªÉn th√†nh ${newStatus}`, "success");
        fetchOrders(pageConfig.pageNumber); // Load l·∫°i trang hi·ªán t·∫°i
      } catch (err) {
        Swal.fire("L·ªói", "C·∫≠p nh·∫≠t tr·∫°ng th√°i th·∫•t b·∫°i", "error");
      }
    }
  };

  // H√†m helper ƒë·ªÉ render Badge m√†u s·∫Øc cho Status
  const renderStatusBadge = (status) => {
    const colors = {
      "PENDING": "bg-warning text-dark",
      "SHIPPED": "bg-info text-white",
      "DELIVERED": "bg-success text-white",
      "CANCELLED": "bg-danger text-white"
    };
    return <span className={`badge ${colors[status] || "bg-secondary"}`}>{status}</span>;
  };

  return (
    <div className="card border-0 shadow-sm">
      <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
        <h5 className="m-0 fw-bold">üì¶ QU·∫¢N L√ù ƒê∆†N H√ÄNG</h5>
        <div className="d-flex gap-2">
          <select 
            className="form-select form-select-sm" 
            style={{ width: "150px" }}
            onChange={(e) => setFilterStatus(e.target.value)}
          >
            <option value="">T·∫•t c·∫£ tr·∫°ng th√°i</option>
            <option value="PENDING">ƒêang ch·ªù</option>
            <option value="SHIPPED">ƒêang giao</option>
            <option value="DELIVERED">ƒê√£ giao</option>
            <option value="CANCELLED">ƒê√£ h·ªßy</option>
          </select>
        </div>
      </div>

      <div className="table-responsive">
        <table className="table table-hover align-middle mb-0 text-start">
          <thead className="table-light">
            <tr>
              <th>M√£ ƒë∆°n</th>
              <th>Kh√°ch h√†ng</th>
              <th>Ng√†y ƒë·∫∑t</th>
              <th>T·ªïng ti·ªÅn</th>
              <th>Tr·∫°ng th√°i</th>
              <th className="text-center">Thao t√°c</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6" className="text-center py-5">ƒêang t·∫£i d·ªØ li·ªáu ƒë∆°n h√†ng...</td></tr>
            ) : orders.map((order) => (
              <tr key={order.orderId}>
                <td className="fw-bold text-primary">#{order.orderId}</td>
                <td>
                  <div className="fw-bold small">{order.email}</div>
                  <div className="text-muted" style={{ fontSize: "11px" }}>{order.orderItems?.length || 0} s·∫£n ph·∫©m</div>
                </td>
                <td className="small">{order.orderDate}</td>
                <td className="fw-bold">{order.totalAmount?.toLocaleString()}ƒë</td>
                <td>{renderStatusBadge(order.orderStatus)}</td>
                <td className="text-center">
                  <div className="btn-group shadow-sm">
                    {/* Xem chi ti·∫øt (N·∫øu c√≥ Modal chi ti·∫øt sau n√†y) */}
                    <button className="btn btn-sm btn-outline-secondary" title="Xem chi ti·∫øt">
                      <Eye size={16} />
                    </button>
                    
                    {/* N√∫t Duy·ªát nhanh sang SHIPPED n·∫øu ƒëang PENDING */}
                    {order.orderStatus === "PENDING" && (
                      <button 
                        className="btn btn-sm btn-outline-success" 
                        title="Duy·ªát giao h√†ng"
                        onClick={() => updateOrderStatus(order.orderId, "SHIPPED")}
                      >
                        <CheckCircle size={16} />
                      </button>
                    )}

                    {/* N√∫t H·ªßy ƒë∆°n */}
                    {order.orderStatus !== "CANCELLED" && order.orderStatus !== "DELIVERED" && (
                      <button 
                        className="btn btn-sm btn-outline-danger" 
                        title="H·ªßy ƒë∆°n"
                        onClick={() => updateOrderStatus(order.orderId, "CANCELLED")}
                      >
                        <XCircle size={16} />
                      </button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Ph√¢n trang */}
      <div className="card-footer bg-white d-flex justify-content-between align-items-center py-3">
        <span className="small text-muted">T·ªïng: {pageConfig.totalElements} ƒë∆°n h√†ng</span>
        <nav>
          <ul className="pagination pagination-sm mb-0">
            {[...Array(pageConfig.totalPages)].map((_, i) => (
              <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                <button className="page-link" onClick={() => fetchOrders(i)}>{i + 1}</button>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </div>
  );
};

export default OrderListAD;
