import React, { useState, useEffect } from 'react';

// 1. IMPORT ẢNH TỪ THƯ MỤC ASSETS
// Thay đổi tên file .jpg/.png tương ứng với ảnh trong máy của bạn
import BannerImg1 from '../../assets/images/banner1.jpg';
import BannerImg2 from '../../assets/images/banner/banner3.jpg';
import BannerImg3 from '../../assets/images/banners/banner4.jpg';

// 2. CẤU HÌNH DỮ LIỆU BANNER
const HARDCODED_BANNERS = [
  {
    id: 1,
    title: "Khuyến mãi mùa hè",
    imageUrl: BannerImg1,
    link: "/shop"
  },
  {
    id: 2,
    title: "Bộ sưu tập mới",
    imageUrl: BannerImg2,
    link: "/new-arrivals"
  },
  {
    id: 3,
    title: "Ưu đãi thành viên",
    imageUrl: BannerImg3,
    link: "/sale"
  }
];

const Banner = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [imageLoaded, setImageLoaded] = useState({});

  // Tự động chuyển slide mỗi 5 giây
  useEffect(() => {
    if (HARDCODED_BANNERS.length > 1) {
      const timer = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % HARDCODED_BANNERS.length);
      }, 5000);
      return () => clearInterval(timer);
    }
  }, []);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % HARDCODED_BANNERS.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + HARDCODED_BANNERS.length) % HARDCODED_BANNERS.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const handleImageLoad = (id) => {
    setImageLoaded(prev => ({ ...prev, [id]: true }));
  };

  return (
    <section className="banner-section mb-5">
      <div className="container-fluid px-0">
        <div className="banner-slider position-relative overflow-hidden" style={{ backgroundColor: '#f8f9fa' }}>

          {/* Track chứa các Slide - Chuyển động mượt mà */}
          <div 
            className="banner-track d-flex" 
            style={{ 
              transform: `translateX(-${currentSlide * 100}%)`,
              transition: 'transform 0.7s cubic-bezier(0.4, 0, 0.2, 1)',
              willChange: 'transform'
            }}
          >
            {HARDCODED_BANNERS.map((banner) => (
              <div key={banner.id} className="banner-slide flex-shrink-0 w-100 position-relative">

                {/* Lớp phủ Loading khi ảnh đang tải */}
                {!imageLoaded[banner.id] && (
                  <div 
                    className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center justify-content-center"
                    style={{ background: '#e9ecef', zIndex: 1 }}
                  >
                    <div className="spinner-border text-warning" role="status">
                      <span className="visually-hidden">Đang tải...</span>
                    </div>
                  </div>
                )}

                <img
                  src={banner.imageUrl}
                  alt={banner.title}
                  draggable="false"
                  className="img-fluid w-100"
                  style={{ 
                    height: '65vh',         // Chiều cao banner (65% chiều cao trình duyệt)
                    minHeight: '400px',
                    maxHeight: '700px',
                    objectFit: 'cover',     // Giúp ảnh không bị méo
                    userSelect: 'none',
                    display: 'block'
                  }}
                  onLoad={() => handleImageLoad(banner.id)}
                />
              </div>
            ))}
          </div>

          {/* NÚT ĐIỀU HƯỚNG (Chỉ hiện trên Desktop) */}
          <button
            className="position-absolute top-50 start-0 translate-middle-y btn btn-light rounded-circle shadow m-3 d-none d-md-flex align-items-center justify-content-center"
            onClick={prevSlide}
            style={{ zIndex: 10, width: '45px', height: '45px', opacity: 0.7, border: 'none' }}
          >
            <i className="fas fa-chevron-left"></i>
          </button>

          <button
            className="position-absolute top-50 end-0 translate-middle-y btn btn-light rounded-circle shadow m-3 d-none d-md-flex align-items-center justify-content-center"
            onClick={nextSlide}
            style={{ zIndex: 10, width: '45px', height: '45px', opacity: 0.7, border: 'none' }}
          >
            <i className="fas fa-chevron-right"></i>
          </button>

          {/* DẤU CHẤM CHỈ CHỈ SỐ (DOTS) */}
          <div className="position-absolute bottom-0 start-50 translate-middle-x mb-4 d-flex" style={{ zIndex: 10 }}>
            {HARDCODED_BANNERS.map((_, index) => (
              <div
                key={index}
                onClick={() => goToSlide(index)}
                className="mx-1"
                style={{ 
                  width: index === currentSlide ? '30px' : '12px', 
                  height: '12px',
                  borderRadius: '10px',
                  backgroundColor: index === currentSlide ? '#ff6a00' : 'rgba(255, 255, 255, 0.8)',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
                }}
              />
            ))}
          </div>

        </div>
      </div>
    </section>
  );
};

export default Banner;
