import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Search, User, ShoppingBag, LogOut, LayoutGrid, MapPin, ChevronDown } from "lucide-react"; // Dùng Lucide cho hiện đại
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  // Logic giữ nguyên như cũ của mày
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) navigate(`/product`, { state: { searchKeyword: searchTerm.trim() } });
  };

  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) { console.error(err); }
      }
    } else { setUser(null); setUserInfo(null); }
  }, []);

  const loadCartCount = async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      if (currentUser?.email) {
        const count = await cartService.getCartCount(currentUser.email);
        setCartCount(count);
      } else {
        const localCart = cartService.getCartFromStorage();
        setCartCount(cartService.getTotalItems(localCart?.products || []));
      }
    } catch (error) { setCartCount(0); }
  };

  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } catch (err) { console.error(err); } finally { setLoadingCategories(false); }
  };

  useEffect(() => {
    initUser();
    fetchCategories();
    window.addEventListener("authUpdated", initUser);
    return () => window.removeEventListener("authUpdated", initUser);
  }, [initUser]);

  useEffect(() => {
    loadCartCount();
    window.addEventListener('cartUpdated', loadCartCount);
    return () => window.removeEventListener('cartUpdated', loadCartCount);
  }, [user]);

  const handleLogout = () => {
    Swal.fire({
      title: 'Xác nhận đăng xuất?',
      text: "Hẹn gặp lại bạn sớm nhé!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#f63',
      confirmButtonText: 'Đăng xuất',
      cancelButtonText: 'Ở lại'
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.clear();
        setUser(null);
        setUserInfo(null);
        setCartCount(0);
        window.dispatchEvent(new Event("authUpdated"));
        navigate("/login");
      }
    });
  };

  return (
    <header className="main-header sticky-top">
      {/* Top Bar - Tinh gọn hơn */}
      <div className="top-bar d-none d-md-block">
        <div className="container d-flex justify-content-between align-items-center py-2">
          <div className="top-bar-left small text-white opacity-75">
            <MapPin size={14} className="me-1" /> Giao tới: <b>Hà Nội - TP. Hồ Chí Minh</b> | Siêu tốc 2H
          </div>
          <div className="top-bar-right d-flex gap-3 small">
            <Link to="/help" className="text-white text-decoration-none opacity-75 hover-opacity-100">Trợ giúp</Link>
            <Link to="/news" className="text-white text-decoration-none opacity-75 hover-opacity-100">Tin tức</Link>
          </div>
        </div>
      </div>

      {/* Main Header */}
      <section className="header-middle py-3 bg-white shadow-sm">
        <div className="container">
          <div className="row align-items-center">
            {/* Logo - Modern Font */}
            <div className="col-lg-2 col-6">
              <Link to="/" className="text-decoration-none">
                <h3 className="logo-text m-0">Shop<span className="text-orange">BeAnh</span></h3>
              </Link>
            </div>

            {/* Search Bar - Hiện đại với bo góc lớn */}
            <div className="col-lg-6 d-none d-lg-block">
              <form className="modern-search-box" onSubmit={handleSearchSubmit}>
                <input 
                  type="text" 
                  className="form-control" 
                  placeholder="Hôm nay bạn muốn mua gì?..." 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button type="submit" className="btn-search">
                  <Search size={20} />
                </button>
              </form>
            </div>

            {/* Actions */}
            <div className="col-lg-4 col-6">
              <div className="d-flex justify-content-end align-items-center gap-4">

                {/* User Dropdown */}
                <div className="header-action-item dropdown">
                  <div className="d-flex align-items-center gap-2 pointer" data-bs-toggle="dropdown">
                    <div className="icon-circle shadow-sm">
                      <User size={20} />
                    </div>
                    <div className="d-none d-md-block">
                      <div className="text-muted smaller">Tài khoản</div>
                      <div className="fw-bold small">{userInfo?.firstName || user?.firstName || "Xin chào!"} <ChevronDown size={12}/></div>
                    </div>
                  </div>

                  <ul className="dropdown-menu dropdown-menu-end shadow-lg border-0 rounded-4 mt-3 animate slideIn">
                    {!user ? (
                      <>
                        <li><Link className="dropdown-item py-2" to="/login">Đăng nhập</Link></li>
                        <li><Link className="dropdown-item py-2" to="/register">Đăng ký thành viên</Link></li>
                      </>
                    ) : (
                      <>
                        <li className="px-3 py-2 border-bottom mb-2">
                          <div className="small text-muted">Chào mừng,</div>
                          <div className="fw-bold text-dark">{user.email}</div>
                        </li>
                        <li><Link className="dropdown-item" to="/profile">Thông tin cá nhân</Link></li>
                        <li><Link className="dropdown-item" to="/orders">Đơn hàng của tôi</Link></li>
                        <li><hr className="dropdown-divider" /></li>
                        <li><button onClick={handleLogout} className="dropdown-item text-danger d-flex align-items-center gap-2">
                          <LogOut size={16}/> Đăng xuất
                        </button></li>
                      </>
                    )}
                  </ul>
                </div>

                {/* Cart Icon - Vibrant Color */}
                <Link to="/cart" className="header-action-item text-decoration-none">
                  <div className="position-relative icon-circle shadow-sm cart-icon-vibrant">
                    <ShoppingBag size={20} />
                    {cartCount > 0 && (
                      <span className="cart-badge">{cartCount}</span>
                    )}
                  </div>
                </Link>

              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Nav Categories - Clean & Minimal */}
      <nav className="nav-bottom bg-white border-top d-none d-lg-block">
        <div className="container">
          <ul className="nav align-items-center">
            <li className="nav-item dropdown-hover position-relative">
              <Link className="nav-link category-trigger py-3 d-flex align-items-center gap-2" to="#">
                <LayoutGrid size={18} className="text-orange" />
                <span>DANH MỤC SẢN PHẨM</span>
              </Link>

              <div className="mega-menu-content shadow-lg border-0 rounded-bottom-4">
                <div className="row g-0">
                  <div className="col-12 scrollable-container">
                    {loadingCategories ? (
                      <div className="p-4 text-center">Đang tải...</div>
                    ) : (
                      <div className="category-grid p-3">
                        {categories.map((cat, idx) => (
                          <div key={idx} className="category-item-link" onClick={() => navigate('/product', { state: { selectedCategory: cat.categoryId || cat.id } })}>
                            {cat.categoryName || cat.name}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </li>

            {/* Quick Links */}
            <li className="nav-item ms-4 d-flex gap-4">
              <Link to="/" className="nav-link-modern">Trang chủ</Link>
              <Link to="/product" className="nav-link-modern text-orange fw-bold">Khuyến mãi hot</Link>
              <Link to="/product" className="nav-link-modern">Hàng mới về</Link>
              <Link to="/product" className="nav-link-modern">Bán chạy</Link>
            </li>
          </ul>
        </div>
      </nav>

      <style>{`
        :root {
          --primary-orange: #ff6a00;
          --secondary-orange: #ff9d00;
          --dark-blue: #1e293b;
        }

        .main-header { font-family: 'Inter', sans-serif; z-index: 1050; }

        /* Top Bar */
        .top-bar { background: linear-gradient(90deg, #ff6a00, #ff8c00); }
        .hover-opacity-100:hover { opacity: 1 !important; }

        /* Logo */
        .logo-text { font-weight: 800; letter-spacing: -1.5px; color: var(--dark-blue); }
        .text-orange { color: var(--primary-orange); }

        /* Search Box */
        .modern-search-box {
          display: flex;
          background: #f1f5f9;
          border-radius: 50px;
          padding: 4px;
          transition: all 0.3s;
          border: 1px solid transparent;
        }
        .modern-search-box:focus-within {
          background: #fff;
          border-color: var(--primary-orange);
          box-shadow: 0 0 0 4px rgba(255, 106, 0, 0.1);
        }
        .modern-search-box input {
          background: transparent;
          border: none;
          padding: 8px 20px;
          font-size: 14px;
        }
        .modern-search-box input:focus { box-shadow: none; background: transparent; }
        .btn-search {
          background: var(--primary-orange);
          color: white;
          border: none;
          border-radius: 50px;
          width: 45px;
          height: 40px;
          display: flex;
          align-items: center;
          justify-content: center;
          transition: 0.3s;
        }
        .btn-search:hover { background: #e65c00; transform: scale(0.95); }

        /* Icons & Actions */
        .icon-circle {
          width: 42px;
          height: 42px;
          background: #f8fafc;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          color: var(--dark-blue);
          transition: 0.3s;
        }
        .header-action-item:hover .icon-circle {
          background: var(--primary-orange);
          color: white;
        }
        .cart-icon-vibrant { background: #fff5ee; color: var(--primary-orange); }
        .cart-badge {
          position: absolute;
          top: -2px;
          right: -2px;
          background: #ef4444;
          color: white;
          font-size: 10px;
          font-weight: bold;
          padding: 2px 6px;
          border-radius: 10px;
          border: 2px solid white;
        }

        /* Navigation */
        .nav-link-modern {
          font-size: 13px;
          font-weight: 600;
          color: #64748b;
          text-decoration: none;
          text-transform: uppercase;
          transition: 0.3s;
        }
        .nav-link-modern:hover { color: var(--primary-orange); }
        .category-trigger { font-weight: 800; color: var(--dark-blue) !important; font-size: 13px; }

        /* Mega Menu */
        .mega-menu-content {
          position: absolute;
          top: 100%;
          left: 0;
          width: 600px;
          background: white;
          display: none;
          z-index: 1000;
          padding: 10px;
        }
        .dropdown-hover:hover .mega-menu-content { display: block; animation: fadeInUp 0.3s ease; }
        .category-grid {
          display: grid;
          grid-template-columns: repeat(3, 1fr);
          gap: 10px;
        }
        .category-item-link {
          padding: 10px;
          font-size: 14px;
          color: #475569;
          border-radius: 8px;
          cursor: pointer;
          transition: 0.2s;
        }
        .category-item-link:hover {
          background: #fff5ee;
          color: var(--primary-orange);
          padding-left: 15px;
        }

        /* Utils */
        .pointer { cursor: pointer; }
        .smaller { font-size: 11px; }
        @keyframes fadeInUp {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </header>
  );
};

export default Header;
