import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import Swal from 'sweetalert2';
import axios from 'axios'; // Mày nhớ chạy: npm install axios

const Checkout = () => {
    const [cart, setCart] = useState(null);
    const [loading, setLoading] = useState(true);
    const [processing, setProcessing] = useState(false);
    const [user, setUser] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState('COD');
    const [shippingAddress, setShippingAddress] = useState('');
    const [mobileNumber, setMobileNumber] = useState('');
    const navigate = useNavigate();

    const currentUser = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        loadCartAndUser();
    }, []);

    const loadCartAndUser = async () => {
        if (!currentUser || !currentUser.email) {
            Swal.fire('Thông báo', 'Vui lòng đăng nhập để tiếp tục', 'info');
            navigate('/login');
            return;
        }

        try {
            setLoading(true);
            const cartData = await cartService.getActiveCart(currentUser.email);
            if (!cartData || !cartData.products || cartData.products.length === 0) {
                navigate('/cart');
                return;
            }
            setCart(cartData);
            setUser(currentUser);
            setMobileNumber(currentUser.mobileNumber || '');
            setShippingAddress(localStorage.getItem('shippingAddress') || '');
        } catch (error) {
            Swal.fire('Lỗi', 'Không thể tải dữ liệu giỏ hàng', 'error');
        } finally {
            setLoading(false);
        }
    };

    const handlePlaceOrder = async () => {
        const phoneClean = mobileNumber.trim();
        // Validation
        if (!phoneClean || !/^[0-9]{10,11}$/.test(phoneClean)) {
            Swal.fire('Lỗi', 'Số điện thoại không hợp lệ (10-11 số)', 'warning');
            return;
        }
        if (!shippingAddress.trim()) {
            Swal.fire('Thông tin trống', 'Vui lòng nhập địa chỉ giao hàng', 'warning');
            return;
        }

        const result = await Swal.fire({
            title: 'Xác nhận đặt hàng?',
            text: paymentMethod === 'BANKING' ? 'Hệ thống sẽ chuyển hướng đến cổng VNPay' : 'Xác nhận đặt hàng thanh toán tiền mặt',
            icon: 'question',
            showCancelButton: true,
            confirmButtonColor: '#ffc107',
            confirmButtonText: 'Đồng ý',
            cancelButtonText: 'Hủy'
        });

        if (result.isConfirmed) {
            try {
                setProcessing(true);
                localStorage.setItem('shippingAddress', shippingAddress);

                // --- XỬ LÝ THANH TOÁN ONLINE (VNPAY) ---
                if (paymentMethod === 'BANKING') {
                    // Gọi sang Server Node.js (cổng 3000)
                    const response = await axios.get(`http://localhost:3000/payment`, {
                        params: { amount: totalAmount }
                    });

                    if (response.data.url) {
                        // Lưu đơn hàng tạm vào LocalStorage để sau khi thanh toán xong thì tạo Order chính thức
                        localStorage.setItem('pendingOrder', JSON.stringify({
                            email: user.email,
                            cartId: cart.cartId,
                            address: shippingAddress,
                            phone: phoneClean,
                            method: 'BANKING'
                        }));
                        // Nhảy sang trang VNPay
                        window.location.href = response.data.url;
                        return; 
                    }
                }

                // --- XỬ LÝ THANH TOÁN TIỀN MẶT (COD) ---
                await cartService.createOrder(user.email, cart.cartId, 'COD', shippingAddress, phoneClean);
                await cartService.clearCart(user.email, cart.cartId);

                await Swal.fire({ icon: 'success', title: 'Thành công!', text: 'Đơn hàng đã được tiếp nhận.', timer: 2000, showConfirmButton: false });
                navigate('/orders');

            } catch (error) {
                Swal.fire('Thất bại', error.response?.data?.message || 'Có lỗi xảy ra', 'error');
            } finally {
                setProcessing(false);
            }
        }
    };

    const totalAmount = cart ? cartService.calculateTotal(cart.products) : 0;

    if (loading) return <div className="container mt-5 text-center">Đang tải...</div>;

    return (
        <div className="container mt-4 mb-5">
            <h3 className="fw-bold mb-4"><i className="fas fa-check-circle me-2 text-success"></i>HOÀN TẤT ĐẶT HÀNG</h3>

            <div className="row g-4">
                {/* Cột trái: Form thông tin */}
                <div className="col-lg-7">
                    <div className="card shadow-sm border-0 rounded-4 mb-4 p-4">
                        <h5 className="fw-bold mb-4 border-bottom pb-2">Thông tin giao hàng</h5>
                        <div className="mb-3">
                            <label className="form-label small fw-bold">Người nhận</label>
                            <input type="text" className="form-control bg-light" value={`${user?.firstName || ''} ${user?.lastName || ''}`} readOnly />
                        </div>
                        <div className="mb-3">
                            <label className="form-label small fw-bold text-danger">Số điện thoại *</label>
                            <input type="tel" className="form-control border-warning" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value.replace(/[^0-9]/g, ''))} />
                        </div>
                        <div className="mb-3">
                            <label className="form-label small fw-bold text-danger">Địa chỉ chi tiết *</label>
                            <textarea className="form-control border-warning" rows="3" value={shippingAddress} onChange={(e) => setShippingAddress(e.target.value)}></textarea>
                        </div>
                    </div>

                    <div className="card shadow-sm border-0 rounded-4 p-4">
                        <h5 className="fw-bold mb-4 border-bottom pb-2">Phương thức thanh toán</h5>
                        {['COD', 'BANKING'].map((method) => (
                            <div key={method} className={`p-3 mb-2 rounded-3 border d-flex align-items-center ${paymentMethod === method ? 'border-warning bg-light' : ''}`} 
                                 onClick={() => setPaymentMethod(method)} style={{ cursor: 'pointer' }}>
                                <input type="radio" className="form-check-input" checked={paymentMethod === method} readOnly />
                                <div className="ms-3">
                                    <i className={`fas fa-${method === 'COD' ? 'money-bill-wave' : 'university'} me-2`}></i>
                                    <span className="fw-bold">{method === 'COD' ? 'Thanh toán khi nhận hàng' : 'Thanh toán Online (VNPay)'}</span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Cột phải: Tóm tắt */}
                <div className="col-lg-5">
                    <div className="card shadow-sm border-0 rounded-4 p-4">
                        <h5 className="fw-bold mb-4">Chi tiết đơn hàng</h5>
                        <div className="mb-3" style={{ maxHeight: '300px', overflowY: 'auto' }}>
                            {cart.products.map((item, idx) => (
                                <div key={idx} className="d-flex justify-content-between mb-2 pb-2 border-bottom">
                                    <span className="small">{item.productName} (x{item.quantity})</span>
                                    <span className="fw-bold small">{((item.specialPrice || item.price) * item.quantity).toLocaleString()}₫</span>
                                </div>
                            ))}
                        </div>
                        <div className="d-flex justify-content-between h4 fw-bold">
                            <span>Tổng tiền:</span>
                            <span className="text-danger">{totalAmount.toLocaleString()}₫</span>
                        </div>
                        <button className="btn btn-warning w-100 py-3 mt-4 fw-bold rounded-pill" onClick={handlePlaceOrder} disabled={processing}>
                            {processing ? 'ĐANG XỬ LÝ...' : 'XÁC NHẬN ĐẶT HÀNG'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Checkout;
