import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const ProductListAD = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  // Th√™m state ƒë·ªÉ qu·∫£n l√Ω ph√¢n trang n·∫øu mu·ªën sau n√†y d√πng
  const [pageConfig, setPageConfig] = useState({
    pageNumber: 0,
    totalPages: 0,
    totalElements: 0
  });

  // 1. H√†m l·∫•y danh s√°ch s·∫£n ph·∫©m
  const fetchProducts = useCallback(async (page = 0) => {
    try {
      const token = localStorage.getItem("jwt-token");
      
      // N·∫øu kh√¥ng c√≥ token, b√°o l·ªói ngay
      if (!token) {
        Swal.fire("C·∫£nh b√°o", "M√†y ch∆∞a ƒëƒÉng nh·∫≠p ho·∫∑c phi√™n l√†m vi·ªác h·∫øt h·∫°n!", "warning");
        return;
      }

      // G·ªçi ƒë√∫ng API Admin ƒë√£ ph√¢n trang (m·∫∑c ƒë·ªãnh pageSize 50 cho admin xem cho s∆∞·ªõng)
      const res = await axios.get(`http://localhost:8080/api/admin/products?pageNumber=${page}&pageSize=50`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log("D·ªØ li·ªáu Backend tr·∫£ v·ªÅ:", res.data);

      // Backend tr·∫£ v·ªÅ ProductResponse { content: [], pageNumber: 0, ... }
      if (res.data && res.data.content) {
        setProducts(res.data.content);
        setPageConfig({
          pageNumber: res.data.pageNumber,
          totalPages: res.data.totalPages,
          totalElements: res.data.totalElements
        });
      } else {
        setProducts([]);
      }
      setLoading(false);
    } catch (err) {
      console.error("L·ªói API:", err);
      // N·∫øu l·ªói 401 ho·∫∑c 403 l√† do Security ch·∫∑n
      if (err.response?.status === 401 || err.response?.status === 403) {
        Swal.fire("T·ª´ ch·ªëi!", "M√†y kh√¥ng c√≥ quy·ªÅn Admin ho·∫∑c Token l·ªüm r·ªìi.", "error");
      } else {
        Swal.fire("L·ªói!", "Kh√¥ng th·ªÉ k·∫øt n·ªëi t·ªõi Server.", "error");
      }
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // 2. H√†m X√≥a s·∫£n ph·∫©m
  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "X√°c nh·∫≠n x√≥a?",
      text: `M√†y c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m ID: ${id} kh√¥ng?`,
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "X√≥a lu√¥n!",
      cancelButtonText: "H·ªßy"
    });

    if (confirm.isConfirmed) {
      try {
        const token = localStorage.getItem("jwt-token");
        await axios.delete(`http://localhost:8080/api/admin/products/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        Swal.fire("Th√†nh c√¥ng!", "S·∫£n ph·∫©m ƒë√£ 'bay m√†u'.", "success");
        fetchProducts(pageConfig.pageNumber); // Load l·∫°i ƒë√∫ng trang hi·ªán t·∫°i
      } catch (err) {
        Swal.fire("L·ªói!", "X√≥a kh√¥ng ƒë∆∞·ª£c, check l·∫°i li√™n k·∫øt kh√≥a ngo·∫°i xem.", "error");
      }
    }
  };

  if (loading) return (
    <div className="d-flex justify-content-center align-items-center" style={{height: "300px"}}>
      <div className="spinner-border text-primary" role="status"></div>
      <span className="ms-2">ƒêang n·∫°p kho m·ªπ ph·∫©m...</span>
    </div>
  );

  return (
    <div className="card shadow-sm p-4 border-0">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h4 className="m-0 text-primary fw-bold">üì¶ Qu·∫£n l√Ω Kho M·ªπ Ph·∫©m</h4>
          <small className="text-muted">T·ªïng s·ªë: {pageConfig.totalElements} s·∫£n ph·∫©m</small>
        </div>
        <button className="btn btn-success shadow-sm px-4">
          <i className="bi bi-plus-circle me-2"></i>Th√™m M·ªõi
        </button>
      </div>

      <div className="table-responsive">
        <table className="table table-hover align-middle border">
          <thead className="table-dark">
            <tr>
              <th style={{width: "5%"}}>ID</th>
              <th style={{width: "10%"}}>·∫¢nh</th>
              <th style={{width: "25%"}}>T√™n S·∫£n Ph·∫©m</th>
              <th style={{width: "15%"}}>Danh M·ª•c</th>
              <th style={{width: "15%"}}>Gi√° B√°n</th>
              <th style={{width: "10%"}}>Chi·∫øt Kh·∫•u</th>
              <th className="text-center" style={{width: "20%"}}>Thao T√°c</th>
            </tr>
          </thead>
          <tbody>
            {products.length > 0 ? (
              products.map((p) => (
                <tr key={p.productId}>
                  <td>{p.productId}</td>
                  <td>
                    <img 
                      // Link ·∫£nh g·ªçi qua API getImage c·ªßa Backend
                      src={p.image ? `http://localhost:8080/api/public/products/image/${p.image}` : "https://via.placeholder.com/60"} 
