import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  // ‚úÖ Ki·ªÉm tra authentication v√† l·∫•y user ID th·∫≠t
  const checkAuth = () => {
    try {
      const token = localStorage.getItem("token");
      const userData = localStorage.getItem("user");

      console.log("üîê AUTH CHECK:", {
        hasToken: !!token,
        hasUser: !!userData
      });

      if (!token || !userData) {
        return false;
      }

      const user = JSON.parse(userData);
      const userId = user.userId;

      // ‚úÖ CH·ªà cho ph√©p user ID l√† s·ªë (kh√¥ng ph·∫£i temp)
      if (!userId || typeof userId !== 'number') {
        console.error("‚ùå Invalid user ID (not a number):", userId);
        return false;
      }

      console.log("‚úÖ Valid user ID:", userId);
      return { token, user, userId };
    } catch (error) {
      console.error("‚ùå Auth check error:", error);
      return false;
    }
  };

  useEffect(() => {
    console.log("üéØ EDIT PROFILE - INITIALIZING");

    const auth = checkAuth();
    if (!auth) {
      console.log("‚ùå AUTH FAILED - Invalid user ID");
      setMessage({ 
        type: "error", 
        text: "Vui l√≤ng ƒëƒÉng nh·∫≠p v·ªõi t√†i kho·∫£n h·ª£p l·ªá ƒë·ªÉ ch·ªânh s·ª≠a h·ªì s∆°" 
      });
      return;
    }

    const { user: userData, userId } = auth;
    setUser(userData);

    console.log("‚úÖ AUTH VALID - Loading user data for ID:", userId);

    // Load user data t·ª´ API
    const loadUserData = async () => {
      setLoading(true);
      try {
        console.log("üì° Loading user data from API...");
        const userProfile = await userService.getUserById(userId);

        console.log("‚úÖ User data loaded:", userProfile);

        // ‚úÖ Map data t·ª´ API sang form (h·ªó tr·ª£ c·∫£ snake_case v√† camelCase)
