import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import Swal from 'sweetalert2'; // Dùng Swal cho mượt và đẹp hơn alert thường

const ProductCart = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [updatingId, setUpdatingId] = useState(null); // Chỉ định sản phẩm nào đang xử lý
  const navigate = useNavigate();

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: null };

  useEffect(() => {
    if (!currentUser.email) {
      setLoading(false);
      return;
    }
    loadCart();
  }, []);

  const loadCart = async () => {
    try {
      setLoading(true);
      const cartData = await cartService.getActiveCart(currentUser.email);
      setCart(cartData);
    } catch (error) {
      console.error('❌ Lỗi tải giỏ hàng:', error);
    } finally {
      setLoading(false);
    }
  };

  // ✅ FIX: Cập nhật số lượng không load lại toàn trang
  const handleUpdateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1 || updatingId) return;

    // Lưu lại giá trị cũ để rollback nếu lỗi
    const oldCart = { ...cart };

    try {
      setUpdatingId(productId); // Đánh dấu sản phẩm đang quay spinner nhỏ

      // Bước 1: Cập nhật UI ngay lập tức (Optimistic Update)
      setCart(prevCart => ({
        ...prevCart,
        products: prevCart.products.map(item => 
          item.productId === productId ? { ...item, quantity: newQuantity } : item
        )
      }));

      // Bước 2: Gọi API chạy ngầm
      await cartService.updateQuantity(cart.cartId, productId, newQuantity);

      // Bắn event để Header cập nhật số lượng nếu cần
      window.dispatchEvent(new Event("cartUpdated"));

    } catch (error) {
      setCart(oldCart); // Rollback dữ liệu nếu API lỗi
      Swal.fire('Lỗi', 'Không thể cập nhật số lượng', 'error');
    } finally {
      setUpdatingId(null);
    }
  };

  // ✅ FIX: Xóa sản phẩm mượt mà
  const handleRemoveItem = async (productId) => {
    const result = await Swal.fire({
      title: 'Xóa sản phẩm?',
      text: "Sản phẩm sẽ được loại khỏi giỏ hàng!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#ffc107',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Đồng ý',
      cancelButtonText: 'Hủy'
    });

    if (!result.isConfirmed) return;

    try {
      setUpdatingId(productId);

      // Bước 1: Xóa khỏi UI ngay lập tức
      setCart(prevCart => ({
        ...prevCart,
        products: prevCart.products.filter(item => item.productId !== productId)
      }));

      // Bước 2: Gọi API
      await cartService.removeFromCart(cart.cartId, productId);

      window.dispatchEvent(new Event("cartUpdated"));
    } catch (error) {
      loadCart(); // Nếu lỗi thì load lại chuẩn từ server
      Swal.fire('Lỗi', 'Không thể xóa sản phẩm', 'error');
    } finally {
      setUpdatingId(null);
    }
  };

  // Các hàm tính toán giữ nguyên nhưng tối ưu hiệu năng
  const calculateCartTotal = () => {
    return cart?.products?.reduce((total, item) => {
      const price = item.productPrice || item.specialPrice || item.price || 0;
      return total + (price * (item.quantity || 1));
    }, 0) || 0;
  };

  if (loading) return (
    <div className="text-center py-5">
      <div className="spinner-border text-warning" role="status"></div>
      <p className="mt-2">Đang xử lý dữ liệu...</p>
    </div>
  );

  if (!cart || !cart.products || cart.products.length === 0) {
    return (
      <div className="container mt-5 text-center">
        <h3 className="text-muted">Giỏ hàng của bạn đang trống</h3>
        <Link to="/product" className="btn btn-warning mt-3">Mua sắm ngay</Link>
      </div>
    );
  }

  return (
    <div className="container mt-4 mb-5">
      <h2 className="text-center fw-bold mb-4">GIỎ HÀNG</h2>
      <div className="row">
        {/* Cột danh sách sản phẩm */}
        <div className="col-lg-8">
          <div className={`card shadow-sm ${updatingId ? 'opacity-75' : ''}`} style={{ transition: '0.3s' }}>
            {cart.products.map((item) => (
              <div key={item.productId} className="row align-items-center p-3 border-bottom m-0">
                <div className="col-3 col-md-2">
                  <img 
                    src={`http://localhost:8080/api/public/products/image/${item.image}`} 
                    className="img-fluid rounded" alt={item.productName} 
                  />
                </div>
                <div className="col-9 col-md-4">
                  <h6 className="mb-0 fw-bold">{item.productName}</h6>
                  <p className="text-danger mb-0">{(item.productPrice || item.price).toLocaleString()}₫</p>
                </div>
                {/* Bộ tăng giảm số lượng */}
                <div className="col-6 col-md-3 mt-2 mt-md-0">
                  <div className="d-flex align-items-center border rounded-pill px-2" style={{ width: 'fit-content' }}>
                    <button 
                      className="btn btn-sm border-0" 
                      onClick={() => handleUpdateQuantity(item.productId, item.quantity - 1)}
                      disabled={item.quantity <= 1 || updatingId === item.productId}
                    >-</button>
                    <span className="px-2 fw-bold">{item.quantity}</span>
                    <button 
                      className="btn btn-sm border-0" 
                      onClick={() => handleUpdateQuantity(item.productId, item.quantity + 1)}
                      disabled={updatingId === item.productId}
                    >+</button>
                  </div>
                </div>
                {/* Tổng tiền từng món & Xóa */}
                <div className="col-6 col-md-3 text-end">
                  <p className="fw-bold mb-0">{( (item.productPrice || item.price) * item.quantity).toLocaleString()}₫</p>
                  <button 
                    className="btn btn-sm text-muted" 
                    onClick={() => handleRemoveItem(item.productId)}
                  >
                    <i className="fas fa-trash-alt"></i> Xóa
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Cột Tổng thanh toán (Sticky) */}
        <div className="col-lg-4 mt-4 mt-lg-0">
          <div className="card shadow-sm p-3 sticky-top" style={{ top: '80px' }}>
            <h5 className="fw-bold border-bottom pb-2">Tóm tắt đơn hàng</h5>
            <div className="d-flex justify-content-between my-2">
              <span>Tạm tính:</span>
              <span>{calculateCartTotal().toLocaleString()}₫</span>
            </div>
            <div className="d-flex justify-content-between my-2">
              <span>Giao hàng:</span>
              <span className="text-success">Miễn phí</span>
            </div>
            <hr />
            <div className="d-flex justify-content-between mb-4">
              <span className="h5 fw-bold">Tổng cộng:</span>
              <span className="h5 fw-bold text-danger">{calculateCartTotal().toLocaleString()}₫</span>
            </div>
            <button 
              className="btn btn-warning w-full py-3 fw-bold rounded-pill"
              onClick={() => navigate('/checkout')}
            >
              TIẾN HÀNH THANH TOÁN
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductCart;
