import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Header = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // ðŸ”¹ Láº¥y thÃ´ng tin user tá»« localStorage
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (e) {
        console.error("Invalid user data in localStorage");
      }
    }
  }, []);

  return (
    <header className="section-header" style={{ borderBottom: "1px solid #ddd" }}>
      {/* ðŸ”¸ Thanh top */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container d-flex justify-content-between align-items-center">
          <ul className="navbar-nav mr-auto">
            {!user ? (
              <li>
                <span className="nav-link">
                  Hi, <Link to="/login">Log In</Link> or <Link to="/register">Register</Link>
                </span>
              </li>
            ) : (
              <li>
                <span className="nav-link">
                  ðŸ‘‹ Hi, <strong>{user.first_name}</strong>
                </span>
              </li>
            )}
          </ul>
          <ul className="navbar-nav">
            <li><a href="#" className="nav-link">Help</a></li>
          </ul>
        </div>
      </nav>

      {/* ðŸ”¸ Header chÃ­nh */}
      <div className="container py-3">
        <div className="row align-items-center">
          {/* Logo */}
          <div className="col-6 col-md-2">
            <Link to="/" className="brand-wrap">
              <img
                src={require("../assets/images/logo.png")}
                height={56}
                alt="Logo"
                style={{ maxHeight: "60px" }}
              />
            </Link>
          </div>

          {/* Search */}
          <div className="col-md-6">
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="Search..."
                style={{
                  borderRadius: "20px 0 0 20px",
                  border: "1px solid #ccc",
                }}
              />
              <button
                className="btn btn-primary"
                style={{
                  borderRadius: "0 20px 20px 0",
                  padding: "0 20px",
                }}
              >
                Search
              </button>
            </div>
          </div>

          {/* User & Cart */}
          <div className="col-md-4 d-flex justify-content-end align-items-center">
            {user && (
              <div className="d-flex align-items-center gap-3">
                <Link
                  to="/profile"
                  style={{
                    color: "#333",
                    textDecoration: "none",
                    fontWeight: "bold",
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                  }}
                >
                  <i className="fa fa-user-circle" style={{ fontSize: "20px" }}></i>
                  {user.first_name}
                </Link>
                <Link to="/cart" style={{ color: "#333", textDecoration: "none" }}>
                  <i className="fa fa-shopping-cart" style={{ fontSize: "22px" }}></i>
                </Link>
              </div>
            )}
            {!user && (
              <Link
                to="/login"
                className="btn btn-outline-primary ml-2"
                style={{ borderRadius: "20px" }}
              >
                Sign in
              </Link>
            )}
          </div>
        </div>

        {/* ðŸ”¸ Navbar chÃ­nh */}
        <nav className="navbar navbar-main navbar-expand mt-3 pl-0">
          <ul className="navbar-nav flex-wrap">
            <li className="nav-item">
              <Link to="/" className="nav-link">Home</Link>
            </li>
            <li className="nav-item">
              <Link to="/product" className="nav-link">Products</Link>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
