import React, { useState, useEffect, useCallback } from "react";
import categoryService from "../../../services/categoryService";
import api from "../../../services/api";
import Swal from "sweetalert2";
import { useNavigate } from "react-router-dom";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  const navigate = useNavigate();

  // 1. Qu·∫£n l√Ω ph√¢n trang - ƒê·∫£m b·∫£o c√°c field n√†y kh·ªõp v·ªõi CategoryResponse c·ªßa BE
  const [pageConfig, setPageConfig] = useState({ 
    pageNumber: 0, 
    pageSize: 10, 
    totalPages: 0,
    totalElements: 0, // ƒê√¢y l√† "T·ªïng m·ª•c"
    lastPage: false
  });

  // ================= 2. FETCH DATA (FIX T·ªîNG M·ª§C) =================
  const fetchCategories = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      // G·ªçi service v·ªõi tham s·ªë ph√¢n trang
      const res = await categoryService.getAllCategories(page, pageConfig.pageSize);
      
      console.log("üì• [D·ªØ li·ªáu BE tr·∫£ v·ªÅ]:", res);

      // C·∫≠p nh·∫≠t danh s√°ch (res.content l√† m·∫£ng data)
      setCategories(res.content || []);

      // C·∫≠p nh·∫≠t PageConfig - Quan tr·ªçng l√† totalElements
      setPageConfig({ 
        pageNumber: res.pageNumber || 0, 
        pageSize: res.pageSize || 10,
        totalPages: res.totalPages || 0,
        totalElements: res.totalElements || 0, // Fix l·∫°i t·ªïng m·ª•c ·ªü ƒë√¢y
        lastPage: res.lastPage || false
      });
    } catch (err) {
      console.error("‚ùå L·ªói Fetch Category:", err);
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  // ================= 3. CHECK TOKEN (GI·ªêNG PRODUCT) =================
  useEffect(() => { 
    const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
    if (token) {
        fetchCategories(0); 
    } else {
        navigate("/login");
    }
  }, [fetchCategories, navigate]);

  // ================= 4. THAO T√ÅC (ADD/EDIT/DELETE) =================
  const getAuthConfig = () => {
    const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
    return { headers: { Authorization: `Bearer ${token}` } };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEdit) {
        await api.put(`/admin/categories/${formData.categoryId}`, { categoryName: formData.categoryName }, getAuthConfig());
      } else {
        await api.post(`/admin/categories`, { categoryName: formData.categoryName }, getAuthConfig());
      }
      Swal.fire("Th√†nh c√¥ng!", "D·ªØ li·ªáu ƒë√£ c·∫≠p nh·∫≠t", "success");
      setShowModal(false);
      fetchCategories(pageConfig.pageNumber); 
    } catch (err) {
      Swal.fire("L·ªói", err.response?.data?.message || "Thao t√°c th·∫•t b·∫°i", "error");
    }
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({ title: "X√≥a danh m·ª•c?", icon: "warning", showCancelButton: true });
    if (confirm.isConfirmed) {
      try {
        await api.delete(`/admin/categories/${id}`, getAuthConfig());
        Swal.fire("ƒê√£ x√≥a!", "", "success");
        fetchCategories(0);
      } catch (err) { Swal.fire("L·ªói", "Kh√¥ng th·ªÉ x√≥a.", "error"); }
    }
  };

  return (
    <div className="p-1">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="m-0 fw-bold">QU·∫¢N L√ù DANH M·ª§C</h5>
          <button className="btn btn-primary btn-sm px-3" onClick={() => { setIsEdit(false); setFormData({categoryId:"", categoryName:""}); setShowModal(true); }}>
            + Th√™m m·ªõi
          </button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0 text-start">
            <thead className="table-light">
              <tr>
                <th width="10%">ID</th>
                <th width="70%">T√™n danh m·ª•c</th>
                <th width="20%" className="text-center">Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-4">ƒêang t·∫£i...</td></tr>
              ) : categories.map(c => (
                <tr key={c.categoryId}>
                  <td className="text-muted">#{c.categoryId}</td>
                  <td className="fw-semibold">{c.categoryName}</td>
                  <td className="text-center">
                    <button className="btn btn-sm btn-outline-info me-2 shadow-sm" onClick={() => { setIsEdit(true); setFormData(c); setShowModal(true); }}>S·ª≠a</button>
                    <button className="btn btn-sm btn-outline-danger shadow-sm" onClick={() => handleDelete(c.categoryId)}>X√≥a</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* ================= PH·∫¶N TRANG & T·ªîNG M·ª§C (FIXED) ================= */}
        <div className="card-footer bg-white d-flex justify-content-between align-items-center py-3">
          <div className="text-muted small">
            Hi·ªÉn th·ªã <b>{categories.length}</b> / <b>{pageConfig.totalElements}</b> danh m·ª•c
          </div>
          <nav>
            <ul className="pagination pagination-sm mb-0">
              {/* N√∫t Tr∆∞·ªõc */}
              <li className={`page-item ${pageConfig.pageNumber === 0 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber - 1)}>Tr∆∞·ªõc</button>
              </li>

              {/* Danh s√°ch s·ªë trang */}
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => fetchCategories(i)}>{i + 1}</button>
                </li>
              ))}

              {/* N√∫t Sau */}
              <li className={`page-item ${pageConfig.pageNumber >= pageConfig.totalPages - 1 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber + 1)}>Sau</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* MODAL (Gi·ªØ nguy√™n) */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0 shadow">
              <form onSubmit={handleSubmit}>
                <div className="modal-header border-bottom-0">
                  <h6 className="fw-bold m-0">{isEdit ? "C·∫¨P NH·∫¨T DANH M·ª§C" : "TH√äM DANH M·ª§C"}</h6>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body py-3">
