  const [isAdding, setIsAdding] = useState(false);

  // Gi·∫£ s·ª≠ b·∫°n ƒë√£ c√≥ useEffect loadProduct ·ªü ƒë√¢y...

  /* ================= HANDLE ADD TO CART (FIXED) ================= */
  const handleAddToCart = async () => {
    // 1. L·∫•y th√¥ng tin x√°c th·ª±c (Gi·ªëng logic Mobile)
    const cartId = cartService.getCartId();
    const token = localStorage.getItem('token');

    console.log("DEBUG Web - cartId:", cartId);
    console.log("DEBUG Web - productId:", id);

    // 2. Ki·ªÉm tra quy·ªÅn truy c·∫≠p
    if (!token || !cartId) {
      Swal.fire({
        icon: 'warning',
        title: 'Ch∆∞a ƒëƒÉng nh·∫≠p',
        text: 'Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ s·ª≠ d·ª•ng gi·ªè h√†ng!',
        confirmButtonText: 'ƒê·∫øn trang ƒêƒÉng nh·∫≠p'
      }).then((result) => {
        if (result.isConfirmed) navigate('/login');
      });
      return;
    }

    try {
      setIsAdding(true);

      // 3. G·ªçi API Add to cart (Gi·ªëng h√†m ADD_TO_CART b√™n Mobile)
      await cartService.addToCart(cartId, id, quantity);

      // 4. Th√¥ng b√°o th√†nh c√¥ng
      Swal.fire({
        icon: 'success',
        title: 'Th√†nh c√¥ng',
        text: 'ƒê√£ th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng üõí',
        showConfirmButton: false,
        timer: 1500
      });

      // Optional: Chuy·ªÉn h∆∞·ªõng ƒë·∫øn gi·ªè h√†ng gi·ªëng router.push("/mycart")
      // navigate('/cart');

    } catch (error) {
      console.error("ADD TO CART ERROR:", error.response?.data || error);

      // X·ª≠ l√Ω l·ªói 401 c·ª• th·ªÉ n·∫øu Token h·∫øt h·∫°n
      if (error.response?.status === 401) {
        Swal.fire('H·∫øt phi√™n l√†m vi·ªác', 'Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i', 'error');
      } else {
        Swal.fire('L·ªói', 'Kh√¥ng th·ªÉ th√™m v√†o gi·ªè h√†ng', 'error');
      }
    } finally {
      setIsAdding(false);
    }
  };

  return (
    <div className="product-detail-container">
      {/* ... code hi·ªÉn th·ªã s·∫£n ph·∫©m c·ªßa b·∫°n ... */}

      <div className="quantity-controls">
        <button onClick={() => setQuantity(q => Math.max(1, q - 1))}>-</button>
        <span>{quantity}</span>
        <button onClick={() => setQuantity(q => q + 1)}>+</button>
      </div>

      <button 
        className="btn-add-to-cart" 
        onClick={handleAddToCart}
        disabled={isAdding}
      >
        {isAdding ? 'ƒêang th√™m...' : 'Add to Basket'}
      </button>
    </div>
  );
};

export default ProductDetail;
