import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobileNumber: "",
    password: "",
    confirmPassword: "",
    // Thông tin địa chỉ
    street: "",
    buildingName: "",
    city: "",
    state: "",
    country: "Việt Nam",
    pincode: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    // Xóa thông báo lỗi khi người dùng nhập lại
    if (backendErrors[name]) {
      setBackendErrors({ ...backendErrors, [name]: "" });
    }
  };

  const validateForm = () => {
    let errors = {};
    const nameRegex = /^[a-zA-ZÀ-ỹ\s]*$/; // Hỗ trợ tiếng Việt có dấu, không số
    const phoneRegex = /^\d{10}$/;

    // Kiểm tra Họ (5-20 ký tự)
    if (form.firstName.length < 5 || form.firstName.length > 20) {
      errors.firstName = "Họ và tên đệm phải từ 5 đến 20 ký tự.";
    } else if (!nameRegex.test(form.firstName)) {
      errors.firstName = "Họ không được chứa số hoặc ký tự đặc biệt.";
    }

    // Kiểm tra Tên (5-20 ký tự)
    if (form.lastName.length < 5 || form.lastName.length > 20) {
      errors.lastName = "Tên phải từ 5 đến 20 ký tự.";
    } else if (!nameRegex.test(form.lastName)) {
      errors.lastName = "Tên không được chứa số hoặc ký tự đặc biệt.";
    }

    // Kiểm tra Số điện thoại (Đúng 10 số)
    if (!phoneRegex.test(form.mobileNumber)) {
      errors.mobileNumber = "Số điện thoại phải đúng 10 chữ số.";
    }

    if (form.password.length < 6) {
      errors.password = "Mật khẩu nên có ít nhất 6 ký tự.";
    }

    if (form.password !== form.confirmPassword) {
      errors.confirmPassword = "Mật khẩu xác nhận không khớp.";
    }

    setBackendErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      Swal.fire({
        icon: "warning",
        title: "Dữ liệu chưa hợp lệ",
        text: "Vui lòng kiểm tra lại các thông báo lỗi màu đỏ.",
        confirmButtonColor: "#ff6a00"
      });
      return;
    }

    setLoading(true);
    try {
      // Cấu trúc Payload khớp với Entity User (addresses là List, roles là Set)
      const payload = {
        firstName: form.firstName,
        lastName: form.lastName,
        mobileNumber: form.mobileNumber,
        email: form.email,
        password: form.password,
        roles: [{ roleId: 102, roleName: "USER" }],
        addresses: [
          {
            street: form.street,
            buildingName: form.buildingName,
            city: form.city,
            state: form.state,
            country: form.country,
            pincode: form.pincode,
          }
        ]
      };

      await authService.register(payload);

      Swal.fire({
        icon: "success",
        title: "Đăng ký thành công!",
        text: "Chào mừng bạn đến với hệ thống của chúng tôi.",
        confirmButtonColor: "#ff6a00",
      }).then(() => navigate("/login"));

    } catch (error) {
      const errorData = error.response?.data;
      if (typeof errorData === "object") {
        setBackendErrors(errorData);
      } else {
        Swal.fire({
          icon: "error",
          title: "Đăng ký thất bại",
          text: errorData || "Email đã tồn tại hoặc lỗi máy chủ.",
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="bg-light py-5" style={{ minHeight: "100vh" }}>
      <div className="container">
        <div className="card mx-auto shadow border-0" style={{ maxWidth: "750px", borderRadius: "15px" }}>
          <div className="card-body p-5">
            <h3 className="text-center fw-bold mb-4" style={{ color: "#ff6a00" }}>TẠO TÀI KHOẢN MỚI</h3>

            <form onSubmit={handleSubmit}>
              {/* Phần 1: Thông tin cá nhân */}
              <div className="mb-4">
                <h6 className="fw-bold border-bottom pb-2 mb-3 text-secondary">1. Thông tin cá nhân</h6>
                <div className="row g-3">
                  <div className="col-md-6">
                    <label className="form-label small">Họ & Tên đệm (5-20 ký tự)</label>
                    <input name="firstName" type="text" className={`form-control ${backendErrors.firstName ? 'is-invalid' : ''}`} value={form.firstName} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.firstName}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small">Tên (5-20 ký tự)</label>
                    <input name="lastName" type="text" className={`form-control ${backendErrors.lastName ? 'is-invalid' : ''}`} value={form.lastName} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.lastName}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small">Email</label>
                    <input name="email" type="email" className={`form-control ${backendErrors.email ? 'is-invalid' : ''}`} value={form.email} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.email}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small">Số điện thoại (10 số)</label>
                    <input name="mobileNumber" type="text" className={`form-control ${backendErrors.mobileNumber ? 'is-invalid' : ''}`} value={form.mobileNumber} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.mobileNumber}</div>
                  </div>
                </div>
              </div>

              {/* Phần 2: Địa chỉ */}
              <div className="mb-4">
                <h6 className="fw-bold border-bottom pb-2 mb-3 text-secondary">2. Địa chỉ cư trú</h6>
                <div className="row g-3">
                  <div className="col-md-8">
                    <label className="form-label small">Số nhà, tên đường</label>
                    <input name="street" type="text" className="form-control" value={form.street} onChange={handleChange} required />
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small">Tòa nhà/Căn hộ</label>
                    <input name="buildingName" type="text" className="form-control" value={form.buildingName} onChange={handleChange} />
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small">Thành phố</label>
                    <input name="city" type="text" className="form-control" value={form.city} onChange={handleChange} required />
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small">Tỉnh/Thành</label>
                    <input name="state" type="text" className="form-control" value={form.state} onChange={handleChange} required />
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small">Mã bưu chính (Pincode)</label>
                    <input name="pincode" type="text" className="form-control" value={form.pincode} onChange={handleChange} required />
                  </div>
                </div>
              </div>

              {/* Phần 3: Mật khẩu */}
              <div className="mb-4">
                <h6 className="fw-bold border-bottom pb-2 mb-3 text-secondary">3. Thiết lập bảo mật</h6>
                <div className="row g-3">
                  <div className="col-md-6">
                    <label className="form-label small">Mật khẩu</label>
                    <input name="password" type="password" className={`form-control ${backendErrors.password ? 'is-invalid' : ''}`} value={form.password} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.password}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small">Xác nhận mật khẩu</label>
                    <input name="confirmPassword" type="password" className={`form-control ${backendErrors.confirmPassword ? 'is-invalid' : ''}`} value={form.confirmPassword} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.confirmPassword}</div>
                  </div>
                </div>
              </div>

              <button type="submit" className="btn btn-primary w-100 fw-bold py-2 shadow-sm" style={{ backgroundColor: "#ff6a00", border: "none" }} disabled={loading}>
                {loading ? (
                  <><span className="spinner-border spinner-border-sm me-2"></span> Đang tạo tài khoản...</>
                ) : "ĐĂNG KÝ NGAY"}
              </button>
            </form>

            <div className="text-center mt-4">
              <span className="text-muted">Bạn đã có tài khoản? </span>
              <Link to="/login" className="fw-bold text-decoration-none" style={{ color: "#ff6a00" }}>Đăng nhập</Link>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Register;
