import api from "./api";

const cartService = {
  getCartId: () => {
    // Lấy thông tin user từ localStorage sau khi đăng nhập thành công
    const userStr = localStorage.getItem("user");
    if (!userStr) return null;
    const user = JSON.parse(userStr);

    // Trả về cartId (Backend của bạn thường để trong user.cart.cartId hoặc user.cartId)
    return user?.cart?.cartId || user?.cartId || null;
  },

  addToCart: async (cartId, productId, quantity) => {
    // Endpoint chuẩn giống Mobile
    return await api.post(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
  }
};

export default cartService;
