import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobileNumber: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    // Xóa lỗi ngay khi người dùng bắt đầu sửa lại
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: "" }));
    }
  };

  const validate = () => {
    let newErrors = {};
    const phoneRegex = /^\d{10}$/;

    if (form.firstName.length < 5 || form.firstName.length > 20) 
        newErrors.firstName = "Họ phải từ 5 đến 20 ký tự.";
    if (form.lastName.length < 5 || form.lastName.length > 20) 
        newErrors.lastName = "Tên phải từ 5 đến 20 ký tự.";
    if (!phoneRegex.test(form.mobileNumber)) 
        newErrors.mobileNumber = "Số điện thoại phải đúng 10 chữ số.";
    if (form.password.length < 6) 
        newErrors.password = "Mật khẩu tối thiểu 6 ký tự.";
    if (form.password !== form.confirmPassword) 
        newErrors.confirmPassword = "Mật khẩu xác nhận không khớp.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) {
      Swal.fire({
        icon: "warning",
        title: "Lưu ý",
        text: "Vui lòng kiểm tra lại thông tin báo đỏ.",
        confirmButtonColor: "#ff6a00",
      });
      return;
    }

    setLoading(true);
    try {
      // Gửi thông tin cá nhân kèm địa chỉ mặc định để Backend không báo lỗi
      const payload = {
        firstName: form.firstName,
        lastName: form.lastName,
        mobileNumber: form.mobileNumber,
        email: form.email,
        password: form.password,
        roles: [{ roleId: 102, roleName: "USER" }],
        addresses: [
          {
            street: "Chưa cập nhật",
            buildingName: "Chưa cập nhật",
            city: "Hà Nội",
            state: "Hà Nội",
            country: "Việt Nam",
            pincode: "100000",
          },
        ],
      };

      await authService.register(payload);

      Swal.fire({
        icon: "success",
        title: "Đăng ký thành công!",
        text: "Chào mừng bạn đến với Guardian.",
        confirmButtonColor: "#ff6a00",
      }).then(() => navigate("/login"));

    } catch (err) {
      const errorData = err.response?.data;
      if (typeof errorData === "object") {
        setErrors(errorData);
      } else {
        Swal.fire({
          icon: "error",
          title: "Đăng ký thất bại",
          text: errorData || "Email hoặc số điện thoại đã tồn tại!",
          confirmButtonColor: "#d33",
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="bg-light py-5" style={{ minHeight: "100vh", display: "flex", alignItems: "center" }}>
      <div className="container">
        <div className="card mx-auto shadow-sm border-0" style={{ maxWidth: "500px", borderRadius: "15px" }}>
          <div className="card-body p-4 p-md-5">
            <div className="text-center mb-4">
              <h3 className="fw-bold" style={{ color: "#ff6a00" }}>TẠO TÀI KHOẢN</h3>
              <p className="text-muted small">Nhanh chóng và dễ dàng</p>
            </div>

            <form onSubmit={handleSubmit} noValidate>
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="form-label small fw-bold">Họ</label>
                  <input name="firstName" type="text" className={`form-control ${errors.firstName ? "is-invalid" : ""}`} value={form.firstName} onChange={handleChange} placeholder="Nguyễn" required />
                  <div className="invalid-feedback">{errors.firstName}</div>
                </div>
                <div className="col-md-6">
                  <label className="form-label small fw-bold">Tên</label>
                  <input name="lastName" type="text" className={`form-control ${errors.lastName ? "is-invalid" : ""}`} value={form.lastName} onChange={handleChange} placeholder="Văn An" required />
                  <div className="invalid-feedback">{errors.lastName}</div>
                </div>

                <div className="col-12">
                  <label className="form-label small fw-bold">Email</label>
                  <input name="email" type="email" className={`form-control ${errors.email ? "is-invalid" : ""}`} value={form.email} onChange={handleChange} placeholder="email@example.com" required />
                  <div className="invalid-feedback">{errors.email}</div>
                </div>

                <div className="col-12">
                  <label className="form-label small fw-bold">Số điện thoại</label>
                  <input name="mobileNumber" type="text" className={`form-control ${errors.mobileNumber ? "is-invalid" : ""}`} value={form.mobileNumber} onChange={handleChange} placeholder="09xxxxxxxx" required />
                  <div className="invalid-feedback">{errors.mobileNumber}</div>
                </div>

                <div className="col-12">
                  <label className="form-label small fw-bold">Mật khẩu</label>
                  <input name="password" type="password" className={`form-control ${errors.password ? "is-invalid" : ""}`} value={form.password} onChange={handleChange} placeholder="••••••••" required />
                  <div className="invalid-feedback">{errors.password}</div>
                </div>

                <div className="col-12">
                  <label className="form-label small fw-bold">Xác nhận mật khẩu</label>
                  <input name="confirmPassword" type="password" className={`form-control ${errors.confirmPassword ? "is-invalid" : ""}`} value={form.confirmPassword} onChange={handleChange} placeholder="••••••••" required />
                  <div className="invalid-feedback">{errors.confirmPassword}</div>
                </div>
              </div>

              <button type="submit" className="btn btn-lg w-100 fw-bold text-white mt-4 shadow-sm" style={{ backgroundColor: "#ff6a00", border: "none" }} disabled={loading}>
                {loading ? <span className="spinner-border spinner-border-sm me-2"></span> : "ĐĂNG KÝ NGAY"}
              </button>
            </form>

            <div className="text-center mt-4">
              <span className="text-muted">Đã có tài khoản? </span>
              <Link to="/login" className="fw-bold text-decoration-none" style={{ color: "#ff6a00" }}>Đăng nhập</Link>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Register;
