import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => { 
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);
  const [quantity, setQuantity] = useState(1);

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: 'guest@example.com' };

  useEffect(() => {
    console.log("üîÑ Product ID t·ª´ URL:", productId);

    const fetchProductDetail = async () => {
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Kh√¥ng t√¨m th·∫•y ID s·∫£n ph·∫©m t·ª´ URL");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);
        console.log("üì° G·ªçi API v·ªõi ID:", productId);

        const productData = await productService.getProductById(productId);
        console.log("‚úÖ D·ªØ li·ªáu nh·∫≠n ƒë∆∞·ª£c:", productData);

        setProduct(productData);
      } catch (err) {
        console.error("‚ùå L·ªói khi t·∫£i chi ti·∫øt s·∫£n ph·∫©m:", err);
        setError(`Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

  // Trong ProductDetail.js - S·ª¨A H√ÄM handleAddToCart
const handleAddToCart = async () => {
  if (!currentUser || !currentUser.email || currentUser.email === 'guest@example.com') {
    alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m v√†o gi·ªè h√†ng');
    navigate('/login');
    return;
  }

  if (!product || product.quantity === 0) {
    alert('‚ùå S·∫£n ph·∫©m ƒë√£ h·∫øt h√†ng');
    return;
  }

  if (quantity > product.quantity) {
    alert(`‚ùå Ch·ªâ c√≤n ${product.quantity} s·∫£n ph·∫©m trong kho`);
    return;
  }

  try {
    setAddingToCart(true);

    // 1. L·∫•y th√¥ng tin user
    const user = JSON.parse(localStorage.getItem('user'));
    const emailId = user.email;

    // 2. L·∫§Y HO·∫∂C T·∫†O cartId - QUAN TR·ªåNG
    const cartId = await cartService.getOrCreateCartId(emailId);

    if (!cartId) {
      alert('‚ùå Kh√¥ng th·ªÉ t·∫°o gi·ªè h√†ng. Vui l√≤ng th·ª≠ l·∫°i.');
      return;
    }

    console.log("üõí Th√™m v√†o gi·ªè h√†ng:", { cartId, productId, quantity });

    // 3. Th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng
    await cartService.addToCart(cartId, productId, quantity);

    // 4. C·∫≠p nh·∫≠t gi·ªè h√†ng trong localStorage v√† state
    const updatedCart = await cartService.getCart(emailId, cartId);
    cartService.updateCartInStorage(updatedCart);

    // 5. Th√¥ng b√°o th√†nh c√¥ng
    alert(`‚úÖ ƒê√£ th√™m ${quantity} "${product.productName}" v√†o gi·ªè h√†ng!`);

    // 6. C·∫≠p nh·∫≠t cart count global (n·∫øu c√≥)
    window.dispatchEvent(new Event('cartUpdated'));

    // 7. Option: Chuy·ªÉn ƒë·∫øn gi·ªè h√†ng sau 1.5s
    setTimeout(() => {
      navigate('/cart');
    }, 1500);

  } catch (error) {
    console.error('‚ùå L·ªói khi th√™m v√†o gi·ªè h√†ng:', error);

    let errorMessage = 'L·ªói khi th√™m v√†o gi·ªè h√†ng';

    if (error.response?.status === 400) {
      errorMessage = 'S·∫£n ph·∫©m ƒë√£ c√≥ trong gi·ªè h√†ng ho·∫∑c s·ªë l∆∞·ª£ng kh√¥ng h·ª£p l·ªá';
    } else if (error.response?.status === 404) {
      errorMessage = 'Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m ho·∫∑c gi·ªè h√†ng';
    } else if (error.message?.includes('cartId')) {
      errorMessage = 'L·ªói gi·ªè h√†ng: ' + error.message;
    } else if (error.response?.data?.message) {
      errorMessage = error.response.data.message;
    }

    alert(`‚ùå ${errorMessage}`);
  } finally {
    setAddingToCart(false);
  }
};

  const handleBuyNow = async () => {
    if (!currentUser || !currentUser.email || currentUser.email === 'guest@example.com') {
      alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ mua h√†ng');
      navigate('/login');
      return;
    }

    try {
      setAddingToCart(true);

      const user = JSON.parse(localStorage.getItem('user'));
      const emailId = user.email;
      const cartId = user.cart?.cartId;

      if (!cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
        return;
      }

      // Th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng
      await cartService.addToCart(cartId, productId, quantity);

      // Chuy·ªÉn th·∫≥ng ƒë·∫øn checkout
      navigate('/checkout');

    } catch (error) {
      console.error('‚ùå L·ªói khi mua ngay:', error);
      alert('‚ùå L·ªói khi x·ª≠ l√Ω ƒë∆°n h√†ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setAddingToCart(false);
    }
  };

  const increaseQuantity = () => {
    if (product && quantity < product.quantity) {
      setQuantity(quantity + 1);
    }
  };

  const decreaseQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center py-5">
          <div className="spinner-border text-primary" style={{width: '3rem', height: '3rem'}} role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-3">ƒêang t·∫£i th√¥ng tin s·∫£n ph·∫©m...</p>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger text-center">
          <h4>‚ùå Kh√¥ng th·ªÉ t·∫£i s·∫£n ph·∫©m</h4>
          <p>{error || "S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i"}</p>
          <div className="mt-3">
            <button 
              className="btn btn-primary me-2"
              onClick={() => navigate(-1)}
            >
              ‚Üê Quay l·∫°i
            </button>
            <Link to="/products" className="btn btn-outline-primary">
              üè† V·ªÅ trang s·∫£n ph·∫©m
            </Link>
          </div>
        </div>
      </div>
    );
  }

  const {
    productName,
    price,
    specialPrice,
    quantity: stockQuantity,
    discount,
    description,
    image,
    category
  } = product;

  const displayPrice = specialPrice || price;
  const hasDiscount = discount > 0;

  return (
    <div className="container mt-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">üè† Trang ch·ªß</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/products" className="text-decoration-none">S·∫£n ph·∫©m</Link>
          </li>
          {category && (
            <li className="breadcrumb-item">
              <Link to={`/products?category=${category.categoryId}`} className="text-decoration-none">
                {category.categoryName}
              </Link>
            </li>
          )}
          <li className="breadcrumb-item active" aria-current="page">
            {productName}
          </li>
        </ol>
      </nav>

      <div className="row">
        {/* H√¨nh ·∫£nh s·∫£n ph·∫©m */}
        <div className="col-lg-6 mb-4">
          <div className="card shadow-sm border-0">
            <div className="card-body p-3">
              <img
                src={`${IMAGE_BASE_URL}/${image}`}
                alt={productName}
                onError={(e) => {
                  e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png";
                }}
                className="img-fluid rounded"
                style={{ 
                  maxHeight: "500px", 
                  width: "100%", 
                  objectFit: "cover" 
                }}
              />
            </div>
          </div>
        </div>

        {/* Th√¥ng tin s·∫£n ph·∫©m */}
        <div className="col-lg-6">
          <div className="card shadow-sm border-0 h-100">
            <div className="card-body p-4">
              <h1 className="h2 text-dark mb-3">{productName}</h1>

              {/* Category */}
              {category && (
                <div className="mb-3">
                  <span className="badge bg-secondary fs-6">
                    {category.categoryName}
                  </span>
                </div>
              )}

              {/* Gi√° s·∫£n ph·∫©m */}
              <div className="mb-4">
                <div className="d-flex align-items-center flex-wrap">
                  <span className="h2 text-danger fw-bold me-3">
                    {displayPrice?.toLocaleString()} ‚Ç´
                  </span>

                  {specialPrice && price && specialPrice < price && (
                    <span className="h5 text-muted text-decoration-line-through me-2">
                      {price?.toLocaleString()} ‚Ç´
                    </span>
                  )}

                  {hasDiscount && (
                    <span className="badge bg-success fs-6">
                      -{discount}%
                    </span>
                  )}
                </div>
              </div>

              {/* T√¨nh tr·∫°ng kho */}
              <div className="mb-4">
                <span className={`badge ${stockQuantity === 0 ? 'bg-danger' : 'bg-success'} fs-6 p-2`}>
                  {stockQuantity === 0 ? 'üõë H·∫øt h√†ng' : `‚úÖ C√≤n ${stockQuantity} s·∫£n ph·∫©m`}
                </span>
              </div>

              {/* Ch·ªçn s·ªë l∆∞·ª£ng */}
              {stockQuantity > 0 && (
                <div className="mb-4">
                  <label className="form-label fw-semibold">S·ªë l∆∞·ª£ng:</label>
                  <div className="d-flex align-items-center" style={{ maxWidth: '150px' }}>
                    <button 
                      className="btn btn-outline-secondary"
                      onClick={decreaseQuantity}
                      disabled={quantity <= 1}
                    >
                      -
                    </button>
                    <span className="mx-3 fw-bold fs-5">{quantity}</span>
                    <button 
                      className="btn btn-outline-secondary"
                      onClick={increaseQuantity}
                      disabled={quantity >= stockQuantity}
                    >
                      +
                    </button>
                  </div>
                  <small className="text-muted">T·ªëi ƒëa: {stockQuantity} s·∫£n ph·∫©m</small>
                </div>
              )}

              {/* N√∫t h√†nh ƒë·ªông */}
              <div className="d-grid gap-2 d-md-flex mb-4">
                <button 
                  className="btn btn-primary btn-lg flex-fill me-md-2"
                  onClick={handleAddToCart}
                  disabled={stockQuantity === 0 || addingToCart}
                >
                  {addingToCart ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      ƒêang th√™m...
                    </>
                  ) : (
                    <>
                      üõí Th√™m v√†o gi·ªè h√†ng
                    </>
                  )}
                </button>

                <button 
                  className="btn btn-danger btn-lg flex-fill"
                  onClick={handleBuyNow}
                  disabled={stockQuantity === 0 || addingToCart}
                >
                  ‚ö° Mua ngay
                </button>
              </div>

              {/* Th√¥ng b√°o ƒëƒÉng nh·∫≠p */}
              {(!currentUser || currentUser.email === 'guest@example.com') && (
                <div className="alert alert-warning">
                  <small>üí° Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng</small>
                </div>
              )}

              {/* M√¥ t·∫£ s·∫£n ph·∫©m */}
              <div className="mt-4">
                <h5 className="text-dark mb-3">üìù M√¥ t·∫£ s·∫£n ph·∫©m</h5>
                <div className="border-top pt-3">
                  <p className="text-muted" style={{ whiteSpace: 'pre-line', lineHeight: '1.6' }}>
                    {description || "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£ s·∫£n ph·∫©m..."}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
