import React, { useState, useEffect, useCallback } from "react";
import productService from "../../../services/productService";
import api from "../../../services/api";
import Swal from "sweetalert2";

const ProductListAD = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [imagePreview, setImagePreview] = useState(null);
  const [formData, setFormData] = useState({
    productId: "",
    productName: "",
    price: "",
    discount: 0,
    quantity: 1,
    description: "",
    categoryId: ""
  });
  const [selectedFile, setSelectedFile] = useState(null);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0 });

  const fetchData = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const prodRes = await productService.getAllProductsAD(page, 10);
      const catRes = await api.get("/public/categories");

      setProducts(prodRes.content || []);
      setPageConfig({ 
        pageNumber: prodRes.pageNumber || 0, 
        totalPages: prodRes.totalPages || 0 
      });
      setCategories(catRes.data.content || catRes.data || []);
    } catch (err) {
      console.error("‚ùå L·ªói Fetch Data:", err.response || err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { fetchData(); }, [fetchData]);

const handleOpenModal = (product = null) => {
  if (product) {
    setIsEdit(true);
    setFormData({
      productId: product.productId,
      productName: product.productName,
      price: product.price,
      discount: product.discount,
      quantity: product.quantity,
      description: product.description,
      // L·∫•y categoryId t·ª´ object category m√† Backend tr·∫£ v·ªÅ
      categoryId: product.category?.categoryId || "" 
    });
    setImagePreview(product.image ? `http://localhost:8080/api/public/products/image/${product.image}` : null);
  } else {
    setIsEdit(false);
    // Reset form v·ªÅ tr·∫Øng
    setFormData({ productId: "", productName: "", price: "", discount: 0, quantity: 1, description: "", categoryId: "" });
    setImagePreview(null);
  }
  setSelectedFile(null);
  setShowModal(true);
};
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(file));
      console.log("üì∏ ƒê√£ ch·ªçn file:", file.name);
    }
  };

  // ================= X·ª¨ L√ù SUBMIT (C√ì CONSOLE DEBUG) =================
const handleSubmit = async (e) => {
  e.preventDefault();
  
  // 1. Validation nhanh t·∫°i Client ƒë·ªÉ tr√°nh BE tr·∫£ l·ªói 400
  if (formData.productName.length < 3) {
    return Swal.fire("L·ªói", "T√™n s·∫£n ph·∫©m ph·∫£i c√≥ √≠t nh·∫•t 3 k√Ω t·ª±", "error");
  }
  if (formData.description.length < 6) {
    return Swal.fire("L·ªói", "M√¥ t·∫£ ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±", "error");
  }

  const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
  const config = {
    headers: { Authorization: `Bearer ${token}` }
  };

  try {
    // 2. T·∫°o Payload kh·ªõp 100% v·ªõi Entity Product.java
    const productPayload = {
      productName: formData.productName.trim(),
      description: formData.description.trim(),
      quantity: parseInt(formData.quantity) || 0,
      price: parseFloat(formData.price) || 0,
      discount: parseFloat(formData.discount) || 0,
      // BE c√≥ tr∆∞·ªùng specialPrice, d√π BE t·ª± t√≠nh nh∆∞ng ta c·ª© g·ª≠i 0 ho·∫∑c 
      // gi√° tr·ªã t·∫°m t√≠nh ƒë·ªÉ tr√°nh Hibernate check null
      specialPrice: 0 
    };

    let savedProduct;

    if (isEdit) {
      // üîÑ C·∫¨P NH·∫¨T: /admin/products/{id}
      const res = await api.put(`/admin/products/${formData.productId}`, productPayload, config);
      savedProduct = res.data;
    } else {
      // ‚ûï TH√äM M·ªöI: B·∫ÆT BU·ªòC c√≥ ch·ªØ "s" ·ªü cu·ªëi "products"
      const catId = formData.categoryId;
      const res = await api.post(`/admin/categories/${catId}/products`, productPayload, config);
      savedProduct = res.data;
    }

    // 3. X·ª≠ l√Ω Upload ·∫£nh (S·ª≠ d·ª•ng productId tr·∫£ v·ªÅ t·ª´ savedProduct)
    if (selectedFile && (savedProduct?.productId || formData.productId)) {
      const pId = savedProduct?.productId || formData.productId;
      const imageForm = new FormData();
      imageForm.append("image", selectedFile);
      
      await api.put(`/admin/products/${pId}/image`, imageForm, {
        headers: { 
          ...config.headers,
          "Content-Type": "multipart/form-data" 
        }
      });
    }

    Swal.fire("Th√†nh c√¥ng!", "S·∫£n ph·∫©m ƒë√£ ƒë∆∞·ª£c l∆∞u v√†o Database.", "success");
    setShowModal(false);
    fetchData(); 

  } catch (err) {
    console.error("üî• L·ªói chi ti·∫øt:", err.response?.data);
    
    // ƒê·ªçc l·ªói Validation t·ª´ BE (n·∫øu c√≥)
    let errorMsg = "L·ªói kh√¥ng x√°c ƒë·ªãnh";
    if (err.response?.status === 400) {
      // BE tr·∫£ v·ªÅ l·ªói validation c·ªßa @Size ho·∫∑c @NotBlank
      errorMsg = err.response.data.message || "D·ªØ li·ªáu kh√¥ng h·ª£p l·ªá (Check ƒë·ªô d√†i t√™n/m√¥ t·∫£)";
    } else if (err.response?.status === 401) {
      errorMsg = "Token h·∫øt h·∫°n ho·∫∑c kh√¥ng c√≥ quy·ªÅn Admin";
    }
    
    Swal.fire("Th·∫•t b·∫°i", errorMsg, "error");
  }
};

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "X√≥a s·∫£n ph·∫©m?",
      text: "H√†nh ƒë·ªông n√†y kh√¥ng th·ªÉ ho√†n t√°c!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "X√≥a"
    });

    if (confirm.isConfirmed) {
      try {
        await api.delete(`/admin/products/${id}`);
        console.log("üóëÔ∏è ƒê√£ x√≥a s·∫£n ph·∫©m ID:", id);
        Swal.fire("ƒê√£ x√≥a!", "Th√†nh c√¥ng.", "success");
        fetchData();
      } catch (err) {
        console.error("‚ùå L·ªói khi x√≥a:", err.response?.data || err.message);
        Swal.fire("L·ªói", "Kh√¥ng th·ªÉ x√≥a s·∫£n ph·∫©m n√†y.", "error");
      }
    }
  };

  if (loading && products.length === 0) return <div className="text-center mt-5"><h3>üöÄ ƒêang t·∫£i d·ªØ li·ªáu...</h3></div>;

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="m-0 fw-bold text-dark"><i className="bi bi-box-seam me-2"></i>QU·∫¢N L√ù S·∫¢N PH·∫®M</h5>
          <button className="btn btn-primary btn-sm px-3" onClick={() => handleOpenModal()}>
            + Th√™m m·ªõi
          </button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th>ID</th>
                <th>·∫¢nh</th>
                <th>T√™n s·∫£n ph·∫©m</th>
                <th>Gi√° g·ªëc</th>
                <th>Gi√° b√°n</th>
                <th>Kho</th>
                <th className="text-center">Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {products.map(p => (
                <tr key={p.productId}>
                  <td>{p.productId}</td>
                  <td>
                    <img 
                      src={p.image ? `http://localhost:8080/api/public/products/image/${p.image}` : "https://via.placeholder.com/50"} 
                      style={{width: "45px", height: "45px", objectFit: "cover", borderRadius: "8px"}}
                      alt="product"
                      onError={(e) => e.target.src="https://via.placeholder.com/50"}
                    />
                  </td>
                  <td className="fw-semibold">{p.productName}</td>
                  <td className="text-muted text-decoration-line-through small">{p.price?.toLocaleString()}‚Ç´</td>
                  <td className="text-danger fw-bold">{p.specialPrice?.toLocaleString()}‚Ç´</td>
                  <td>
                    {p.quantity > 0 ? (
                        <span className="badge bg-success-subtle text-success">{p.quantity} c√°i</span>
                    ) : (
                        <span className="badge bg-danger-subtle text-danger">H·∫øt h√†ng</span>
                    )}
                  </td>
                  <td className="text-center">
                    <button className="btn btn-link text-info p-0 me-3" onClick={() => handleOpenModal(p)}>S·ª≠a</button>
                    <button className="btn btn-link text-danger p-0" onClick={() => handleDelete(p.productId)}>X√≥a</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.6)", backdropFilter: "blur(4px)"}}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content border-0">
              <form onSubmit={handleSubmit}>
                <div className="modal-header border-0">
                  <h5 className="fw-bold">{isEdit ? "Ch·ªânh s·ª≠a s·∫£n ph·∫©m" : "Th√™m s·∫£n ph·∫©m m·ªõi"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body row g-3">
                  <div className="col-md-7">
                    <label className="form-label small fw-bold">T√™n s·∫£n ph·∫©m</label>
                    <input type="text" className="form-control" required value={formData.productName}
                           onChange={e => setFormData({...formData, productName: e.target.value})}/>
                  </div>
                  <div className="col-md-5">
                    <label className="form-label small fw-bold">Danh m·ª•c</label>
                    <select className="form-select" required value={formData.categoryId}
                            onChange={e => setFormData({...formData, categoryId: e.target.value})}>
                      <option value="">Ch·ªçn danh m·ª•c...</option>
                      {categories.map(c => <option key={c.categoryId} value={c.categoryId}>{c.categoryName}</option>)}
                    </select>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small fw-bold">Gi√° b√°n (‚Ç´)</label>
                    <input type="number" className="form-control" value={formData.price}
                           onChange={e => setFormData({...formData, price: e.target.value})}/>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small fw-bold">Gi·∫£m gi√° (%)</label>
                    <input type="number" className="form-control" value={formData.discount}
                           onChange={e => setFormData({...formData, discount: e.target.value})}/>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small fw-bold">S·ªë l∆∞·ª£ng kho</label>
                    <input type="number" className="form-control" value={formData.quantity}
                           onChange={e => setFormData({...formData, quantity: e.target.value})}/>
                  </div>
                  <div className="col-12">
                    <label className="form-label small fw-bold">M√¥ t·∫£ s·∫£n ph·∫©m</label>
                    <textarea className="form-control" rows="3" value={formData.description}
                              onChange={e => setFormData({...formData, description: e.target.value})}></textarea>
                  </div>
                  <div className="col-12">
                    <label className="form-label small fw-bold">H√¨nh ·∫£nh</label>
                    <div className="d-flex align-items-center gap-3 p-3 border rounded bg-light">
                      <div style={{width: "80px", height: "80px", border: "2px dashed #ccc", overflow: "hidden", display: "flex", alignItems: "center", justifyContent: "center", background: "#fff"}}>
                        {imagePreview ? <img src={imagePreview} style={{width: "100%", height: "100%", objectFit: "cover"}} /> : <i className="bi bi-image"></i>}
                      </div>
                      <input type="file" className="form-control" onChange={handleFileChange}/>
                    </div>
                  </div>
                </div>
                <div className="modal-footer border-0">
                  <button type="button" className="btn btn-light btn-sm" onClick={() => setShowModal(false)}>H·ªßy</button>
                  <button type="submit" className="btn btn-dark btn-sm px-4">L∆∞u</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductListAD;
