import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Header = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // üîπ L·∫•y th√¥ng tin ng∆∞·ªùi d√πng t·ª´ localStorage khi load trang
  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  // üîπ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("jwt");
    localStorage.removeItem("user");
    localStorage.removeItem("role");
    document.cookie = "role=; path=/;";
    alert("ƒê√£ ƒëƒÉng xu·∫•t!");
    navigate("/site/auth/login");
  };

  return (
    <div className="navbar bg-white text-red shadow-md px-4">
      <div className="navbar-start">
        <img
          src="/images/logo.png"
          width={200}
          height={200}
          alt="NGUYEN THI THU THUY_SHOP"
          className="shadow-amber-950 shadow-2xl rounded-lg"
        />
      </div>

      <div className="navbar-center hidden lg:flex">
        <ul className="menu menu-horizontal px-1 space-x-4 text-orange-500">
          <li><Link to="/" className="hover:text-orange-700">Home</Link></li>
          <li><Link to="/site/product" className="hover:text-orange-700">Product</Link></li>
          <li><Link to="/about" className="hover:text-orange-700">About</Link></li>
          <li><Link to="/site/page" className="hover:text-orange-700">Page</Link></li>
          <li><Link to="/contact" className="hover:text-orange-700">Contact</Link></li>
        </ul>
      </div>

      <div className="navbar-end flex items-center space-x-4">
        {/* üõí Gi·ªè h√†ng */}
        <div className="relative">
          <button className="btn btn-ghost btn-circle">
            <svg xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6" fill="none" viewBox="0 0 24 24"
              stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2"
                d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
            <span className="badge badge-sm absolute top-0 right-0 bg-red-500 text-white">
              8
            </span>
          </button>
        </div>

        {/* üë§ User */}
        <div className="dropdown dropdown-end">
          <div className="flex flex-col items-center">
            <div tabIndex={0} role="button" className="btn btn-ghost btn-circle avatar">
              <div className="w-10 h-10 rounded-full ring ring-primary ring-offset-base-100 ring-offset-2 overflow-hidden">
                <img
                  src="/images/avatar.jpeg"
                  width={40}
                  height={40}
                  alt="User Avatar"
                  className="rounded-full"
                />
              </div>
            </div>

            {user && (
              <span className="text-sm text-center font-medium text-gray-700 block">
                {user.username || user.first_name || "User"}
              </span>
            )}
          </div>

          {/* Menu Dropdown */}
          {user ? (
            <ul
              tabIndex={0}
              className="menu menu-sm dropdown-content bg-white text-black rounded-box shadow-lg w-52 p-2 mt-2">
              <li><Link to="/profile">Profile</Link></li>
              <li><Link to="/settings">Settings</Link></li>
              <li><button onClick={handleLogout} className="w-full text-left">Logout</button></li>
            </ul>
          ) : (
            <div className="mt-2">
              <Link to="/site/auth/login" className="text-blue-600 hover:underline">
                Login
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Header;
