import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';

const ProductSold = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchTopSellingProducts();
  }, []);

  const fetchTopSellingProducts = async () => {
    try {
      setLoading(true);
      const response = await productService.getTopSellingProducts(0, 8);
      setProducts(response.content || []);
    } catch (err) {
      console.error('❌ Lỗi khi lấy sản phẩm bán chạy:', err);
      setError('Không thể tải sản phẩm bán chạy');
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(price);
  };

  const getProductImageUrl = (imageName) => {
    if (!imageName || imageName === 'default.png') {
      return '/images/default-product.png';
    }
    return `/api/public/products/image/${imageName}`;
  };

  if (loading) {
    return (
      <section className="section-content padding-y">
        <div className="container">
          <div className="row">
            {[...Array(8)].map((_, index) => (
              <div key={index} className="col-xl-3 col-lg-4 col-md-6 col-sm-6 mb-4">
                <div className="card product-card h-100">
                  <div className="img-wrapper" style={{ height: '200px', backgroundColor: '#f8f9fa' }}>
                    <div className="d-flex justify-content-center align-items-center h-100">
                      <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                      </div>
                    </div>
                  </div>
                  <div className="card-body">
                    <div className="placeholder-glow">
                      <span className="placeholder col-8 mb-2"></span>
                      <span className="placeholder col-6 mb-2"></span>
                      <span className="placeholder col-4"></span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="section-content padding-y">
        <div className="container">
          <div className="alert alert-danger text-center">
            <i className="fas fa-exclamation-triangle me-2"></i>
            {error}
            <button 
              className="btn btn-sm btn-outline-danger ms-3"
              onClick={fetchTopSellingProducts}
            >
              Thử lại
            </button>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="section-content padding-y bg-light">
      <div className="container">
        {/* Header */}
        <div className="row mb-4">
          <div className="col-md-8">
            <h2 className="section-title">
              <i className="fas fa-fire text-danger me-2"></i>
              Sản Phẩm Bán Chạy
            </h2>
            <p className="text-muted mb-0">Những sản phẩm được ưa chuộng nhất</p>
          </div>
          <div className="col-md-4 text-end">
            <Link to="/products/top-selling" className="btn btn-outline-primary">
              Xem tất cả <i className="fas fa-arrow-right ms-1"></i>
            </Link>
          </div>
        </div>

        {/* Products Grid */}
        <div className="row">
          {products.length > 0 ? (
            products.map((product, index) => (
              <div key={product.productId} className="col-xl-3 col-lg-4 col-md-6 col-sm-6 mb-4">
                <div className="card product-card h-100 shadow-sm">
                  {/* Top Badges */}
                  <div className="position-absolute top-0 start-0 end-0 d-flex justify-content-between p-2">
                    {/* Top Ranking Badge */}
                    {index < 3 && (
                      <span className={`badge ${index === 0 ? 'bg-danger' : index === 1 ? 'bg-warning' : 'bg-info'}`}>
                        <i className={`fas ${index === 0 ? 'fa-crown' : index === 1 ? 'fa-medal' : 'fa-award'} me-1`}></i>
                        Top {index + 1}
                      </span>
                    )}

                    {/* Sold Count Badge */}
                    <span className="badge bg-dark">
                      <i className="fas fa-shopping-cart me-1"></i>
                      {product.sold || 0}
                    </span>
                  </div>

                  {/* Product Image */}
                  <div className="img-wrapper position-relative">
                    <Link to={`/products/${product.productId}`}>
                      <img
                        src={getProductImageUrl(product.image)}
                        className="card-img-top"
                        alt={product.productName}
                        style={{ 
                          height: '200px', 
                          objectFit: 'cover',
                          width: '100%'
                        }}
                        onError={(e) => {
                          e.target.src = '/images/default-product.png';
                        }}
                      />
                    </Link>

                    {/* Discount Badge */}
                    {product.discount > 0 && (
                      <div className="position-absolute bottom-0 start-0 m-2 badge bg-danger">
                        -{product.discount}%
                      </div>
                    )}
                  </div>

                  {/* Card Body */}
                  <div className="card-body d-flex flex-column">
                    {/* Product Name */}
                    <h6 className="card-title mb-2">
                      <Link 
                        to={`/products/${product.productId}`} 
                        className="text-dark text-decoration-none product-name"
                        title={product.productName}
                      >
                        {product.productName}
                      </Link>
                    </h6>

                    {/* Product Info */}
                    <div className="product-info mb-3">
                      {/* Views Count */}
                      <div className="mb-1">
                        <small className="text-muted">
                          <i className="fas fa-eye me-1"></i>
                          {product.views || 0} lượt xem
                        </small>
                      </div>

                      {/* Stock Status */}
                      <div>
                        {product.quantity > 0 ? (
                          <small className="text-success">
                            <i className="fas fa-check-circle me-1"></i>
                            Còn {product.quantity} sản phẩm
                          </small>
                        ) : (
                          <small className="text-danger">
                            <i className="fas fa-times-circle me-1"></i>
                            Hết hàng
                          </small>
                        )}
                      </div>
                    </div>

                    {/* Price Section */}
                    <div className="price-section mt-auto">
                      {product.discount > 0 ? (
                        <>
                          <div className="d-flex align-items-center mb-1">
                            <strong className="text-danger h6 mb-0">
                              {formatPrice(product.specialPrice || product.price * (1 - product.discount / 100))}
                            </strong>
                          </div>
                          <small className="text-muted text-decoration-line-through">
                            {formatPrice(product.price)}
                          </small>
                        </>
                      ) : (
                        <strong className="text-primary h6 mb-0">
                          {formatPrice(product.price)}
                        </strong>
                      )}
                    </div>

                    {/* Action Button */}
                    <div className="d-grid gap-2 mt-3">
                      <Link 
                        to={`/products/${product.productId}`} 
                        className={`btn btn-primary btn-sm ${product.quantity <= 0 ? 'disabled' : ''}`}
                      >
                        <i className="fas fa-shopping-cart me-1"></i>
                        {product.quantity > 0 ? 'Mua ngay' : 'Hết hàng'}
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12 text-center py-5">
              <div className="text-muted">
                <i className="fas fa-box-open fa-3x mb-3"></i>
                <h5>Không có sản phẩm bán chạy</h5>
                <p>Hiện chưa có sản phẩm nào được bán</p>
                <button 
                  className="btn btn-outline-primary mt-2"
                  onClick={fetchTopSellingProducts}
                >
                  <i className="fas fa-redo me-1"></i>
                  Tải lại
                </button>
              </div>
            </div>
          )}
        </div>

        {/* View More for Mobile */}
        {products.length > 0 && (
          <div className="row mt-4 d-md-none">
            <div className="col-12 text-center">
              <Link to="/products/top-selling" className="btn btn-primary">
                Xem thêm sản phẩm bán chạy
              </Link>
            </div>
          </div>
        )}
      </div>

      <style jsx>{`
        .section-content {
          padding: 3rem 0;
        }

        .section-title {
          font-weight: 700;
          color: #2c3e50;
          position: relative;
          padding-bottom: 10px;
        }

        .section-title::after {
          content: '';
          position: absolute;
          bottom: 0;
          left: 0;
          width: 60px;
          height: 3px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          border-radius: 2px;
        }

        .product-card {
          transition: all 0.3s ease;
          border: 1px solid #e9ecef;
          border-radius: 12px;
          overflow: hidden;
        }

        .product-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 8px 25px rgba(0,0,0,0.15) !important;
          border-color: #007bff;
        }

        .img-wrapper {
          overflow: hidden;
          background: #f8f9fa;
        }

        .product-card img {
          transition: transform 0.3s ease;
        }

        .product-card:hover img {
          transform: scale(1.05);
        }

        .product-name {
          display: -webkit-box;
          -webkit-line-clamp: 2;
          -webkit-box-orient: vertical;
          overflow: hidden;
          line-height: 1.4;
        }

        .product-name:hover {
          color: #007bff !important;
        }

        .price-section {
          border-top: 1px solid #f1f1f1;
          padding-top: 12px;
        }

        @media (max-width: 768px) {
          .section-content {
            padding: 2rem 0;
          }

          .product-card {
            margin-bottom: 1rem;
          }
        }
      `}</style>
    </section>
  );
};

export default ProductSold;
