import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";
import "../assets/css/style.css"; 

const Header = () => {
  // ... (Gi·ªØ nguy√™n to√†n b·ªô ph·∫ßn logic state v√† function c·ªßa m√†y b√™n tr√™n)
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/product`, { state: { searchKeyword: searchTerm.trim() } });
    }
  };

  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) { console.error(err); }
      }
    } else { setUser(null); setUserInfo(null); }
  }, []);

  const loadCartCount = async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      if (currentUser?.email) {
        const count = await cartService.getCartCount(currentUser.email);
        setCartCount(count);
      } else {
        const localCart = cartService.getCartFromStorage();
        setCartCount(cartService.getTotalItems(localCart?.products || []));
      }
    } catch (error) { setCartCount(0); }
  };

  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } catch (err) { console.error(err); } finally { setLoadingCategories(false); }
  };

  useEffect(() => {
    initUser();
    fetchCategories();
    window.addEventListener("authUpdated", initUser);
    return () => window.removeEventListener("authUpdated", initUser);
  }, [initUser]);

  useEffect(() => {
    loadCartCount();
    window.addEventListener('cartUpdated', loadCartCount);
    return () => window.removeEventListener('cartUpdated', loadCartCount);
  }, [user]);

  const handleCategoryClick = (category) => {
    navigate(`/product`, { 
      state: { 
        selectedCategory: category.categoryId || category.id, 
        categoryName: category.categoryName || category.name 
      } 
    });
  };

  const handleLogout = () => {
    Swal.fire({
      title: 'B·∫°n mu·ªën ƒëƒÉng xu·∫•t?',
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#ff6a00',
      confirmButtonText: 'ƒêƒÉng xu·∫•t',
      cancelButtonText: 'H·ªßy'
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.clear();
        setUser(null);
        setUserInfo(null);
        setCartCount(0);
        window.dispatchEvent(new Event("authUpdated"));
        navigate("/login");
      }
    });
  };

  const renderUserName = () => {
    if (!user) return "T√†i kho·∫£n";
    return userInfo?.firstName || user.firstName || user.email?.split('@')[0] || "User";
  };

  return (
    <header className="section-header sticky-top bg-white">
      {/* Top Ads - L√†m cho thanh m·ªèng v√† ch·ªØ ch·∫°y m∆∞·ª£t h∆°n */}
      <div className="top-banner-ads py-1 small text-center fw-medium">
        üî• N·ªôi th√†nh H√† N·ªôi - TP. HCM | GIAO SI√äU T·ªêC 2H - FREESHIP 0ƒê
      </div>

      <section className="header-main py-3 border-bottom">
        <div className="container">
          <div className="row align-items-center">
            {/* Logo - CƒÉn tr√°i s·∫Øc n√©t */}
            <div className="col-lg-2 col-md-3 col-6">
              <Link to="/" className="text-decoration-none">
                <h2 className="logo-brand mb-0">Shop<span className="accent">BeAnh</span></h2>
              </Link>
            </div>

            {/* Thanh t√¨m ki·∫øm - Bo tr√≤n c·ª±c ƒë·∫°i (Pill shape) */}
            <div className="col-lg-6 col-md-5 d-none d-md-block">
              <form className="search-modern-wrap shadow-sm" onSubmit={handleSearchSubmit}>
                <input 
                  type="text" 
                  placeholder="T√¨m ki·∫øm s·∫£n ph·∫©m..." 
                  className="form-control border-0 px-4" 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button type="submit" className="btn btn-orange-rounded">
                  <i className="fa fa-search"></i>
                </button>
              </form>
            </div>

            {/* Actions - User & Cart s·∫Øp x·∫øp tho√°ng h∆°n */}
            <div className="col-lg-4 col-md-4 col-6">
              <div className="d-flex justify-content-end align-items-center gap-4">

                {/* User Dropdown */}
                <div className="user-dropdown-container position-relative">
                  <div className="d-flex align-items-center gap-2 pointer-cursor">
                    <div className="icon-box-light">
                      <i className="fa fa-user"></i>
                    </div>
                    <div className="d-none d-lg-block">
                      <div className="smaller text-muted">Xin ch√†o,</div>
                      <div className="small fw-bold">{renderUserName()}</div>
                    </div>
                  </div>

                  <ul className="user-mega-menu shadow-lg rounded-4 border-0">
                    {!user ? (
                      <>
                        <li><Link to="/login" className="user-menu-item"><i className="fa fa-sign-in-alt me-2"></i> ƒêƒÉng nh·∫≠p</Link></li>
                        <li><Link to="/register" className="user-menu-item border-top"><i className="fa fa-user-plus me-2"></i> ƒêƒÉng k√Ω</Link></li>
                      </>
                    ) : (
                      <>
                        <li><Link to="/profile" className="user-menu-item"><i className="fa fa-user-circle me-2"></i> Trang c√° nh√¢n</Link></li>
                        <li><Link to="/orders" className="user-menu-item"><i className="fa fa-shopping-bag me-2"></i> ƒê∆°n h√†ng</Link></li>
                        <li><hr className="dropdown-divider" /></li>
                        <li>
                          <button onClick={handleLogout} className="user-menu-item w-100 text-start border-0 bg-transparent text-danger fw-bold">
                            <i className="fa fa-power-off me-2"></i> ƒêƒÉng xu·∫•t
                          </button>
                        </li>
                      </>
                    )}
                  </ul>
                </div>

                {/* Gi·ªè h√†ng - Thi·∫øt k·∫ø l·∫°i Badge */}
                <Link to="/cart" className="text-decoration-none cart-action-wrap">
                  <div className="d-flex align-items-center gap-2">
                    <div className="icon-box-orange position-relative">
                      <i className="fa fa-shopping-cart"></i>
                      <span className="cart-badge-modern shadow-sm">
                        {cartCount}
                      </span>
                    </div>
                    <div className="d-none d-lg-block">
                      <div className="smaller text-muted">Gi·ªè h√†ng</div>
                      <div className="small fw-bold text-dark">S·∫£n ph·∫©m</div>
                    </div>
                  </div>
                </Link>

              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Navigation - ƒê∆°n gi·∫£n, hi·ªán ƒë·∫°i */}
      <nav className="nav-bottom-white d-none d-lg-block shadow-sm">
        <div className="container">
          <ul className="nav d-flex align-items-center gap-1">
            <li className="nav-item dropdown-hover-container">
              <a className="nav-link all-cat-link py-3" href="#" onClick={(e) => e.preventDefault()}>
                <i className="fa fa-bars me-2"></i> T·∫§T C·∫¢ DANH M·ª§C
              </a>
              <ul className="mega-dropdown-menu shadow-lg scrollable-menu border-0 rounded-bottom-4">
                {loadingCategories ? (
                  <li className="text-center p-4">
                    <div className="spinner-border spinner-border-sm text-warning"></div>
                  </li>
                ) : (
                  categories.map((cat, idx) => (
                    <li key={idx}>
                      <button 
                        className="mega-dropdown-item border-0 bg-transparent w-100 text-start" 
                        onClick={() => handleCategoryClick(cat)}
                      >
                        {cat.categoryName || cat.name}
                      </button>
                    </li>
                  ))
                )}
              </ul>
            </li>

            {/* Danh m·ª•c nhanh */}
            {!loadingCategories && categories.slice(0, 6).map((cat, idx) => (
              <li className="nav-item" key={`nav-quick-${idx}`}>
                <button 
                  className="nav-link nav-link-custom py-3 px-3" 
                  onClick={() => handleCategoryClick(cat)}
                >
                  {cat.categoryName || cat.name}
                </button>
              </li>
            ))}

            <li className="nav-item ms-auto">
              <Link to="/product" className="nav-link nav-link-highlight py-3">
                <i className="fa fa-fire me-1"></i> KHUY·∫æN M√ÉI HOT
              </Link>
            </li>
          </ul>
        </div>
      </nav>

      <style>{`
        /* --- T·ªïng th·ªÉ --- */
        .section-header { font-family: 'Inter', sans-serif; border-bottom: 1px solid #eee; }
        .smaller { font-size: 11px; color: #888; }
        .pointer-cursor { cursor: pointer; }

        /* --- M√†u s·∫Øc & Th∆∞∆°ng hi·ªáu --- */
        .logo-brand { font-weight: 800; color: #333; letter-spacing: -1px; }
        .logo-brand .accent { color: #ff6a00; }
        .top-banner-ads { background: linear-gradient(90deg, #ff6a00, #ee0979); color: white; }

        /* --- Thanh t√¨m ki·∫øm Modern --- */
        .search-modern-wrap {
          display: flex;
          background: #f4f6f8;
          border-radius: 50px;
          padding: 4px;
          transition: 0.3s;
        }
        .search-modern-wrap:focus-within {
          background: #fff;
          box-shadow: 0 0 0 3px rgba(255, 106, 0, 0.15) !important;
        }
        .search-modern-wrap input { background: transparent; font-size: 14px; }
        .btn-orange-rounded {
          background: #ff6a00;
          color: white;
          border-radius: 50px;
          width: 45px;
          height: 38px;
          border: none;
          transition: 0.3s;
        }
        .btn-orange-rounded:hover { background: #e65c00; transform: scale(0.95); }

        /* --- Icon Boxes --- */
        .icon-box-light {
          width: 40px; height: 40px;
          background: #f8f9fa;
          border-radius: 12px;
          display: flex; align-items: center; justify-content: center;
          color: #555; font-size: 18px;
        }
        .icon-box-orange {
          width: 40px; height: 40px;
          background: #fff5ee;
          border-radius: 12px;
          display: flex; align-items: center; justify-content: center;
          color: #ff6a00; font-size: 18px;
        }
        .cart-badge-modern {
          position: absolute;
          top: -5px; right: -5px;
          background: #ff4757;
          color: white;
          font-size: 10px;
          padding: 2px 6px;
          border-radius: 50px;
          font-weight: bold;
        }

        /* --- Navigation --- */
        .nav-link-custom {
          font-size: 13px; font-weight: 600; color: #444 !important;
          text-transform: uppercase; transition: 0.2s;
        }
        .nav-link-custom:hover { color: #ff6a00 !important; }
        .all-cat-link { font-weight: 800; color: #333 !important; font-size: 13px; }
        .nav-link-highlight { color: #ff6a00 !important; font-weight: 700; font-size: 13px; }

        /* --- Dropdowns --- */
        .user-mega-menu {
          position: absolute; top: 110%; right: 0;
          min-width: 220px; background: white;
          display: none; z-index: 1000; padding: 10px 0;
        }
        .user-dropdown-container:hover .user-mega-menu { display: block; animation: slideUp 0.3s ease; }
        .user-menu-item {
          padding: 10px 20px; display: block;
          text-decoration: none; color: #444; font-size: 14px; transition: 0.2s;
        }
        .user-menu-item:hover { background: #fff5ee; color: #ff6a00; }

        .mega-dropdown-menu {
          position: absolute; top: 100%; left: 0;
          display: none; background: white; z-index: 1000;
        }
        .dropdown-hover-container:hover .mega-dropdown-menu { display: block; animation: slideUp 0.3s ease; }

        @keyframes slideUp {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </header>
  );
};

export default Header;
