import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import { 
  Eye, CheckCircle, Truck, XCircle, Package, 
  Calendar, User, MapPin, CreditCard, RefreshCw 
} from "lucide-react";
import Swal from "sweetalert2";

const OrderListAD = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0, totalElements: 0 });

  // 1. Load danh sách đơn hàng
  const fetchOrders = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
      const res = await api.get(`/admin/orders?pageNumber=${page}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(res.data.content || []);
      setPageConfig({
        pageNumber: res.data.pageNumber,
        totalPages: res.data.totalPages,
        totalElements: res.data.totalElements
      });
    } catch (err) {
      console.error("❌ Lỗi:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { fetchOrders(0); }, [fetchOrders]);

  // 2. Xem chi tiết (Gọi API Debug của mày)
  const handleViewOrder = async (orderId) => {
    try {
      const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
      const res = await api.get(`/admin/debug/orders/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSelectedOrder(res.data);
      setShowModal(true);
    } catch (err) {
      Swal.fire("Lỗi", "Không thể lấy chi tiết đơn hàng", "error");
    }
  };

  // 3. Cập nhật trạng thái (Dùng PUT API trong Controller của mày)
  const updateStatus = async (orderId, status) => {
    const result = await Swal.fire({
      title: `Chuyển đơn hàng sang [${status}]?`,
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Xác nhận'
    });

    if (result.isConfirmed) {
      try {
        const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
        // Kiểm tra lại endpoint: /api/admin/orders/{orderId}/status?status=...
        await api.put(`/admin/orders/${orderId}/status?status=${status}`, {}, {
          headers: { Authorization: `Bearer ${token}` }
        });
        Swal.fire("Thành công", "Đã cập nhật trạng thái đơn hàng", "success");
        fetchOrders(pageConfig.pageNumber);
        setShowModal(false);
      } catch (err) {
        Swal.fire("Lỗi", "Cập nhật thất bại!", "error");
      }
    }
  };

  const getStatusInfo = (status) => {
    const s = status?.toUpperCase();
    if (s === 'DELIVERED') return { color: 'success', text: 'Hoàn tất' };
    if (s === 'SHIPPED') return { color: 'info', text: 'Đang giao' };
    if (s === 'CANCELLED') return { color: 'danger', text: 'Đã hủy' };
    return { color: 'warning', text: 'Chờ xử lý' };
  };

  return (
    <div className="container-fluid p-0 text-start">
      {/* Thống kê nhanh */}
      <div className="row g-3 mb-4">
        <StatCard title="Tổng đơn hàng" value={pageConfig.totalElements} color="primary" icon={<Package />} />
        <StatCard title="Doanh thu" value={orders.reduce((sum, o) => sum + (o.totalAmount || 0), 0).toLocaleString() + "đ"} color="success" icon={<CreditCard />} />
      </div>

      <div className="card border-0 shadow-sm rounded-4">
        <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
          <h5 className="mb-0 fw-bold">QUẢN LÝ ĐƠN HÀNG</h5>
          <button className="btn btn-sm btn-light border" onClick={() => fetchOrders(0)}>
            <RefreshCw size={14} className="me-1" /> Làm mới
          </button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="bg-light small text-uppercase fw-bold text-muted">
              <tr>
                <th className="ps-4">Mã đơn</th>
                <th>Khách hàng</th>
                <th>Ngày đặt</th>
                <th>Tổng tiền</th>
                <th>Trạng thái</th>
                <th className="text-center">Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="6" className="text-center py-5"><div className="spinner-border spinner-border-sm text-primary"></div></td></tr>
              ) : orders.map((o) => {
                const s = getStatusInfo(o.orderStatus);
                return (
                  <tr key={o.orderId}>
                    <td className="ps-4 fw-bold">#{o.orderId}</td>
                    <td><div className="small fw-semibold">{o.email}</div></td>
                    <td className="small text-muted">{o.orderDate}</td>
                    <td className="fw-bold text-danger">{o.totalAmount?.toLocaleString()}đ</td>
                    <td><span className={`badge rounded-pill bg-${s.color} bg-opacity-10 text-${s.color} border border-${s.color}`}>{s.text}</span></td>
                    <td className="text-center">
                      <button className="btn btn-sm btn-primary rounded-pill px-3" onClick={() => handleViewOrder(o.orderId)}>Chi tiết</button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* MODAL CHI TIẾT & ĐIỀU PHỐI */}
      {showModal && selectedOrder && (
        <div className="modal fade show d-block" style={{ background: 'rgba(0,0,0,0.5)', backdropFilter: 'blur(4px)' }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content border-0 shadow-lg rounded-4 text-start">
              <div className="modal-header border-0">
                <h5 className="fw-bold mb-0">Chi tiết đơn hàng #{selectedOrder.orderId}</h5>
                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
              </div>
              <div className="modal-body p-4">
                <div className="row g-3 mb-4">
                  <InfoItem icon={<User />} label="Khách hàng" value={selectedOrder.email} />
                  <InfoItem icon={<Calendar />} label="Ngày đặt" value={selectedOrder.orderDate} />
                  <InfoItem icon={<MapPin />} label="Địa chỉ" value={selectedOrder.shippingAddress || "Chưa có địa chỉ"} />
                </div>

                <div className="table-responsive border rounded-4 mb-4">
                  <table className="table mb-0 small">
                    <thead className="table-light">
                      <tr><th>Sản phẩm</th><th className="text-center">SL</th><th className="text-end">Đơn giá</th></tr>
                    </thead>
                    <tbody>
                      {selectedOrder.orderItems?.map((item, i) => (
                        <tr key={i}>
                          <td>{item.product?.productName}</td>
                          <td className="text-center">x{item.quantity}</td>
                          <td className="text-end fw-bold">{item.orderedProductPrice?.toLocaleString()}đ</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                {/* NÚT ĐIỀU PHỐI TRẠNG THÁI */}
                <div className="p-3 bg-light rounded-4">
                  <label className="small fw-bold text-muted text-uppercase mb-3 d-block">Cập nhật trạng thái xử lý</label>
                  <div className="d-flex flex-wrap gap-2">
                    <StatusBtn icon={<CheckCircle />} color="btn-success" label="Xác nhận / Hoàn tất" onClick={() => updateStatus(selectedOrder.orderId, 'DELIVERED')} />
                    <StatusBtn icon={<Truck />} color="btn-info" label="Giao hàng" onClick={() => updateStatus(selectedOrder.orderId, 'SHIPPED')} />
                    <StatusBtn icon={<XCircle />} color="btn-danger" label="Hủy đơn" onClick={() => updateStatus(selectedOrder.orderId, 'CANCELLED')} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Sub-components
const StatCard = ({ title, value, color, icon }) => (
  <div className="col-md-6 col-lg-3">
    <div className="card border-0 shadow-sm rounded-4">
      <div className="card-body d-flex align-items-center p-3">
        <div className={`bg-${color} bg-opacity-10 text-${color} rounded-4 p-3 me-3`}>{icon}</div>
        <div><h5 className="fw-bold mb-0">{value}</h5><small className="text-muted fw-bold small text-uppercase">{title}</small></div>
      </div>
    </div>
  </div>
);

const InfoItem = ({ icon, label, value }) => (
  <div className="col-md-4">
    <div className="d-flex align-items-center gap-2 mb-1 text-muted small fw-bold">{icon} <span>{label}</span></div>
    <div className="small fw-semibold">{value}</div>
  </div>
);

const StatusBtn = ({ icon, color, label, onClick }) => (
  <button className={`btn ${color} btn-sm rounded-pill px-3 d-flex align-items-center gap-2`} onClick={onClick}>
    {icon} <span>{label}</span>
  </button>
);

export default OrderListAD;
