import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  // State lưu trữ lỗi chi tiết từ Backend cho từng ô Input
  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
    password: "",
    confirmPassword: "",
    // Dữ liệu địa chỉ mặc định để đồng bộ với bản Mobile
    street: "Default Street",
    buildingName: "Building A",
    city: "Hanoi",
    state: "HN",
    country: "VN",
    pincode: "100000",
  });

  // Xử lý khi người dùng nhập liệu
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    // Nếu ô đó đang có lỗi, người dùng gõ lại thì xóa thông báo lỗi đỏ đi
    if (backendErrors[e.target.name]) {
      setBackendErrors({ ...backendErrors, [e.target.name]: "" });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setBackendErrors({}); // Reset lỗi cũ trước khi gửi

    // Kiểm tra khớp mật khẩu tại Client
    if (form.password !== form.confirmPassword) {
      setBackendErrors({ confirmPassword: "Mật khẩu nhập lại không khớp!" });
      Swal.fire({
        icon: "error",
        title: "Lỗi mật khẩu",
        text: "Mật khẩu xác nhận phải giống với mật khẩu đã nhập.",
        confirmButtonColor: "#ff6a00"
      });
      return;
    }

    setLoading(true);

    try {
      // Chuẩn hóa dữ liệu gửi lên API (Mapping sang CamelCase của Backend)
      const payload = {
        firstName: form.first_name,
        lastName: form.last_name,
        mobileNumber: form.mobile_number,
        email: form.email,
        password: form.password,
        address: {
          street: form.street,
          buildingName: form.buildingName,
          city: form.city,
          state: form.state,
          country: form.country,
          pincode: form.pincode,
        },
        role: [{ roleId: 102, roleName: "USER" }], // Đồng bộ cấu trúc Role với Mobile
      };

      await authService.register(payload);

      // Thông báo thành công giữa màn hình
      Swal.fire({
        title: "Đăng ký thành công!",
        text: "Chào mừng bạn gia nhập cộng đồng Guardian.",
        icon: "success",
        confirmButtonColor: "#ff6a00",
        confirmButtonText: "Đăng nhập ngay"
      }).then((result) => {
        if (result.isConfirmed) {
          navigate("/login");
        }
      });

    } catch (error) {
      const errorData = error.response?.data;
      console.error("❌ Lỗi API:", errorData);

      if (typeof errorData === "object") {
        // Gắn lỗi từ Backend vào các ô Input tương ứng
        setBackendErrors({
          first_name: errorData.firstName,
          last_name: errorData.lastName,
          email: errorData.email,
          mobile_number: errorData.mobileNumber,
          password: errorData.password
        });

        // Thông báo lỗi tổng quát
        Swal.fire({
          icon: "warning",
          title: "Dữ liệu chưa đúng",
          text: "Vui lòng kiểm tra lại các thông tin màu đỏ bên dưới.",
          confirmButtonColor: "#ff6a00"
        });
      } else {
        // Lỗi không xác định (Server sập, trùng email, v.v.)
        Swal.fire({
          icon: "error",
          title: "Đăng ký thất bại",
          text: errorData || "Có lỗi xảy ra, vui lòng thử lại sau.",
          confirmButtonColor: "#d33"
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="section-content padding-y bg-light" style={{ minHeight: "90vh" }}>
      <div className="card mx-auto shadow-sm" style={{ maxWidth: "520px", marginTop: "40px", borderRadius: "15px" }}>
        <article className="card-body p-4">
          <header className="mb-4 text-center">
            <h4 className="card-title fw-bold" style={{ color: "#ff6a00" }}>TẠO TÀI KHOẢN</h4>
            <p className="text-muted small">Thông tin cần từ 5-30 ký tự theo yêu cầu hệ thống</p>
          </header>

          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Họ</label>
                <input
                  name="first_name"
                  type="text"
                  className={`form-control ${backendErrors.first_name ? 'is-invalid' : ''}`}
                  placeholder="VD: Nguyễn Văn"
                  value={form.first_name}
                  onChange={handleChange}
                  required
                />
                <div className="invalid-feedback text-start">{backendErrors.first_name}</div>
              </div>
              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Tên</label>
                <input
                  name="last_name"
                  type="text"
                  className={`form-control ${backendErrors.last_name ? 'is-invalid' : ''}`}
                  placeholder="VD: Anh"
                  value={form.last_name}
                  onChange={handleChange}
                  required
                />
                <div className="invalid-feedback text-start">{backendErrors.last_name}</div>
              </div>
            </div>

            <div className="mb-3">
              <label className="form-label small fw-bold">Email</label>
              <input
                name="email"
                type="email"
                className={`form-control ${backendErrors.email ? 'is-invalid' : ''}`}
                placeholder="example@gmail.com"
                value={form.email}
                onChange={handleChange}
                required
              />
              <div className="invalid-feedback text-start">{backendErrors.email}</div>
            </div>

            <div className="mb-3">
              <label className="form-label small fw-bold">Số điện thoại</label>
              <input
                name="mobile_number"
                type="text"
                className={`form-control ${backendErrors.mobile_number ? 'is-invalid' : ''}`}
                placeholder="Nhập số điện thoại"
                value={form.mobile_number}
                onChange={handleChange}
                required
              />
              <div className="invalid-feedback text-start">{backendErrors.mobile_number}</div>
            </div>

            <div className="row mb-4">
              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Mật khẩu</label>
                <input
                  name="password"
                  type="password"
                  className={`form-control ${backendErrors.password ? 'is-invalid' : ''}`}
                  placeholder="******"
                  value={form.password}
                  onChange={handleChange}
                  required
                />
                <div className="invalid-feedback text-start">{backendErrors.password}</div>
              </div>
              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Nhập lại mật khẩu</label>
                <input
                  name="confirmPassword"
                  type="password"
                  className={`form-control ${backendErrors.confirmPassword ? 'is-invalid' : ''}`}
                  placeholder="******"
                  value={form.confirmPassword}
                  onChange={handleChange}
                  required
                />
                <div className="invalid-feedback text-start">{backendErrors.confirmPassword}</div>
              </div>
            </div>

            <button 
              type="submit" 
              className="btn w-100 fw-bold py-2 shadow-sm text-white"
              style={{ backgroundColor: "#ff6a00", border: "none" }}
              disabled={loading}
            >
              {loading ? (
                <span className="spinner-border spinner-border-sm me-2"></span>
              ) : "TIẾP TỤC ĐĂNG KÝ"}
            </button>
          </form>
        </article>
      </div>

      <p className="text-center mt-4 text-muted">
        Bạn đã có tài khoản? <Link to="/login" className="fw-bold text-decoration-none" style={{ color: "#ff6a00" }}>Đăng nhập</Link>
      </p>
    </section>
  );
};

export default Register;
