import React, { useState, useEffect } from 'react';
import bannerService from '../../services/bannerService';

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Banner = () => {
  const [banners, setBanners] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [imageLoaded, setImageLoaded] = useState({});

  useEffect(() => {
    fetchBanners();
  }, []);

  const fetchBanners = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('üîÑ B·∫Øt ƒë·∫ßu fetch banners...');

      const data = await bannerService.getActiveBanners();
      console.log('üì¢ D·ªØ li·ªáu banners nh·∫≠n ƒë∆∞·ª£c:', data);

      const bannersArray = Array.isArray(data) ? data : [];
      console.log('‚úÖ Banners array:', bannersArray);

      // ‚úÖ L·ªåC CH·ªà L·∫§Y BANNER C√ì ID 1, 2, 3
      const filteredBanners = bannersArray.filter(banner => {
        const bannerId = banner.bannerId || banner.id;
        return bannerId === 1 || bannerId === 2 || bannerId === 3;
      });

      console.log('üéØ Banners sau khi l·ªçc (id 1,2,3):', filteredBanners);

      setBanners(filteredBanners);

    } catch (err) {
      console.error('‚ùå L·ªói khi l·∫•y banner:', err);
      setError('Kh√¥ng th·ªÉ t·∫£i banner: ' + (err.message || 'L·ªói kh√¥ng x√°c ƒë·ªãnh'));
    } finally {
      setLoading(false);
    }
  };

  // T·ª± ƒë·ªông chuy·ªÉn slide - CH·ªà CH·∫†Y KHI C√ì BANNER V√Ä NHI·ªÄU H∆†N 1
  useEffect(() => {
    if (Array.isArray(banners) && banners.length > 1) {
      const timer = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % banners.length);
      }, 5000);

      return () => clearInterval(timer);
    }
  }, [banners.length]);

  const nextSlide = () => {
    if (Array.isArray(banners) && banners.length > 1) {
      setCurrentSlide((prev) => (prev + 1) % banners.length);
    }
  };

  const prevSlide = () => {
    if (Array.isArray(banners) && banners.length > 1) {
      setCurrentSlide((prev) => (prev - 1 + banners.length) % banners.length);
    }
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const getImageUrl = (banner) => {
    if (!banner) return 'https://images.unsplash.com/photo-1607082350899-7e105aa886ae?w=1920&h=600&fit=crop&auto=format';

    const imageName = banner.imageUrl || banner.image_url || banner.imageName;

    if (!imageName) {
      return 'https://images.unsplash.com/photo-1607082350899-7e105aa886ae?w=1920&h=600&fit=crop&auto=format';
    }

    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  const handleImageLoad = (bannerId) => {
    setImageLoaded(prev => ({ ...prev, [bannerId]: true }));
  };

  const handleImageError = (banner, e) => {
    console.error('‚ùå L·ªói t·∫£i ·∫£nh banner:', banner);
    e.target.src = 'https://images.unsplash.com/photo-1607082350899-7e105aa886ae?w=1920&h=600&fit=crop&auto=format';
  };

  // Loading state
  if (loading) {
    return (
      <section className="banner-section mb-5">
        <div className="container-fluid px-0">
          <div 
            className="banner-placeholder d-flex align-items-center justify-content-center"
            style={{
              height: '60vh',
              minHeight: '300px',
              maxHeight: '600px',
              background: 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%)'
            }}
          >
            <div className="text-center">
              <div className="spinner-border text-primary mb-3" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="text-muted mb-0">ƒêang t·∫£i banner...</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // Error state
  if (error) {
    return (
      <section className="banner-section mb-5">
        <div className="container-fluid px-0">
          <div 
            className="banner-error d-flex align-items-center justify-content-center"
            style={{
              height: '300px',
              background: 'linear-gradient(135deg, #fff5f5 0%, #fed7d7 100%)'
            }}
          >
            <div className="text-center">
              <div className="text-danger mb-2">
                <i className="fas fa-exclamation-triangle fa-2x"></i>
              </div>
              <p className="text-danger mb-2">{error}</p>
              <button 
                onClick={fetchBanners}
                className="btn btn-sm btn-outline-danger"
              >
                <i className="fas fa-redo me-1"></i>
                Th·ª≠ l·∫°i
              </button>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // Empty state - HI·ªÇN TH·ªä KHI KH√îNG C√ì BANNER ID 1,2,3
  if (!Array.isArray(banners) || banners.length === 0) {
    return (
      <section className="banner-section mb-5">
        <div className="container-fluid px-0">
          <div 
            className="banner-empty d-flex align-items-center justify-content-center"
            style={{
              height: '300px',
              background: 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)'
            }}
          >
            <div className="text-center text-muted">
              <i className="fas fa-image fa-2x mb-2"></i>
              <p className="mb-0">Kh√¥ng t√¨m th·∫•y banner ID 1, 2, 3</p>
              <small className="text-muted">
                Ch·ªâ hi·ªÉn th·ªã banner c√≥ ID 1, 2, 3
              </small>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // Main banner v·ªõi ·∫£nh ch·∫•t l∆∞·ª£ng cao - CH·ªà HI·ªÇN TH·ªä BANNER ID 1,2,3
  return (
    <section className="banner-section mb-5">
      <div className="container-fluid px-0">
        <div className="banner-slider position-relative overflow-hidden">

          {/* Banner Slides - Ch·ªâ hi·ªÉn th·ªã banner id 1,2,3 */}
          <div 
            className="banner-track d-flex" 
            style={{ 
              transform: `translateX(-${currentSlide * 100}%)`,
              transition: 'transform 0.5s ease-in-out'
            }}
          >
            {banners.map((banner, index) => {
              const bannerId = banner.bannerId || banner.id;
              return (
                <div
                  key={bannerId}
                  className="banner-slide flex-shrink-0 w-100 position-relative"
                >
                  {/* Hi·ªÉn th·ªã ID banner */}
                  {/* <div 
                    className="position-absolute top-0 start-0 m-3 badge bg-primary"
                    style={{ zIndex: 10 }}
                  >
                    Banner ID: {bannerId}
                  </div> */}

                  {/* Loading indicator cho t·ª´ng ·∫£nh */}
                  {!imageLoaded[bannerId] && (
                    <div 
                      className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center justify-content-center"
                      style={{
                        background: 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%)',
                        zIndex: 1
                      }}
                    >
                      <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading image...</span>
                      </div>
                    </div>
                  )}

                  <img
                    src={getImageUrl(banner)}
                    alt={banner.title || 'Banner Image'}
                    className="img-fluid w-100"
                    style={{ 
                      height: '60vh',
                      minHeight: '500px',
                      maxHeight: '600px',
                      objectFit: 'cover',
                      objectPosition: 'center',
                      imageRendering: 'auto',
                      backfaceVisibility: 'hidden',
                      transform: 'translate3d(0,0,0)'
                    }}
                    loading="eager"
                    onLoad={() => handleImageLoad(bannerId)}
                    onError={(e) => handleImageError(banner, e)}
                  />
                </div>
              );
            })}
          </div>

          {/* Navigation Arrows - Ch·ªâ hi·ªán khi c√≥ nhi·ªÅu h∆°n 1 banner */}
          {banners.length > 1 && (
            <>
              <button
                className="banner-arrow banner-arrow-prev position-absolute top-50 start-0 translate-middle-y btn btn-light rounded-circle shadow border-0"
                onClick={prevSlide}
                style={{ 
                  left: '20px', 
                  zIndex: 20,
                  width: '50px',
                  height: '50px',
                  fontSize: '1.2rem',
                  opacity: 0.9,
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => e.target.style.opacity = '1'}
                onMouseLeave={(e) => e.target.style.opacity = '0.9'}
              >
                <i className="fas fa-chevron-left"></i>
              </button>

              <button
                className="banner-arrow banner-arrow-next position-absolute top-50 end-0 translate-middle-y btn btn-light rounded-circle shadow border-0"
                onClick={nextSlide}
                style={{ 
                  right: '20px', 
                  zIndex: 20,
                  width: '50px',
                  height: '50px',
                  fontSize: '1.2rem',
                  opacity: 0.9,
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => e.target.style.opacity = '1'}
                onMouseLeave={(e) => e.target.style.opacity = '0.9'}
              >
                <i className="fas fa-chevron-right"></i>
              </button>
            </>
          )}

          {/* Dots Indicator - Ch·ªâ hi·ªán khi c√≥ nhi·ªÅu h∆°n 1 banner */}
          {banners.length > 1 && (
            <div 
              className="banner-dots position-absolute bottom-0 start-50 translate-middle-x mb-4"
              style={{ zIndex: 20 }}
            >
              {banners.map((banner, index) => {
                const bannerId = banner.bannerId || banner.id;
                return (
                  <button
                    key={bannerId}
                    className={`banner-dot btn btn-sm rounded-circle mx-1 border-0 ${
                      index === currentSlide ? 'btn-light' : 'btn-outline-light'
                    }`}
                    onClick={() => goToSlide(index)}
                    style={{ 
                      width: '14px', 
                      height: '14px',
                      transition: 'all 0.3s ease',
                      opacity: index === currentSlide ? 1 : 0.7
                    }}
                    onMouseEnter={(e) => e.target.style.opacity = '1'}
                    onMouseLeave={(e) => e.target.style.opacity = index === currentSlide ? '1' : '0.7'}
                  />
                );
              })}
            </div>
          )}

          {/* Hi·ªÉn th·ªã th√¥ng tin s·ªë l∆∞·ª£ng banner ƒëang hi·ªÉn th·ªã */}
          {/* <div 
            className="position-absolute top-0 end-0 m-3 badge bg-info"
            style={{ zIndex: 10 }}
          >
            ƒêang hi·ªÉn th·ªã: {banners.length}/3 banner (ID: 1,2,3)
          </div> */}

        </div>
      </div>
    </section>
  );
};

export default Banner;
