import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";

import "bootstrap/dist/css/bootstrap.min.css";
import "../assets/fonts/fontawesome/css/all.min.css";

import logo from "../assets/images/logo.png";
import flagVN from "../assets/images/icons/flags/VN.webp";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ‚úÖ L·∫•y th√¥ng tin user t·ª´ localStorage + ƒë·ªìng b·ªô DB
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const storedToken = localStorage.getItem("token");

    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        if ((parsedUser.userId || parsedUser.user_id) && storedToken) {
          const userId = parsedUser.userId || parsedUser.user_id;
          setLoading(true);

          // üß© G·ªçi API ƒë·ªÉ l·∫•y user t·ª´ DB
          userService
            .getUserById(userId)
            .then(async (userData) => {
              if (userData) {
                console.log("‚úÖ ƒê√£ c√≥ user trong DB:", userData);
                setUserInfo(userData);
              } else {
                // üß© N·∫øu ch∆∞a c√≥ user trong DB, t·ª± ƒë·ªông t·∫°o m·ªõi
                console.log("‚öôÔ∏è User ch∆∞a t·ªìn t·∫°i trong DB ‚Üí t·∫°o m·ªõi...");
                const newUser = {
                  id: userId,
                  first_name: parsedUser.first_name || "",
                  last_name: parsedUser.last_name || "",
                  email: parsedUser.email,
                  mobile_number: parsedUser.mobile_number || "",
                };
                const created = await userService.createOrUpdateUser(newUser);
                setUserInfo(created);
              }
            })
            .catch(async (err) => {
              console.error("‚ùå L·ªói l·∫•y th√¥ng tin user:", err);
              // üß© Fallback: t·∫°o m·ªõi n·∫øu l·ªói do user ch∆∞a t·ªìn t·∫°i
              if (parsedUser.email) {
                const newUser = {
                  id: parsedUser.userId || parsedUser.user_id,
                  first_name: parsedUser.first_name || "",
                  last_name: parsedUser.last_name || "",
                  email: parsedUser.email,
                };
                const created = await userService.createOrUpdateUser(newUser);
                setUserInfo(created);
              }
            })
            .finally(() => setLoading(false));
        }
      } catch (error) {
        console.error("‚ùå L·ªói parse user:", error);
        setLoading(false);
      }
    }
  }, []);

  // ‚úÖ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    navigate("/login");
  };

  // ‚úÖ Chuy·ªÉn ƒë·∫øn trang profile
  const handleProfileClick = () => {
    navigate(user ? "/profile" : "/login");
  };

  // ‚úÖ Hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng
  const renderUserName = () => {
    if (!user) return "My profile";
    if (loading)
      return (
        <div className="d-flex align-items-center">
          <div className="spinner-border spinner-border-sm text-primary me-2"></div>
          <span>Loading...</span>
        </div>
      );

    const name = `${userInfo?.first_name || ""} ${userInfo?.last_name || ""}`.trim();
    if (name) return name;
    if (user.email) return user.email;
    return "User";
  };

  // ‚úÖ Hi·ªÉn th·ªã l·ªùi ch√†o
  const renderGreeting = () => {
    if (!user) return null;
    if (userInfo?.first_name)
      return `üëã Hi, ${userInfo.first_name}`;
    if (user.email)
      return `üëã Hi, ${user.email.split("@")[0]}`;
    return "üëã Hi, User";
  };

  return (
    <header className="section-header">
      {/* ==== THANH TR√äN ==== */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav mr-auto">
              <li>
                {user ? (
                  <span className="nav-link">
                    {renderGreeting()} |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ==== THANH CH√çNH ==== */}
      <div className="container">
        <section className="header-main border-bottom">
          <div className="row row-sm">
            <div className="col-6 col-sm col-md col-lg flex-grow-0">
              <Link to="/" className="brand-wrap">
                <img src={logo} alt="Logo" height={56} />
              </Link>
            </div>

            <div className="col-lg-6">
              <form className="search-header">
                <input type="text" className="form-control" placeholder="Search" />
              </form>
            </div>

            <div className="col-xl-4">
              <div className="widgets-wrap float-md-right">
                <div className="widget-header mr-3">
                  <a href="#" className="widget-view" onClick={(e) => { e.preventDefault(); handleProfileClick(); }}>
                    <div className="icon-area">
                      <i className="fa fa-user"></i>
                    </div>
                    <small className="text">{renderUserName()}</small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        <nav className="navbar navbar-main navbar-expand pl-0">
          <ul className="navbar-nav flex-wrap">
            <li className="nav-item"><Link to="/" className="nav-link">Home</Link></li>
            <li className="nav-item"><Link to="/product" className="nav-link">Products</Link></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
