import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const AdminLogin = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await authService.login({ email, password });
      const token = res.data.token || res.data.jwt || res.data["jwt-token"];
      
      // Ki·ªÉm tra Role Admin t·ª´ Token (Gi·ªëng logic m√†y vi·∫øt)
      // ... (Ph·∫ßn decode token tao ƒë√£ h∆∞·ªõng d·∫´n ·ªü tr√™n) ...

      localStorage.setItem("admin-token", token);
      localStorage.setItem("admin-role", "ADMIN");
      
      Swal.fire({ icon: "success", title: "ƒê√£ x√°c th·ª±c Admin üëë", timer: 1000, showConfirmButton: false });
      navigate("/admin");
    } catch (err) {
      Swal.fire("L·ªói!", "T√†i kho·∫£n kh√¥ng c√≥ quy·ªÅn truy c·∫≠p!", "error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      height: "100vh",
      width: "100vw",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      background: "linear-gradient(135deg, #1a1a2e 0%, #16213e 100%)", // M√†u n·ªÅn t·ªëi chuy√™n cho Admin
      position: "fixed",
      top: 0,
      left: 0,
      zIndex: 9999
    }}>
      <div className="card border-0 shadow-lg" style={{ width: "400px", borderRadius: "15px", overflow: "hidden" }}>
        <div className="bg-primary p-4 text-center text-white">
          <h3 className="m-0 fw-bold">ADMIN PORTAL</h3>
          <small className="opacity-75">H·ªá th·ªëng qu·∫£n l√Ω n·ªôi b·ªô</small>
        </div>
        <div className="card-body p-4">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label fw-bold small">T√†i kho·∫£n Admin</label>
              <input type="email" className="form-control" required onChange={e => setEmail(e.target.value)} placeholder="admin@example.com" />
            </div>
            <div className="mb-4">
              <label className="form-label fw-bold small">M·∫≠t kh·∫©u</label>
              <input type="password" className="form-control" required onChange={e => setPassword(e.target.value)} placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢" />
            </div>
            <button className="btn btn-primary w-100 py-2 fw-bold shadow-sm" disabled={loading}>
              {loading ? "ƒêANG KI·ªÇM TRA..." : "V√ÄO H·ªÜ TH·ªêNG"}
            </button>
          </form>
        </div>
        <div className="card-footer bg-light text-center py-3 border-0">
          <button onClick={() => navigate("/")} className="btn btn-link btn-sm text-decoration-none text-muted">
             ‚Üê Quay v·ªÅ trang ch·ªß User
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
