// src/components/Header.js
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../assets/fonts/fontawesome/css/all.min.css";

import logo from "../assets/images/logo.png";
import flagVN from "../assets/images/icons/flags/VN.png";

const Header = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  // ‚úÖ Load user khi kh·ªüi ƒë·ªông
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) setUser(JSON.parse(storedUser));
  }, []);

  // ‚úÖ T·ª± ƒë·ªông c·∫≠p nh·∫≠t Header khi user thay ƒë·ªïi trong localStorage
  useEffect(() => {
    const handleStorageChange = () => {
      const updatedUser = localStorage.getItem("user");
      if (updatedUser) {
        setUser(JSON.parse(updatedUser));
      } else {
        setUser(null);
      }
    };

    // L·∫Øng nghe s·ª± ki·ªán localStorage thay ƒë·ªïi
    window.addEventListener("storage", handleStorageChange);

    // Cleanup khi unmount
    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <header className="bg-light shadow-sm py-3 px-4 d-flex justify-content-between align-items-center">
      {/* Logo */}
      <div className="d-flex align-items-center gap-3">
        <Link to="/">
          <img src={logo} alt="Logo" width="120" />
        </Link>
        <img src={flagVN} alt="VN" width="30" />
      </div>

      {/* T√™n ng∆∞·ªùi d√πng */}
      <div className="d-flex align-items-center gap-3">
        {user ? (
          <>
            <span className="fw-semibold text-dark">
              üëã Xin ch√†o,{" "}
              <span className="text-primary">
                {user.first_name} {user.last_name}
              </span>
            </span>

            <button
              onClick={() => navigate("/profile")}
              className="btn btn-outline-primary btn-sm"
            >
              <i className="fa fa-user me-1"></i> H·ªì s∆°
            </button>

            <button
              onClick={handleLogout}
              className="btn btn-outline-danger btn-sm"
            >
              <i className="fa fa-sign-out-alt me-1"></i> ƒêƒÉng xu·∫•t
            </button>
          </>
        ) : (
          <Link to="/login" className="btn btn-primary btn-sm">
            <i className="fa fa-sign-in-alt me-1"></i> ƒêƒÉng nh·∫≠p
          </Link>
        )}
      </div>
    </header>
  );
};

export default Header;
