import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import bannerService from "../services/bannerService";
import cartService from "../services/cartService";

// âœ… Import CSS, Font, Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";

// âœ… Import hÃ¬nh áº£nh
import flagVN from "../assets/images/icons/flags/VN.webp";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [logo, setLogo] = useState(null);
  const [cartCount, setCartCount] = useState(0);
  const navigate = useNavigate();
  const location = useLocation();

  // âœ… HÃ m kiá»ƒm tra active menu
  const isActive = (path) => {
    return location.pathname === path;
  };

  // âœ… HÃ m kiá»ƒm tra active cho category
  const isCategoryActive = () => {
    return location.pathname === '/product' && location.state?.selectedCategory;
  };

  // âœ… Láº¥y user tá»« localStorage vÃ  fetch thÃ´ng tin má»›i nháº¥t
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        console.log("ðŸ‘¤ User from localStorage:", parsedUser);
        setUser(parsedUser);

        if (parsedUser.userId && typeof parsedUser.userId === 'number') {
          setLoading(true);

          userService.getUserById(parsedUser.userId)
            .then((userData) => {
              console.log("âœ… User data from API:", userData);

              const updatedUser = {
                ...parsedUser,
                firstName: userData.firstName || userData.first_name || "",
                lastName: userData.lastName || userData.last_name || "",
                email: userData.email,
                mobileNumber: userData.mobileNumber || userData.mobile_number || ""
              };

              setUserInfo({
                firstName: updatedUser.firstName,
                lastName: updatedUser.lastName,
                email: updatedUser.email
              });

              localStorage.setItem("user", JSON.stringify(updatedUser));
              console.log("ðŸ’¾ Updated user in localStorage:", updatedUser);
            })
            .catch((err) => {
              console.error("âŒ Lá»—i láº¥y thÃ´ng tin user:", err);
              if (parsedUser.firstName || parsedUser.first_name) {
                setUserInfo({
                  firstName: parsedUser.firstName || parsedUser.first_name || "",
                  lastName: parsedUser.lastName || parsedUser.last_name || "",
                  email: parsedUser.email
                });
              }
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          if (parsedUser.firstName || parsedUser.first_name) {
            setUserInfo({
              firstName: parsedUser.firstName || parsedUser.first_name || "",
              lastName: parsedUser.lastName || parsedUser.last_name || "",
              email: parsedUser.email
            });
          }
        }
      } catch (error) {
        console.error("âŒ Parse user lá»—i:", error);
        setLoading(false);
      }
    }
  }, []);

  // âœ… Láº¥y danh sÃ¡ch categories tá»« API
  useEffect(() => {
    fetchCategories();
  }, []);

  // âœ… Láº¥y logo tá»« bannerService
  useEffect(() => {
    fetchLogo();
  }, []);

  // âœ… Láº¥y sá»‘ lÆ°á»£ng giá» hÃ ng - ÄÃƒ Sá»¬A
  useEffect(() => {
    loadCartCount();
  }, [user, location.pathname]); // ThÃªm location.pathname Ä‘á»ƒ reload khi route thay Ä‘á»•i

  const fetchCategories = async () => {
    try {
      setLoadingCategories(true);
      const categoriesData = await categoryService.getAllCategories();
      setCategories(categoriesData || []);
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y danh má»¥c:', error);
      setCategories([]);
    } finally {
      setLoadingCategories(false);
    }
  };

  const fetchLogo = async () => {
    try {
      const banners = await bannerService.getAllBanners();
      const logoBanner = banners.find(banner => 
        banner.bannerId === 4 || banner.id === 4
      );

      if (logoBanner) {
        setLogo(logoBanner);
      } else if (banners.length > 0) {
        setLogo(banners[0]);
      }
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y logo:', error);
    }
  };

  // âœ… HÃ€M LOAD CART COUNT ÄÃƒ Sá»¬A
  // Trong Header.js - Sá»¬A HÃ€M loadCartCount
const loadCartCount = async () => {
  try {
    let count = 0;

    // LuÃ´n Æ°u tiÃªn localStorage trÆ°á»›c
    // Trong Header.js - Sá»¬A HÃ€M getCartCount
const getCartCount = async (emailId) => {
  try {
    // LuÃ´n Æ°u tiÃªn localStorage trÆ°á»›c
    const localCount = cartService.getCartCountFromStorage();
    if (localCount > 0) {
      return localCount;
    }

    // Chá»‰ gá»i API náº¿u cÃ³ user thá»±c sá»± vÃ  cÃ³ cartId
    if (emailId && emailId !== 'guest@example.com') {
      try {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        const cartId = user.cart?.cartId;

        // Náº¿u khÃ´ng cÃ³ cartId, khÃ´ng gá»i API
        if (!cartId) {
          console.warn('âš ï¸ KhÃ´ng cÃ³ cartId, khÃ´ng thá»ƒ láº¥y cart tá»« server');
          return 0;
        }

        const cart = await cartService.getCart(emailId, cartId);
        if (cart && cart.products) {
          // Cáº­p nháº­t localStorage
          cartService.updateCartInStorage(cart);
          return cartService.getTotalItems(cart.products);
        }
      } catch (serverError) {
        console.warn('âš ï¸ KhÃ´ng thá»ƒ láº¥y cart tá»« server:', serverError.message);
        // Tiáº¿p tá»¥c vá»›i localStorage
      }
    }

    return 0;
  } catch (error) {
    console.error('âŒ Lá»—i khi láº¥y sá»‘ lÆ°á»£ng giá» hÃ ng:', error);
    return cartService.getCartCountFromStorage();
  }
};

    // Chá»‰ gá»i API náº¿u cÃ³ user thá»±c sá»±
    if (user && user.email && user.email !== 'guest@example.com') {
      try {
        const serverCount = await cartService.getCartCount(user.email);
        if (serverCount > 0) {
          count = serverCount;
          console.log("ðŸ›’ Cart count tá»« server:", count);
        }
      } catch (serverError) {
        console.warn("âš ï¸ KhÃ´ng thá»ƒ láº¥y cart count tá»« server:", serverError.message);
        // Váº«n sá»­ dá»¥ng count tá»« localStorage
      }
    }

    setCartCount(count);
  } catch (error) {
    console.error('âŒ Lá»—i khi láº¥y sá»‘ lÆ°á»£ng giá» hÃ ng:', error);
    // Fallback vá» localStorage
    const localCount = cartService.getCartCountFromStorage();
    setCartCount(localCount);
  }
};

  // âœ… THÃŠM: HÃ m refresh cart count (cÃ³ thá»ƒ gá»i tá»« bÃªn ngoÃ i)
  const refreshCartCount = () => {
    loadCartCount();
  };

  const getLogoUrl = () => {
    if (!logo) return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";

    const imageName = logo.imageUrl || logo.image_url;

    if (!imageName) {
      return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";
    }

    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  // âœ… Äiá»u hÆ°á»›ng Ä‘áº¿n trang sáº£n pháº©m theo category
  const handleCategoryClick = (category) => {
    const categoryId = category.categoryId || category.id;
    const categoryName = category.categoryName || category.name || category.title || 'Danh má»¥c';

    if (categoryId) {
      navigate(`/product`, { 
        state: { 
          selectedCategory: categoryId,
          categoryName: categoryName
        }
      });
    }
  };

  // âœ… ÄÄƒng xuáº¥t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    localStorage.removeItem("cart"); // âœ… THÃŠM: XÃ³a cart khi logout
    setUser(null);
    setUserInfo(null);
    setCartCount(0);
    navigate("/login");
  };

  // âœ… Chuyá»ƒn Ä‘áº¿n trang chá»‰nh sá»­a profile
  const handleProfileClick = () => {
    if (user) {
      navigate("/profile");
    } else {
      navigate("/login");
    }
  };

  // âœ… HÃ m hiá»ƒn thá»‹ tÃªn ngÆ°á»i dÃ¹ng
  const renderUserName = () => {
    if (!user) return "My profile";

    if (loading) {
      return (
        <div className="d-flex align-items-center">
          <div className="spinner-border spinner-border-sm me-2" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          Loading...
        </div>
      );
    }

    if (userInfo && (userInfo.firstName || userInfo.lastName)) {
      const firstName = userInfo.firstName || "";
      const lastName = userInfo.lastName || "";
      const fullName = `${firstName} ${lastName}`.trim();

      if (fullName) {
        return fullName;
      }
    }

    if (user.firstName || user.first_name) {
      const firstName = user.firstName || user.first_name || "";
      const lastName = user.lastName || user.last_name || "";
      const fullName = `${firstName} ${lastName}`.trim();

      if (fullName) {
        return fullName;
      }
    }

    if (user.email) {
      return user.email.split('@')[0];
    }

    return "User";
  };

  // âœ… HÃ m hiá»ƒn thá»‹ lá»i chÃ o
  const renderGreeting = () => {
    if (!user) return null;

    if (userInfo && userInfo.firstName) {
      return `ðŸ‘‹ Hi, ${userInfo.firstName}`;
    }

    if (user.firstName || user.first_name) {
      const firstName = user.firstName || user.first_name;
      return `ðŸ‘‹ Hi, ${firstName}`;
    }

    if (user.email) {
      return `ðŸ‘‹ Hi, ${user.email.split('@')[0]}`;
    }

    return "ðŸ‘‹ Hi, User";
  };

  // âœ… HÃ m hiá»ƒn thá»‹ tÃªn category
  const getCategoryName = (category) => {
    return category.categoryName || category.name || category.title || 'Danh má»¥c';
  };

  // âœ… HÃ m get menu style vá»›i active state
  const getMenuStyle = (isActive) => {
    const baseStyle = {
      padding: '0.5rem 1.5rem',
      borderRadius: '25px',
      transition: 'all 0.3s ease',
      fontWeight: '600'
    };

    if (isActive) {
      return {
        ...baseStyle,
        backgroundColor: '#667eea',
        color: 'white',
        transform: 'scale(1.05)'
      };
    }

    return {
      ...baseStyle,
      backgroundColor: 'transparent',
      color: '#212529'
    };
  };

  // âœ… HÃ m get dropdown style vá»›i active state
  const getDropdownStyle = (isActive) => {
    const baseStyle = {
      padding: '0.5rem 1.5rem',
      borderRadius: '25px',
      transition: 'all 0.3s ease',
      fontWeight: '600',
      cursor: 'pointer'
    };

    if (isActive) {
      return {
        ...baseStyle,
        backgroundColor: '#667eea',
        color: 'white',
        transform: 'scale(1.05)'
      };
    }

    return {
      ...baseStyle,
      backgroundColor: 'transparent',
      color: '#212529'
    };
  };

  return (
    <header className="section-header">
      {/* ======= THANH TRÃŠN ======= */}
      <nav className="navbar navbar-expand-lg navbar-light border-bottom">
        <div className="container">
          <button 
            className="navbar-toggler" 
            type="button" 
            data-bs-toggle="collapse" 
            data-bs-target="#navbarTop4"
            aria-controls="navbarTop4" 
            aria-expanded="false" 
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav me-auto">
              <li className="nav-item">
                {user ? (
                  <span className="nav-link">
                    {renderGreeting()} |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi,{" "}
                    <Link 
                      to="/login" 
                      className="text-decoration-none fw-semibold"
                      style={{ margin: '0 8px' }}
                    >
                      Log In
                    </Link>{" "}
                    or{" "}
                    <Link 
                      to="/register" 
                      className="text-decoration-none fw-semibold"
                      style={{ margin: '0 8px' }}
                    >
                      Register
                    </Link>
                  </span>
                )}
              </li>
            </ul>

            <ul className="navbar-nav">
              <li className="nav-item">
                <a href="#" className="nav-link" style={{ padding: '8px 16px' }}>
                  <img src={flagVN} height="20" alt="flag" className="me-2" /> 
                  Ship to
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ======= THANH CHÃNH - LOGO + MENU CÃ™NG HÃ€NG ======= */}
      <div className="container">
        <section className="header-main border-bottom py-3">
          <div className="d-flex align-items-center justify-content-between">
            {/* âœ… Logo to bÃªn trÃ¡i */}
            <div className="flex-shrink-0">
              <Link to="/" className="brand-wrap text-decoration-none">
                <div className="d-flex align-items-center">
                  <div 
                    className="rounded-circle overflow-hidden border shadow-lg"
                    style={{
                      width: '100px',
                      height: '100px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      backgroundColor: '#f8f9fa',
                      border: '3px solid #667eea'
                    }}
                  >
                    <img
                      src={getLogoUrl()}
                      alt="Logo"
                      className="img-fluid"
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover'
                      }}
                      onError={(e) => {
                        e.target.src = "https://via.placeholder.com/100x100/667eea/ffffff?text=LOGO";
                      }}
                    />
                  </div>
                  <div className="ms-3">
                    <h3 className="fw-bold text-primary mb-1">THUYRSHOP</h3>
                    <small className="text-muted fw-semibold">Premium Shopping</small>
                  </div>
                </div>
              </Link>
            </div>

            {/* âœ… Menu chÃ­nh - Vá»šI ACTIVE STATE */}
            <div className="flex-grow-1 mx-4">
              <nav className="navbar navbar-main p-0">
                <ul className="navbar-nav flex-row justify-content-center w-100">
                  {/* Home Menu - ACTIVE STATE */}
                  <li className="nav-item mx-3">
                    <Link 
                      to="/" 
                      className="nav-link fs-5 position-relative text-decoration-none"
                      style={getMenuStyle(isActive('/'))}
                    >
                      Home
                    </Link>
                  </li>

                  {/* Products Menu - ACTIVE STATE */}
                  <li className="nav-item mx-3">
                    <Link 
                      to="/product" 
                      className="nav-link fs-5 position-relative text-decoration-none"
                      style={getMenuStyle(isActive('/product') && !isCategoryActive())}
                    >
                      Products
                    </Link>
                  </li>

                  {/* âœ… Categories Dropdown - ACTIVE STATE */}
                  <li className="nav-item dropdown mx-3">
                    <a
                      className="nav-link dropdown-toggle fs-5 position-relative text-decoration-none"
                      href="#"
                      id="categoriesDropdown"
                      role="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                      style={getDropdownStyle(isCategoryActive())}
                    >
                      Categories
                      {loadingCategories && (
                        <span className="ms-1">
                          <div className="spinner-border spinner-border-sm" role="status">
                            <span className="visually-hidden">Loading...</span>
                          </div>
                        </span>
                      )}
                    </a>

                    <ul 
                      className="dropdown-menu shadow-lg" 
                      aria-labelledby="categoriesDropdown"
                      style={{ 
                        maxHeight: '400px', 
                        overflowY: 'auto',
                        border: 'none',
                        borderRadius: '12px'
                      }}
                    >
                      {loadingCategories ? (
                        <li>
                          <div className="dropdown-item text-muted">
                            <div className="d-flex align-items-center">
                              <div className="spinner-border spinner-border-sm me-2" role="status">
                                <span className="visually-hidden">Loading...</span>
                              </div>
                              Loading categories...
                            </div>
                          </div>
                        </li>
                      ) : categories.length > 0 ? (
                        categories.map((category, index) => (
                          <li key={category.categoryId || category.id || index}>
                            <a
                              className="dropdown-item py-2 px-3"
                              href="#"
                              onClick={(e) => {
                                e.preventDefault();
                                handleCategoryClick(category);
                              }}
                              style={{
                                transition: 'all 0.2s ease',
                                borderRadius: '6px',
                                margin: '2px 8px'
                              }}
                            >
                              {getCategoryName(category)}
                            </a>
                          </li>
                        ))
                      ) : (
                        <li>
                          <div className="dropdown-item text-muted py-2 px-3">
                            No categories found
                          </div>
                        </li>
                      )}
                    </ul>
                  </li>
                </ul>
              </nav>
            </div>

            {/* âœ… CÃ¡c icon bÃªn pháº£i */}
            <div className="flex-shrink-0">
              <div className="widgets-wrap d-flex align-items-center gap-4">
                {/* Widget Profile */}
                <div className="widget-header">
                  <a 
                    href="#" 
                    className="widget-view text-decoration-none"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileClick();
                    }}
                  >
                    <div className="icon-area text-center mb-2">
                      <div 
                        className="bg-primary rounded-circle shadow-lg d-flex align-items-center justify-content-center mx-auto"
                        style={{
                          width: '55px',
                          height: '55px',
                          transition: 'all 0.3s ease'
                        }}
                      >
                        <i className="fa fa-user text-white fa-lg"></i>
                      </div>
                    </div>
                    <small className="text-dark fw-semibold d-block text-center fs-6">
                      {renderUserName()}
                    </small>
                  </a>
                </div>

                {/* Widget Cart - ÄÃƒ Sá»¬A: ThÃªm refresh khi click */}
                <div className="widget-header">
                  <Link 
                    to="/cart" 
                    className="widget-view text-decoration-none"
                    onClick={() => {
                      // Refresh cart count khi click vÃ o giá» hÃ ng
                      setTimeout(() => {
                        loadCartCount();
                      }, 100);
                    }}
                  >
                    <div className="icon-area text-center mb-2 position-relative">
                      <div 
                        className="bg-warning rounded-circle shadow-lg d-flex align-items-center justify-content-center mx-auto"
                        style={{
                          width: '55px',
                          height: '55px',
                          transition: 'all 0.3s ease'
                        }}
                      >
                        <i className="fa fa-shopping-cart text-white fa-lg"></i>
                        {cartCount > 0 && (
                          <span 
                            className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger"
                            style={{
                              fontSize: '0.7rem',
                              padding: '0.3em 0.6em',
                              minWidth: '20px'
                            }}
                          >
                            {cartCount > 99 ? '99+' : cartCount}
                          </span>
                        )}
                      </div>
                    </div>
                    <small className="text-dark fw-semibold d-block text-center fs-6">
                      Cart {cartCount > 0 && `(${cartCount})`}
                    </small>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    </header>
  );
};

export default Header;
