import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [pageNumber, setPageNumber] = useState(0); // B·∫Øt ƒë·∫ßu t·ª´ 0 (trang ƒë·∫ßu ti√™n)
  const [pageSize, setPageSize] = useState(4); // M·∫∑c ƒë·ªãnh 4 s·∫£n ph·∫©m/trang
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(false);

  // üß© H√†m t·∫£i s·∫£n ph·∫©m c√≥ ph√¢n trang
  const fetchProducts = async (page = 0, size = pageSize) => {
    try {
      setLoading(true);
      // Chuy·ªÉn ƒë·ªïi page number: React (0-based) ‚Üí API (1-based)
      const apiPage = page + 1;

      const response = await productService.getAllProducts(apiPage, size);

      const data = response || {};
      const content = data.content || [];

      console.log("üì¶ D·ªØ li·ªáu s·∫£n ph·∫©m:", data);
      console.log("üìä Trang hi·ªán t·∫°i:", page, "‚Üí API page:", apiPage);
      console.log("üî¢ T·ªïng s·ªë trang:", data.totalPages);

      setProducts(content);
      setTotalPages(data.totalPages || 0);
    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts(pageNumber, pageSize);
  }, [pageNumber, pageSize]);

  // üß≠ X·ª≠ l√Ω khi chuy·ªÉn trang
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setPageNumber(newPage);
    }
  };

  // üßÆ X·ª≠ l√Ω thay ƒë·ªïi s·ªë l∆∞·ª£ng s·∫£n ph·∫©m/trang
  const handlePageSizeChange = (e) => {
    const newSize = Number(e.target.value);
    setPageSize(newSize);
    setPageNumber(0); // quay l·∫°i trang ƒë·∫ßu
  };

  // üìä T·∫°o danh s√°ch s·ªë trang ƒë·ªÉ hi·ªÉn th·ªã
  const renderPageNumbers = () => {
    const pages = [];
    const maxVisible = 5;
    let start = Math.max(0, pageNumber - Math.floor(maxVisible / 2));
    let end = Math.min(totalPages, start + maxVisible);

    // ƒêi·ªÅu ch·ªânh n·∫øu g·∫ßn cu·ªëi
    if (end - start < maxVisible) {
      start = Math.max(0, end - maxVisible);
    }

    for (let i = start; i < end; i++) {
      pages.push(
        <button
          key={i}
          className={`btn mx-1 ${
            i === pageNumber
              ? "btn-primary text-white"
              : "btn-outline-primary"
          }`}
          onClick={() => handlePageChange(i)}
        >
          {i + 1}
        </button>
      );
    }

    return pages;
  };

  return (
    <section className="padding-bottom container mt-5">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase fw-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
      </header>

      {/* Th√¥ng tin ph√¢n trang */}
      <div className="d-flex justify-content-between align-items-center mb-3">
        <div className="text-muted small">
          üìÑ Trang {pageNumber + 1} / {totalPages} 
          {totalPages > 0 && ` (${products.length} s·∫£n ph·∫©m)`}
        </div>

        {/* B·ªô ch·ªçn s·ªë s·∫£n ph·∫©m/trang */}
        <div className="d-flex align-items-center">
          <label className="me-2 fw-semibold">Hi·ªÉn th·ªã:</label>
          <select
            value={pageSize}
            onChange={handlePageSizeChange}
            className="form-select w-auto"
          >
            <option value="4">4 / trang</option>
            <option value="8">8 / trang</option>
            <option value="12">12 / trang</option>
          </select>
        </div>
      </div>

      {/* üß© Hi·ªÉn th·ªã s·∫£n ph·∫©m */}
      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status"></div>
          <p className="mt-2 text-muted">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      ) : (
        <div className="row">
          {products.map((p) => {
            const productId = p.productId || p.product_id;

            return (
              <div
                key={productId}
                className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4"
              >
                <div className="card card-product-grid shadow-sm h-100">
                  <div className="img-wrap">
                    <Link
                      to={`/product/${productId}`}
                      className="text-decoration-none"
                    >
                      <img
                        src={`${IMAGE_BASE_URL}/${p.image}`}
                        alt={p.productName || p.product_name}
                        onError={(e) =>
                          (e.target.src =
                            "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                        }
                        style={{
                          width: "100%",
                          height: "250px",
                          objectFit: "cover",
                          borderRadius: "10px",
                        }}
                      />
                    </Link>
                  </div>

                  <div className="info-wrap p-3 d-flex flex-column">
                    <Link
                      to={`/product/${productId}`}
                      className="text-decoration-none"
                    >
                      <h6 className="text-truncate text-dark fw-bold">
                        {p.productName || p.product_name}
                      </h6>
                    </Link>

                    <p className="text-muted small mb-2 flex-grow-1">
                      {p.description?.substring(0, 40) ||
                        "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£..."}
                    </p>

                    <div className="price-wrapper mb-1">
                      {p.specialPrice ? (
                        <>
                          <span className="price h5 text-danger mb-0">
                            {p.specialPrice.toLocaleString()} ‚Ç´
                          </span>
                          <span className="text-muted text-decoration-line-through small ms-2">
                            {p.price.toLocaleString()} ‚Ç´
                          </span>
                        </>
                      ) : (
                        <span className="price h5 text-danger mb-0">
                          {p.price.toLocaleString()} ‚Ç´
                        </span>
                      )}
                    </div>

                    {p.discount > 0 && (
                      <small className="text-success mt-1">
                        üî• Gi·∫£m {p.discount}%
                      </small>
                    )}

                    <small
                      className={`mt-1 ${
                        p.quantity === 0 ? "text-danger" : "text-success"
                      }`}
                    >
                      {p.quantity === 0
                        ? "‚ùå H·∫øt h√†ng"
                        : `üì¶ C√≤n ${p.quantity} s·∫£n ph·∫©m`}
                    </small>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* üì≠ Kh√¥ng c√≥ s·∫£n ph·∫©m */}
      {!loading && products.length === 0 && (
        <div className="text-center py-5">
          <h4>üì≠ Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o</h4>
          <p className="text-muted">
            Hi·ªán t·∫°i kh√¥ng c√≥ s·∫£n ph·∫©m n√†o ƒë·ªÉ hi·ªÉn th·ªã.
          </p>
        </div>
      )}

      {/* üìë Ph√¢n trang - Ch·ªâ hi·ªÉn th·ªã khi c√≥ nhi·ªÅu h∆°n 1 trang */}
      {totalPages > 1 && (
        <div className="d-flex justify-content-center align-items-center mt-4 flex-wrap">
          {/* N√∫t Tr∆∞·ªõc */}
          <button
            className="btn btn-outline-primary me-2"
            disabled={pageNumber === 0}
            onClick={() => handlePageChange(pageNumber - 1)}
          >
            ‚Üê Tr∆∞·ªõc
          </button>

          {/* Hi·ªÉn th·ªã trang ƒë·∫ßu n·∫øu kh√¥ng ·ªü trang ƒë·∫ßu */}
          {pageNumber > 2 && (
            <>
              <button
                className="btn btn-outline-primary mx-1"
                onClick={() => handlePageChange(0)}
              >
                1
              </button>
              {pageNumber > 3 && <span className="mx-1">...</span>}
            </>
          )}

          {/* S·ªë trang */}
          {renderPageNumbers()}

          {/* Hi·ªÉn th·ªã trang cu·ªëi n·∫øu kh√¥ng ·ªü trang cu·ªëi */}
          {pageNumber < totalPages - 3 && (
            <>
              {pageNumber < totalPages - 4 && <span className="mx-1">...</span>}
              <button
                className="btn btn-outline-primary mx-1"
                onClick={() => handlePageChange(totalPages - 1)}
              >
                {totalPages}
              </button>
            </>
          )}

          {/* N√∫t Sau */}
          <button
            className="btn btn-outline-primary ms-2"
            disabled={pageNumber === totalPages - 1}
            onClick={() => handlePageChange(pageNumber + 1)}
          >
            Sau ‚Üí
          </button>
        </div>
      )}
    </section>
  );
};

export default ProductList;
