import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        { role: 'ai', text: 'Ch√†o n√†ng! Em l√† Ng·ªçc Anh Beauty AI, em c√≥ th·ªÉ gi√∫p g√¨ cho n√†ng h√¥m nay? ‚ù§Ô∏è' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    // T·ª± ƒë·ªông cu·ªôn xu·ªëng cu·ªëi
    useEffect(() => {
        scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        // L·∫•y th√¥ng tin t·ª´ localStorage
        const user = JSON.parse(localStorage.getItem('user'));
        const token = localStorage.getItem('token') || user?.token;
        const cartId = localStorage.getItem('cartId') || user?.cartId;

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: userMsg,
                cartId: cartId 
            }, {
                headers: {
                    'Authorization': token ? `Bearer ${token}` : '',
                    'Content-Type': 'application/json'
                }
            });

            const data = res.data;
            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: data.reply,
                suggestions: data.suggested_products 
            }]);

        } catch (error) {
            console.error("L·ªói AI:", error);
            setMessages(prev => [...prev, { role: 'ai', text: 'Hic, em ƒëang b·∫≠n m·ªôt ch√∫t, n√†ng ch·ªù em x√≠u nh√©! üòÖ' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1050 }}>
            {/* N√∫t tr√≤n Toggle */}
            <button onClick={() => setIsOpen(!isOpen)} className="btn btn-warning rounded-circle shadow-lg p-0" style={{ width: '60px', height: '60px' }}>
                <i className={`bi ${isOpen ? 'bi-x-lg' : 'bi-chat-dots-fill'} fs-3`}></i>
            </button>

            {/* C·ª≠a s·ªï Chat */}
            {isOpen && (
                <div className="card shadow-lg border-0 rounded-4 mt-2" style={{ width: '350px', height: '500px', display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
                    <div className="card-header bg-warning text-dark py-3 px-3 border-0">
                        <div className="d-flex align-items-center">
                            <div className="bg-white rounded-circle p-1 me-2 shadow-sm">
                                <i className="bi bi-robot text-warning"></i>
                            </div>
                            <div>
                                <h6 className="mb-0 fw-bold">Ng·ªçc Anh Beauty AI</h6>
                                <small className="opacity-75">S·∫µn s√†ng t∆∞ v·∫•n cho n√†ng</small>
                            </div>
                        </div>
                    </div>

                    <div className="card-body p-3 overflow-auto" style={{ flex: 1, backgroundColor: '#fcfcfc' }}>
                        {messages.map((msg, idx) => (
                            <div key={idx} className={`d-flex mb-3 ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'}`}>
                                <div className={`p-2 px-3 rounded-4 shadow-sm ${msg.role === 'user' ? 'bg-warning text-dark' : 'bg-white text-dark border'}`} style={{ maxWidth: '85%', fontSize: '14.5px' }}>
                                    {msg.text}
                                    {msg.suggestions?.length > 0 && (
                                        <div className="mt-2 pt-2 border-top">
                                            {msg.suggestions.map((p, i) => (
                                                <div key={i} className="d-flex align-items-center gap-2 mb-2 p-1 bg-light rounded shadow-sm">
                                                    <img src={`http://localhost:8080/api/public/products/image/${p.image}`} width="35" height="35" className="rounded" alt="" />
                                                    <div className="flex-grow-1 overflow-hidden">
                                                        <div className="small fw-bold text-truncate" style={{maxWidth: '150px'}}>{p.productName}</div>
                                                        <div className="small text-danger fw-bold">{(p.specialPrice || p.price).toLocaleString()}‚Ç´</div>
                                                    </div>
                                                    <a href={`/product/${p.productId}`} className="btn btn-sm btn-link text-warning p-0"><i className="bi bi-chevron-right"></i></a>
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}
                        {loading && <div className="text-muted small"><span className="spinner-grow spinner-grow-sm me-1"></span> ƒêang tr·∫£ l·ªùi...</div>}
                        <div ref={scrollRef} />
                    </div>

                    <div className="card-footer bg-white border-top-0 p-3">
                        <div className="input-group bg-light rounded-pill px-3 py-1 border">
                            <input type="text" className="form-control border-0 bg-transparent shadow-none" placeholder="H·ªèi em v·ªÅ m·ªπ ph·∫©m..." value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && handleSend()} />
                            <button onClick={handleSend} className="btn border-0 text-warning px-0"><i className="bi bi-send-fill fs-5"></i></button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default GeminiChat;
