      .catch((err) => console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err));
  }, []);

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase font-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
      </header>

      <div className="row">
        {products.map((p) => {
          // d√πng ƒë√∫ng ID theo d·ªØ li·ªáu API
          const productId = p.product_id ?? p.id ?? p._id;
          console.log("Rendering product ID:", productId);

          return (
            <div key={productId} className="col-xl-3 col-lg-3 col-md-4 col-6">
              <Link to={`/product/${productId}`}>
                <div className="card card-product-grid shadow-sm">
                  <div className="img-wrap">
                    <img
                      src={productService.getProductImage(p.image)}
                      alt={p.product_name || p.name}
                      onError={(e) =>
                        (e.target.src =
