import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const ProductListAD = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  
  const [formData, setFormData] = useState({
    productId: "",
    productName: "",
    price: "",
    discount: 0,
    quantity: 1,
    description: "",
    categoryId: ""
  });
  const [selectedFile, setSelectedFile] = useState(null);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0 });

  const token = localStorage.getItem("jwt-token");

  const fetchData = useCallback(async (page = 0) => {
    console.log("--- B·∫Øt ƒë·∫ßu l·∫•y d·ªØ li·ªáu t·ª´ API ---");
    console.log("Token hi·ªán c√≥:", token ? "ƒê√£ c√≥ Token" : "CH∆ØA C√ì TOKEN!");

    if (!token) {
        setLoading(false);
        return;
    }

    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      
      // Log ƒë·ªÉ check URL
      console.log(`ƒêang g·ªçi API: http://localhost:8080/api/admin/products?pageNumber=${page}`);

      const [prodRes, catRes] = await Promise.all([
        axios.get(`http://localhost:8080/api/admin/products?pageNumber=${page}&pageSize=10`, config),
        axios.get("http://localhost:8080/api/public/categories", config)
      ]);

      console.log("D·ªØ li·ªáu S·∫£n ph·∫©m nh·∫≠n v·ªÅ:", prodRes.data);
      console.log("D·ªØ li·ªáu Danh m·ª•c nh·∫≠n v·ªÅ:", catRes.data);

      // FIX: Ki·ªÉm tra c·∫•u tr√∫c ph√¢n trang c·ªßa Backend
      const productData = prodRes.data.content || [];
      setProducts(productData);
      
      setPageConfig({ 
        pageNumber: prodRes.data.pageNumber || 0, 
        totalPages: prodRes.data.totalPages || 0 
      });
      
      setCategories(catRes.data.content || catRes.data || []);
      setLoading(false);
      
    } catch (err) {
      console.error("--- L·ªñI API CHI TI·∫æT ---");
      if (err.response) {
        console.error("Status:", err.response.status);
        console.error("Data l·ªói t·ª´ Server:", err.response.data);
      } else {
        console.error("L·ªói kh√¥ng x√°c ƒë·ªãnh:", err.message);
      }
      setLoading(false);
    }
  }, [token]);

  useEffect(() => { fetchData(); }, [fetchData]);

  // Logic Render... (Ph·∫ßn HTML gi·ªØ nguy√™n nh∆∞ b·∫£n tr∆∞·ªõc)
  if (loading) return <div className="text-center mt-5">ƒêang t·∫£i... Check Console (F12) n·∫øu ch·ªù l√¢u.</div>;

  return (
    <div className="container-fluid p-4">
        {/* N·∫øu kh√¥ng c√≥ token th√¨ hi·ªán th√¥ng b√°o thay v√¨ tr·∫Øng trang */}
        {!token && <div className="alert alert-danger">M√†y ch∆∞a ƒëƒÉng nh·∫≠p! H√£y quay l·∫°i trang Login.</div>}
        
        <div className="card shadow border-0">
            <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
                <h5 className="m-0 fw-bold text-primary">üì¶ QU·∫¢N L√ù KHO M·ª∏ PH·∫®M</h5>
                <button className="btn btn-primary" onClick={() => {
                    console.log("B·∫•m n√∫t Th√™m m·ªõi");
                    setShowModal(true);
                    setIsEdit(false);
                }}>+ Th√™m s·∫£n ph·∫©m</button>
            </div>

            <div className="table-responsive">
                <table className="table table-hover align-middle mb-0">
                    <thead className="table-light">
                        <tr>
                            <th>ID</th>
                            <th>·∫¢nh</th>
                            <th>T√™n s·∫£n ph·∫©m</th>
                            <th>Gi√° b√°n</th>
                            <th className="text-center">Thao t√°c</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.length > 0 ? products.map(p => (
                            <tr key={p.productId}>
                                <td>{p.productId}</td>
                                <td>
                                    <img src={`http://localhost:8080/api/public/products/image/${p.image}`} 
                                         style={{width: "50px"}} alt="" 
                                         onError={(e) => e.target.src="https://via.placeholder.com/50"}/>
                                </td>
                                <td>{p.productName}</td>
                                <td>{p.specialPrice?.toLocaleString()}ƒë</td>
                                <td className="text-center">
                                    <button className="btn btn-sm btn-outline-info me-2" onClick={() => {
                                        console.log("S·ª≠a s·∫£n ph·∫©m:", p.productId);
                                        // G√°n data v√†o form...
                                    }}>S·ª≠a</button>
                                </td>
                            </tr>
                        )) : (
                            <tr><td colSpan="5" className="text-center">Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m n√†o. Check Console xem c√≥ l·ªói 403 kh√¥ng.</td></tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    </div>
  );
};

export default ProductListAD;
