import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Header = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // L·∫•y user t·ª´ localStorage khi load trang
  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  // ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("jwt");
    localStorage.removeItem("user");
    localStorage.removeItem("role");
    document.cookie = "role=; path=/;";
    alert("ƒêƒÉng xu·∫•t th√†nh c√¥ng!");
    navigate("/auth/login");
  };

  return (
    <header
      style={{
        backgroundColor: "#fff",
        padding: "10px 20px",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
      }}
    >
      {/* Logo */}
      <div style={{ display: "flex", alignItems: "center" }}>
        <img
          src="/images/logo.png"
          alt="Logo"
          style={{
            width: "120px",
            height: "auto",
            borderRadius: "10px",
            marginRight: "10px",
          }}
        />
        <h2 style={{ color: "#ff6600", fontWeight: "700" }}>
          NGUYEN THI THU THUY SHOP
        </h2>
      </div>

      {/* Menu */}
      <nav>
        <ul
          style={{
            display: "flex",
            gap: "20px",
            listStyle: "none",
            margin: 0,
            padding: 0,
          }}
        >
          <li>
            <Link to="/" style={linkStyle}>
              Home
            </Link>
          </li>
          <li>
            <Link to="/products" style={linkStyle}>
              Product
            </Link>
          </li>
          <li>
            <Link to="/about" style={linkStyle}>
              About
            </Link>
          </li>
          <li>
            <Link to="/contact" style={linkStyle}>
              Contact
            </Link>
          </li>
        </ul>
      </nav>

      {/* Ph·∫ßn b√™n ph·∫£i: user + gi·ªè h√†ng */}
      <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
        {/* Gi·ªè h√†ng */}
        <button
          style={{
            background: "none",
            border: "none",
            position: "relative",
            cursor: "pointer",
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={2}
            stroke="currentColor"
            style={{ width: "26px", height: "26px", color: "#ff6600" }}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 
              2.293c-.63.63-.184 1.707.707 1.707H17m0 
              0a2 2 0 100 4 2 2 0 000-4zm-8 
              2a2 2 0 11-4 0 2 2 0 014 0z"
            />
          </svg>
          <span
            style={{
              position: "absolute",
              top: "-5px",
              right: "-10px",
              background: "red",
              color: "white",
              borderRadius: "50%",
              padding: "2px 6px",
              fontSize: "12px",
            }}
          >
            3
          </span>
        </button>

        {/* Avatar + t√™n ng∆∞·ªùi d√πng */}
        {user ? (
          <div style={{ position: "relative" }}>
            <div style={{ textAlign: "center" }}>
              <img
                src="/images/avatar.jpeg"
                alt="avatar"
                style={{
                  width: "40px",
                  height: "40px",
                  borderRadius: "50%",
                  border: "2px solid #ff6600",
                  cursor: "pointer",
                }}
              />
              <p style={{ fontSize: "14px", margin: "5px 0 0 0" }}>
                {user.username || user.first_name}
              </p>
            </div>

            {/* Dropdown menu */}
            <ul
              style={{
                position: "absolute",
                right: 0,
                top: "60px",
                background: "#fff",
                boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                listStyle: "none",
                padding: "10px",
                borderRadius: "8px",
                display: "none",
              }}
              className="user-dropdown"
            >
              <li>
                <Link to="/profile" style={menuItem}>
                  Profile
                </Link>
              </li>
              <li>
                <Link to="/settings" style={menuItem}>
                  Settings
                </Link>
              </li>
              <li>
                <button onClick={handleLogout} style={menuItem}>
                  Logout
                </button>
              </li>
            </ul>
          </div>
        ) : (
          <Link
            to="/site/auth/login"
            style={{
              padding: "8px 14px",
              backgroundColor: "#ff6600",
              color: "white",
              borderRadius: "6px",
              textDecoration: "none",
            }}
          >
            Login
          </Link>
        )}
      </div>
    </header>
  );
};

// üîπ CSS n·ªôi b·ªô
const linkStyle = {
  textDecoration: "none",
  color: "#ff6600",
  fontWeight: "500",
  transition: "0.2s",
};
const menuItem = {
  display: "block",
  padding: "6px 10px",
  textDecoration: "none",
  color: "#333",
  borderRadius: "5px",
  cursor: "pointer",
};

export default Header;
