import { useEffect, useState, useCallback } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import categoryService from "../../services/categoryService";
import ProductCard from "../product/ProductCard";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();

  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    categoryId: "",
    searchKeyword: "",
    sortBy: "price",
    sortOrder: "asc"
  });

  // 1. LẮNG NGHE DỮ LIỆU TỪ HEADER TRUYỀN SANG (Search hoặc Category từ Header)
  useEffect(() => {
    if (location.state) {
      const { searchKeyword, selectedCategory } = location.state;
      setFilters(prev => ({
        ...prev,
        searchKeyword: searchKeyword || "",
        categoryId: selectedCategory || ""
      }));
      if (searchKeyword) setSearchTerm(searchKeyword);

      // Xóa state để tránh việc load lại khi F5 trang
      window.history.replaceState({}, document.title);
    }
  }, [location.state]);

  // 2. Debounce tìm kiếm tại chỗ
  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      setFilters(prev => ({ ...prev, searchKeyword: searchTerm }));
    }, 500);
    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  // 3. Tải danh mục ban đầu
  useEffect(() => {
    categoryService.getAllCategories().then(data => setCategories(data || []));
  }, []);

  // 4. HÀM TẢI SẢN PHẨM CHÍNH
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    const PAGE_SIZE = 1000;
    try {
      let response;
      const { categoryId, searchKeyword, sortBy, sortOrder } = filters;

      if (searchKeyword) {
        response = await productService.searchProducts(searchKeyword, 0, PAGE_SIZE, sortBy, sortOrder);
      } else if (categoryId) {
        response = await productService.getProductsByCategory(categoryId, 0, PAGE_SIZE, sortBy, sortOrder);
      } else {
        response = await productService.getAllProducts(0, PAGE_SIZE, sortBy, sortOrder);
      }
      setProducts(response?.content || []);
    } catch (err) {
      console.error("Lỗi tải sản phẩm:", err);
      setProducts([]);
    } finally {
      setTimeout(() => setLoading(false), 300);
    }
  }, [filters]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // 5. HÀM XÓA BỘ LỌC
  const handleClearFilters = () => {
    setSearchTerm("");
    setFilters({
      categoryId: "",
      searchKeyword: "",
      sortBy: "price",
      sortOrder: "asc"
    });
    // Xóa state điều hướng nếu có
    navigate("/product", { state: {}, replace: true });
  };

  return (
    <section className="container mt-5 pb-5">
      {/* Thanh công cụ bộ lọc */}
      <div className="row g-3 mb-4 bg-light p-3 rounded-4 align-items-center shadow-sm">
        {/* Lọc theo danh mục */}
        <div className="col-md-3">
          <select 
            value={filters.categoryId} 
            onChange={(e) => setFilters(p => ({ ...p, categoryId: e.target.value }))}
            className="form-select rounded-pill border-0 shadow-sm px-3"
          >
            <option value="">Tất cả danh mục</option>
            {categories.map(c => (
              <option key={c.id || c.categoryId} value={c.id || c.categoryId}>
                {c.categoryName || c.name}
              </option>
            ))}
          </select>
        </div>

        {/* Ô tìm kiếm nhanh */}
        <div className="col-md-6">
          <div className="position-relative">
            <i className="fa fa-search position-absolute top-50 start-0 translate-middle-y ms-3 text-muted"></i>
            <input 
              type="text" 
              className="form-control rounded-pill border-0 shadow-sm ps-5 pe-4"
              placeholder="Tìm kiếm nhanh sản phẩm..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        {/* Nút xóa bộ lọc */}
        <div className="col-md-3 text-md-end text-center">
          <button 
            className="btn btn-outline-danger rounded-pill px-4 shadow-sm fw-bold"
            onClick={handleClearFilters}
            style={{ transition: 'all 0.3s' }}
          >
            <i className="fa fa-sync-alt me-2"></i> Làm mới
          </button>
        </div>
      </div>

      {/* Hiển thị kết quả */}
      <div className={`row animate__animated animate__fadeIn ${loading ? 'opacity-50' : ''}`}>
        {products.length > 0 ? (
          products.map(p => (
            <div key={p.id || p.productId} className="col-xl-3 col-lg-4 col-md-6 col-6 mb-4">
              <ProductCard product={p} />
            </div>
          ))
        ) : !loading && (
          <div className="text-center py-5">
            <div className="mb-3">
              <i className="fa fa-box-open fa-4x text-light-emphasis"></i>
            </div>
            <h4 className="text-muted fw-light">Không tìm thấy sản phẩm nào phù hợp</h4>
            <button className="btn btn-primary mt-3 rounded-pill" onClick={handleClearFilters}>
              Xem tất cả sản phẩm
            </button>
          </div>
        )}
      </div>

      <style>{`
        .transition-all { transition: all 0.3s ease; }
        .form-select, .form-control { height: 45px; }
        .btn-outline-danger:hover { transform: rotate(-10deg); }
      `}</style>
    </section>
  );
};

export default ProductList;
