import axios from 'axios';

const postService = {
  // Sử dụng NewsAPI (cần đăng ký key miễn phí) hoặc lấy từ nguồn RSS công khai
  getExternalPosts: async () => {
    try {
      // Ví dụ lấy tin tức từ một nguồn mở về sức khỏe/gia đình
      // Ở đây dùng giải pháp fetch trực tiếp từ một nguồn JSON placeholder hoặc RSS-to-JSON
      const response = await axios.get('https://api.rss2json.com/v1/api.json?rss_url=https://vnexpress.net/rss/suc-khoe.rss');
      
      return response.data.items.map(item => ({
        id: item.guid,
        title: item.title,
        date: new Date(item.pubDate).toLocaleDateString('vi-VN'),
        img: item.thumbnail || item.enclosure.link || "https://picsum.photos/400/250",
        desc: item.description.replace(/<[^>]*>?/gm, '').substring(0, 150) + "...",
        link: item.link
      }));
    } catch (error) {
      console.error("Không thể lấy tin tức ngoại:", error);
      return [];
    }
  }
};

export default postService;
