import React, { useState, useEffect, useCallback } from "react";
import categoryService from "../../../services/categoryService";
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  
  const [pageConfig, setPageConfig] = useState({ 
    pageNumber: 0, 
    pageSize: 10, 
    totalPages: 0,
    totalElements: 0
  });

  const fetchData = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const res = await categoryService.getAllCategories(page, pageConfig.pageSize);
      setCategories(res.content || []);
      setPageConfig(prev => ({
        ...prev,
        pageNumber: res.pageNumber || 0,
        totalPages: res.totalPages || 0,
        totalElements: res.totalElements || 0
      }));
    } catch (err) {
      console.error("❌ Lỗi load Category:", err);
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  useEffect(() => { fetchData(0); }, [fetchData]);

  const handleOpenModal = (cat = null) => {
    if (cat) {
      setIsEdit(true);
      setFormData({ categoryId: cat.categoryId, categoryName: cat.categoryName });
    } else {
      setIsEdit(false);
      setFormData({ categoryId: "", categoryName: "" });
    }
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.categoryName.trim().length < 5) {
      return Swal.fire("Lỗi", "Tên danh mục phải có ít nhất 5 ký tự", "error");
    }

    try {
      if (isEdit) {
        await categoryService.updateCategory(formData.categoryId, { categoryName: formData.categoryName });
      } else {
        await categoryService.createCategory({ categoryName: formData.categoryName });
      }
      
      Swal.fire("Thành công!", "Danh mục đã được lưu.", "success");
      setShowModal(false);
      fetchData(pageConfig.pageNumber);
    } catch (err) {
      Swal.fire("Thất bại", err.response?.data?.message || "Lỗi hệ thống", "error");
    }
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "Xóa danh mục này?",
      text: "Các sản phẩm thuộc danh mục này cũng có thể bị ảnh hưởng!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      confirmButtonText: "Xóa luôn"
    });

    if (confirm.isConfirmed) {
      try {
        await categoryService.deleteCategory(id);
        Swal.fire("Đã xóa!", "Danh mục đã bay màu.", "success");
        fetchData(0);
      } catch (err) {
        Swal.fire("Lỗi", "Không thể xóa danh mục này (có thể đang chứa sản phẩm).", "error");
      }
    }
  };

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="m-0 fw-bold text-dark"><i className="bi bi-tags me-2"></i>QUẢN LÝ DANH MỤC</h5>
          <button className="btn btn-dark btn-sm px-3" onClick={() => handleOpenModal()}>+ Thêm danh mục</button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th style={{ width: "10%" }}>ID</th>
                <th>Tên danh mục</th>
                <th className="text-center" style={{ width: "20%" }}>Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-4">Đang tải...</td></tr>
              ) : (
                categories.map(c => (
                  <tr key={c.categoryId}>
                    <td>{c.categoryId}</td>
                    <td className="fw-bold text-secondary">{c.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-link text-primary me-2" onClick={() => handleOpenModal(c)}>Sửa</button>
                      <button className="btn btn-link text-danger" onClick={() => handleDelete(c.categoryId)}>Xóa</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Phân trang */}
        <div className="card-footer bg-white d-flex justify-content-between align-items-center py-3">
          <span className="small text-muted">Tổng cộng: {pageConfig.totalElements} danh mục</span>
          <nav>
            <ul className="pagination pagination-sm mb-0">
              <li className={`page-item ${pageConfig.pageNumber === 0 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchData(pageConfig.pageNumber - 1)}>Trước</button>
              </li>
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => fetchData(i)}>{i + 1}</button>
                </li>
              ))}
              <li className={`page-item ${pageConfig.pageNumber >= pageConfig.totalPages - 1 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchData(pageConfig.pageNumber + 1)}>Sau</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* Modal Thêm/Sửa */}
      {showModal && (
        <div className="modal d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0">
              <form onSubmit={handleSubmit}>
                <div className="modal-header">
                  <h5 className="fw-bold">{isEdit ? "Sửa danh mục" : "Thêm danh mục mới"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label small fw-bold">Tên danh mục</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="Nhập ít nhất 5 ký tự..."
                      value={formData.categoryName}
                      onChange={e => setFormData({ ...formData, categoryName: e.target.value })}
                      required
                    />
                  </div>
                </div>
                <div className="modal-footer border-0">
                  <button type="button" className="btn btn-light btn-sm" onClick={() => setShowModal(false)}>Hủy</button>
                  <button type="submit" className="btn btn-dark btn-sm px-4">Lưu</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
