import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState(""); // State cho thanh tìm kiếm

  const navigate = useNavigate();

  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) { console.error(err); }
      }
    } else {
      setUser(null);
      setUserInfo(null);
    }
  }, []);

  const loadCartCount = useCallback(async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      if (currentUser?.email) {
        const count = await cartService.getCartCount(currentUser.email);
        setCartCount(count);
      }
    } catch (error) { setCartCount(0); }
  }, []);

  useEffect(() => {
    initUser();
    categoryService.getAllCategories().then(data => setCategories(data || []));
    loadCartCount();
    window.addEventListener("authUpdated", initUser);
    window.addEventListener('cartUpdated', loadCartCount);
    return () => {
      window.removeEventListener("authUpdated", initUser);
      window.removeEventListener('cartUpdated', loadCartCount);
    };
  }, [initUser, loadCartCount]);

  // HÀM TÌM KIẾM CHÍNH
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate("/product", { 
        state: { searchKeyword: searchTerm.trim() } 
      });
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    window.dispatchEvent(new Event("authUpdated"));
    navigate("/login");
  };

  return (
    <header className="section-header sticky-top">
      <section className="header-main-orange py-3" style={{backgroundColor: '#ff6a00'}}>
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-2 col-6">
              <Link to="/" className="text-decoration-none text-white">
                <h2 className="fw-bold mb-0">ShopBeAnh</h2>
              </Link>
            </div>

            {/* THANH TÌM KIẾM FIX */}
            <div className="col-lg-6 d-none d-md-block">
              <form onSubmit={handleSearchSubmit}>
                <div className="input-group bg-white rounded-pill px-3 py-1">
                  <input 
                    type="text" 
                    className="form-control border-0 shadow-none" 
                    placeholder="Tìm kiếm sản phẩm..." 
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                  <button type="submit" className="btn btn-link text-warning p-0 border-0">
                    <i className="fa fa-search fa-lg"></i>
                  </button>
                </div>
              </form>
            </div>

            <div className="col-lg-4 col-6 d-flex justify-content-end align-items-center gap-4">
              <div className="dropdown">
                <div className="text-white text-center dropdown-toggle" data-bs-toggle="dropdown" style={{cursor: 'pointer'}}>
                  <i className="fa fa-user-circle fa-lg d-block mb-1"></i>
                  <small>{userInfo?.firstName || user?.email?.split('@')[0] || "Tài khoản"}</small>
                </div>
                <ul className="dropdown-menu dropdown-menu-end shadow border-0 mt-2">
                  {!user ? (
                    <>
                      <li><Link className="dropdown-item" to="/login">Đăng nhập</Link></li>
                      <li><Link className="dropdown-item" to="/register">Đăng ký</Link></li>
                    </>
                  ) : (
                    <>
                      <li><Link className="dropdown-item" to="/profile">Cá nhân</Link></li>
                      <li><button className="dropdown-item text-danger" onClick={handleLogout}>Đăng xuất</button></li>
                    </>
                  )}
                </ul>
              </div>

              <Link to="/cart" className="text-white text-center position-relative text-decoration-none">
                <i className="fa fa-shopping-basket fa-lg d-block mb-1"></i>
                <span className="badge rounded-pill bg-white text-danger position-absolute top-0 start-100 translate-middle-x">
                  {cartCount}
                </span>
                <small>Giỏ hàng</small>
              </Link>
            </div>
          </div>
        </div>
      </section>

      <nav className="bg-white border-bottom d-none d-lg-block">
        <div className="container">
          <ul className="nav">
            <li className="nav-item dropdown">
              <a className="nav-link fw-bold text-dark py-3 dropdown-toggle" href="#" data-bs-toggle="dropdown">
                <i className="fa fa-bars me-2 text-warning"></i>DANH MỤC
              </a>
              <ul className="dropdown-menu border-0 shadow">
                {categories.map(cat => (
                  <li key={cat.id || cat.categoryId}>
                    <button className="dropdown-item" onClick={() => navigate('/product', { state: { selectedCategory: cat.id || cat.categoryId } })}>
                      {cat.categoryName || cat.name}
                    </button>
                  </li>
                ))}
              </ul>
            </li>
          </ul>
        </div>
      </nav>
    </header>
  );
};

export default Header;
