import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import categoryService from "../../../services/categoryService";
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  const navigate = useNavigate();

  const [pageConfig, setPageConfig] = useState({
    pageNumber: 0,
    pageSize: 10,
    totalPages: 0,
    totalElements: 0,
  });

  // ================= CHECK QUY·ªÄN ADMIN =================
  const checkAdminAuth = useCallback(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role"); 

    console.log("üõ°Ô∏è [Auth Check] Token hi·ªán t·∫°i:", token ? "ƒê√£ c√≥" : "Tr·ªëng");
    console.log("üõ°Ô∏è [Auth Check] Role hi·ªán t·∫°i:", role);

    // L∆∞u √Ω: N·∫øu Backend tr·∫£ v·ªÅ "ROLE_ADMIN" th√¨ ph·∫£i check role === "ROLE_ADMIN"
    if (!token || role !== "ADMIN") { 
      console.warn("üö´ [Auth Check] Kh√¥ng ph·∫£i ADMIN, s√∫t ra ngo√†i!");
      return false;
    }
    return true;
  }, []);

  const fetchCategories = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const data = await categoryService.getAllCategories(page, pageConfig.pageSize);
      
      console.log("üìä [Render Check] M·∫£ng content s·∫Ω hi·ªÉn th·ªã:", data.content);
      
      setCategories(data.content || []);
      setPageConfig({
        pageNumber: data.pageNumber || 0,
        pageSize: data.pageSize || 10,
        totalPages: data.totalPages || 0,
        totalElements: data.totalElements || 0,
      });
    } catch (err) {
      console.error("‚ùå [Render Check] L·ªói khi set state:", err);
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  useEffect(() => {
    if (checkAdminAuth()) {
      fetchCategories(0);
    } else {
      Swal.fire("L·ªói", "M√†y kh√¥ng c√≥ quy·ªÅn ADMIN!", "error");
      navigate("/login");
    }
  }, [fetchCategories, checkAdminAuth, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEdit) {
        await categoryService.updateCategory(formData.categoryId, { categoryName: formData.categoryName });
      } else {
        await categoryService.createCategory({ categoryName: formData.categoryName });
      }
      setShowModal(false);
      Swal.fire("Th√†nh c√¥ng", "ƒê√£ l∆∞u!", "success");
      fetchCategories(pageConfig.pageNumber);
    } catch (err) {
      console.error("‚ùå [Submit Error] Chi ti·∫øt l·ªói:", err.response?.data);
      Swal.fire("L·ªói", err.response?.data?.message || "Ki·ªÉm tra quy·ªÅn ADMIN/Token", "error");
    }
  };

  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: "X√≥a danh m·ª•c?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "X√≥a"
    });

    if (result.isConfirmed) {
      try {
        await categoryService.deleteCategory(id);
        Swal.fire("ƒê√£ x√≥a!", "", "success");
        fetchCategories(0);
      } catch (err) {
        console.error("‚ùå [Delete Error] L·ªói x√≥a:", err.response?.data);
        Swal.fire("L·ªói", "Kh√¥ng th·ªÉ x√≥a. C√≥ th·ªÉ Token h·∫øt h·∫°n!", "error");
      }
    }
  };

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="mb-0 fw-bold text-primary">QU·∫¢N L√ù DANH M·ª§C</h5>
          <button className="btn btn-dark btn-sm" onClick={() => { setIsEdit(false); setFormData({categoryId: "", categoryName: ""}); setShowModal(true); }}>
            + Th√™m m·ªõi
          </button>
        </div>
        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th width="15%">ID</th>
                <th width="60%">T√™n danh m·ª•c</th>
                <th width="25%" className="text-center">Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-5">ƒêang t·∫£i...</td></tr>
              ) : categories.length > 0 ? (
                categories.map((c) => (
                  <tr key={c.categoryId}>
                    <td className="text-muted">#{c.categoryId}</td>
                    <td className="fw-bold">{c.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-outline-info btn-sm me-2" onClick={() => { setIsEdit(true); setFormData(c); setShowModal(true); }}>S·ª≠a</button>
                      <button className="btn btn-outline-danger btn-sm" onClick={() => handleDelete(c.categoryId)}>X√≥a</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="3" className="text-center py-5 text-danger fw-bold">‚ö†Ô∏è KH√îNG C√ì D·ªÆ LI·ªÜU ƒê·ªÇ HI·ªÇN TH·ªä</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal gi·ªØ nguy√™n nh∆∞ c≈© */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <form onSubmit={handleSubmit}>
                <div className="modal-header">
                  <h5 className="fw-bold">{isEdit ? "C·∫≠p nh·∫≠t" : "Th√™m m·ªõi"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body p-4">
                  <label className="form-label small fw-bold text-muted">T√™n danh m·ª•c</label>
                  <input type="text" className="form-control" value={formData.categoryName} onChange={e => setFormData({...formData, categoryName: e.target.value})} required />
                </div>
                <div className="modal-footer">
                  <button type="submit" className="btn btn-primary w-100 fw-bold">X√ÅC NH·∫¨N L∆ØU</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
