// src/pages/ProductList.jsx
import React, { useEffect, useState } from "react";
import productService from "../../services/productService";
import categoryService from "../services/categoryService";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  // üß† B·ªô l·ªçc
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("");

  // üîπ G·ªçi API s·∫£n ph·∫©m & danh m·ª•c
  useEffect(() => {
    const fetchData = async () => {
      try {
        const productData = await productService.getAllProducts();
        const categoryData = await categoryService.getAllCategories();

        setProducts(Array.isArray(productData) ? productData : []);
        setFilteredProducts(Array.isArray(productData) ? productData : []);
        setCategories(Array.isArray(categoryData) ? categoryData : []);
      } catch (error) {
        console.error("‚ùå L·ªói khi t·∫£i d·ªØ li·ªáu:", error);
      }
    };
    fetchData();
  }, []);

  // üîç H√†m l·ªçc s·∫£n ph·∫©m
  useEffect(() => {
    let filtered = [...products];

    // L·ªçc theo danh m·ª•c
    if (selectedCategory) {
      filtered = filtered.filter(
        (p) => p.category?.id === parseInt(selectedCategory)
      );
    }

    // L·ªçc theo t√™n s·∫£n ph·∫©m
    if (searchTerm) {
      filtered = filtered.filter((p) =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // S·∫Øp x·∫øp
    if (sortOrder === "priceAsc") {
      filtered.sort((a, b) => a.price - b.price);
    } else if (sortOrder === "priceDesc") {
      filtered.sort((a, b) => b.price - a.price);
    }

    setFilteredProducts(filtered);
  }, [selectedCategory, searchTerm, sortOrder, products]);

  return (
    <div className="product-page" style={{ padding: "20px" }}>
      <h2>üõçÔ∏è Danh s√°ch s·∫£n ph·∫©m</h2>

      {/* üéØ B·ªô l·ªçc */}
      <div
        className="filters"
        style={{
          display: "flex",
          gap: "10px",
          marginBottom: "20px",
          alignItems: "center",
        }}
      >
        {/* T√¨m ki·∫øm */}
        <input
          type="text"
          placeholder="üîé T√¨m theo t√™n s·∫£n ph·∫©m..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "8px", flex: 1 }}
        />

        {/* L·ªçc theo danh m·ª•c */}
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          style={{ padding: "8px" }}
        >
          <option value="">T·∫•t c·∫£ danh m·ª•c</option>
          {categories.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>

        {/* S·∫Øp x·∫øp */}
        <select
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
          style={{ padding: "8px" }}
        >
          <option value="">M·∫∑c ƒë·ªãnh</option>
          <option value="priceAsc">Gi√° tƒÉng d·∫ßn</option>
          <option value="priceDesc">Gi√° gi·∫£m d·∫ßn</option>
        </select>
      </div>

      {/* üì¶ Hi·ªÉn th·ªã danh s√°ch */}
      <div
        className="product-grid"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
          gap: "20px",
        }}
      >
        {filteredProducts.length > 0 ? (
          filteredProducts.map((p) => (
            <div
              key={p.id}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "10px",
                textAlign: "center",
              }}
            >
              <img
                src={p.image || "https://via.placeholder.com/150"}
                alt={p.name}
                style={{ width: "100%", borderRadius: "8px" }}
              />
              <h4>{p.name}</h4>
              <p>{p.category?.name}</p>
              <p style={{ color: "red", fontWeight: "bold" }}>
                {p.price?.toLocaleString()}‚Ç´
              </p>
            </div>
          ))
        ) : (
          <p>Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m ph√π h·ª£p.</p>
        )}
      </div>
    </div>
  );
};

export default ProductList;
