import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const ProductListAD = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]); // L∆∞u danh m·ª•c ƒë·ªÉ ch·ªçn khi th√™m/s·ª≠a
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  
  // State cho Form
  const [formData, setFormData] = useState({
    productId: "",
    productName: "",
    price: "",
    discount: 0,
    quantity: 1,
    description: "",
    categoryId: ""
  });
  const [selectedFile, setSelectedFile] = useState(null);

  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0 });

  const token = localStorage.getItem("jwt-token");

  // 1. L·∫•y danh s√°ch s·∫£n ph·∫©m & danh m·ª•c
  const fetchData = useCallback(async (page = 0) => {
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      const [prodRes, catRes] = await Promise.all([
        axios.get(`http://localhost:8080/api/admin/products?pageNumber=${page}&pageSize=10`, config),
        axios.get("http://localhost:8080/api/public/categories", config)
      ]);

      setProducts(prodRes.data.content);
      setPageConfig({ pageNumber: prodRes.data.pageNumber, totalPages: prodRes.data.totalPages });
      setCategories(catRes.data.content || catRes.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  }, [token]);

  useEffect(() => { fetchData(); }, [fetchData]);

  // 2. X·ª≠ l√Ω m·ªü Modal (Th√™m/S·ª≠a)
  const openModal = (product = null) => {
    if (product) {
      setIsEdit(true);
      setFormData({ ...product, categoryId: product.category?.categoryId || "" });
    } else {
      setIsEdit(false);
      setFormData({ productName: "", price: "", discount: 0, quantity: 1, description: "", categoryId: "" });
    }
    setSelectedFile(null);
    setShowModal(true);
  };

  // 3. G·ª≠i d·ªØ li·ªáu (Save/Update)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      let savedProduct;

      if (isEdit) {
        // C·∫≠p nh·∫≠t th√¥ng tin text
        const res = await axios.put(`http://localhost:8080/api/admin/products/${formData.productId}`, formData, config);
        savedProduct = res.data;
      } else {
        // Th√™m m·ªõi
        const res = await axios.post(`http://localhost:8080/api/admin/categories/${formData.categoryId}/product`, formData, config);
        savedProduct = res.data;
      }

      // N·∫øu c√≥ ch·ªçn ·∫£nh m·ªõi th√¨ upload ·∫£nh
      if (selectedFile) {
        const imageForm = new FormData();
        imageForm.append("image", selectedFile);
        await axios.put(`http://localhost:8080/api/admin/products/${savedProduct.productId}/image`, imageForm, {
          headers: { ...config.headers, "Content-Type": "multipart/form-data" }
        });
      }

      Swal.fire("Th√†nh c√¥ng!", "D·ªØ li·ªáu ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t.", "success");
      setShowModal(false);
      fetchData(pageConfig.pageNumber);
    } catch (err) {
