import React from "react";
import { Routes, Route, Link, useNavigate, useLocation } from "react-router-dom";
import { LayoutDashboard, Box, Tags, Users, ShoppingBag, LogOut, Bell, UserCircle } from "lucide-react";
import Dashboard from "./Dashboard";
import ProductListAD from "./products/ProductListAD";
import CategoryListAD from "./categories/CategoryListAD";

const AdminLayout = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    localStorage.clear();
    navigate("/admin/login");
  };

  // H√†m check active link ƒë·ªÉ ƒë·ªïi m√†u menu
  const isActive = (path) => location.pathname === path;

  return (
    <div style={containerStyle}>
      {/* 1. SIDEBAR - ƒê·∫≠m ch·∫•t c√¥ng ngh·ªá */}
      <aside style={sidebarStyle}>
        <div style={logoWrapper}>
          <div style={logoIcon}>üíÑ</div>
          <h4 style={logoText}>COSMETIC <span style={{color: '#3498db'}}>AD</span></h4>
        </div>
        
        <nav style={navStyle}>
          <p style={menuLabel}>CH√çNH</p>
          <MenuLink to="/admin" icon={<LayoutDashboard size={18}/>} text="Dashboard" active={isActive("/admin")} />
          
          <p style={menuLabel}>QU·∫¢N L√ù KHO</p>
          <MenuLink to="/admin/products" icon={<Box size={18}/>} text="S·∫£n ph·∫©m" active={isActive("/admin/products")} />
          <MenuLink to="/admin/categories" icon={<Tags size={18}/>} text="Danh m·ª•c" active={isActive("/admin/categories")} />
          
          <p style={menuLabel}>KH√ÅCH H√ÄNG & ƒê∆†N</p>
          <MenuLink to="/admin/orders" icon={<ShoppingBag size={18}/>} text="ƒê∆°n h√†ng" active={isActive("/admin/orders")} />
          <MenuLink to="/admin/users" icon={<Users size={18}/>} text="Kh√°ch h√†ng" active={isActive("/admin/users")} />
        </nav>

        <div style={footerSidebar}>
          <button onClick={handleLogout} style={logoutButtonStyle}>
            <LogOut size={18} /> <span>ƒêƒÉng xu·∫•t</span>
          </button>
        </div>
      </aside>

      {/* 2. MAIN CONTENT AREA */}
      <div style={mainContentWrapper}>
        
        {/* Header - Glassmorphism nh·∫π */}
        <header style={headerStyle}>
          <div style={breadcrumb}>
            Trang qu·∫£n tr·ªã / <span style={{color: '#2c3e50', fontWeight: 600}}>
              {location.pathname.split('/').pop().toUpperCase() || 'DASHBOARD'}
            </span>
          </div>
          
          <div style={headerRight}>
            <div style={iconAction}><Bell size={20} /></div>
            <div style={userProfile}>
              <div style={userInfo}>
                <span style={userName}>Admin Pro</span>
                <span style={userRole}>Ch·ªß c·ª≠a h√†ng</span>
              </div>
              <UserCircle size={35} color="#2c3e50" />
            </div>
          </div>
        </header>

        {/* Scrollable Main */}
        <main style={scrollableArea}>
          <div style={innerContent}>
            <Routes>
              <Route path="" element={<Dashboard />} />
              <Route path="products" element={<ProductListAD />} />
              <Route path="categories" element={<CategoryListAD />} />
              <Route path="orders" element={<div className="p-4 card border-0 shadow-sm">üöÄ T√≠nh nƒÉng ƒê∆°n h√†ng ƒëang ho√†n thi·ªán...</div>} />
              <Route path="users" element={<div className="p-4 card border-0 shadow-sm">üë• T√≠nh nƒÉng Kh√°ch h√†ng ƒëang ho√†n thi·ªán...</div>} />
            </Routes>
          </div>
        </main>
      </div>
    </div>
  );
};

// Component con cho link menu cho s·∫°ch code
const MenuLink = ({ to, icon, text, active }) => (
  <Link to={to} style={{
    ...linkStyle,
    backgroundColor: active ? "rgba(52, 152, 219, 0.15)" : "transparent",
    color: active ? "#3498db" : "#bdc3c7",
    borderLeft: active ? "4px solid #3498db" : "4px solid transparent",
  }}>
    <span style={{ marginRight: "12px", display: "flex" }}>{icon}</span>
    {text}
  </Link>
);

// ================= STYLES (ƒê√É FIX K·ªäCH TR·∫¶N) =================
const containerStyle = { display: "flex", height: "100vh", width: "100vw", overflow: "hidden", backgroundColor: "#f0f2f5" };

const sidebarStyle = {
  width: "260px", backgroundColor: "#1a222b", display: "flex", flexDirection: "column", flexShrink: 0,
  boxShadow: "4px 0 10px rgba(0,0,0,0.1)", zIndex: 10
};

const logoWrapper = { padding: "25px 20px", display: "flex", alignItems: "center", gap: "12px", borderBottom: "1px solid rgba(255,255,255,0.05)" };
const logoIcon = { fontSize: "24px", background: "#fff", borderRadius: "8px", padding: "5px" };
const logoText = { color: "#fff", margin: 0, fontSize: "1.1rem", fontWeight: "800", letterSpacing: "1px" };

const navStyle = { flex: 1, padding: "20px 0", display: "flex", flexDirection: "column" };
const menuLabel = { color: "#566573", fontSize: "11px", fontWeight: "700", padding: "15px 25px 5px", margin: 0, letterSpacing: "1px" };

const linkStyle = {
  textDecoration: "none", padding: "12px 25px", fontSize: "14px", fontWeight: "500",
  display: "flex", alignItems: "center", transition: "all 0.2s ease"
};

const footerSidebar = { padding: "20px", borderTop: "1px solid rgba(255,255,255,0.05)" };
const logoutButtonStyle = {
  width: "100%", padding: "10px", backgroundColor: "rgba(231, 76, 60, 0.1)", color: "#e74c3c",
  border: "none", borderRadius: "8px", cursor: "pointer", fontWeight: "600", display: "flex", alignItems: "center", justifyContent: "center", gap: "10px"
};

const mainContentWrapper = { flex: 1, display: "flex", flexDirection: "column", minWidth: 0, position: "relative" };

const headerStyle = {
  height: "70px", backgroundColor: "rgba(255,255,255,0.95)", backdropFilter: "blur(10px)",
  display: "flex", alignItems: "center", justifyContent: "space-between", padding: "0 30px",
  boxShadow: "0 1px 4px rgba(0,0,0,0.05)", zIndex: 9, flexShrink: 0
};

const headerRight = { display: "flex", alignItems: "center", gap: "25px" };
const iconAction = { color: "#95a5a6", cursor: "pointer", position: "relative" };
const userProfile = { display: "flex", alignItems: "center", gap: "12px", borderLeft: "1px solid #eee", paddingLeft: "25px" };
const userInfo = { display: "flex", flexDirection: "column", textAlign: "right" };
const userName = { fontSize: "14px", fontWeight: "700", color: "#2c3e50" };
const userRole = { fontSize: "11px", color: "#95a5a6" };
