import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";
import Swal from 'sweetalert2';

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [suggestedProducts, setSuggestedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);

  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);
        // 1. Lấy chi tiết sản phẩm
        const productData = await productService.getProductById(productId);
        setProduct(productData);

        // 2. Giả lập lấy sản phẩm liên quan (Cùng danh mục)
        const related = await productService.getProductsByCategory(productData.categoryId || 1);
        setRelatedProducts(related.content.filter(p => p.productId !== parseInt(productId)).slice(0, 4));

        // 3. Giả lập lấy sản phẩm gợi ý (Bán chạy hoặc ngẫu nhiên)
        const suggested = await productService.getAllProducts(0, 4);
        setSuggestedProducts(suggested.content.slice(0, 4));

      } catch (err) {
        setError(`Lỗi: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
    window.scrollTo(0, 0); // Cuộn lên đầu trang khi đổi ID sản phẩm
  }, [productId]);

  const handleAddToCart = async () => {
    if (!currentUser) {
      Swal.fire({ title: 'Yêu cầu đăng nhập', icon: 'warning', showCancelButton: true, confirmButtonText: 'Đăng nhập' })
          .then((result) => result.isConfirmed && navigate('/login'));
      return;
    }

    try {
      setAddingToCart(true);
      let cart = await cartService.getActiveCart(currentUser.email);
      await cartService.addToCart(cart.cartId, productId, 1);
      Swal.fire({ icon: 'success', title: 'Thành công', text: 'Đã thêm vào giỏ hàng!', timer: 1500 });
    } catch (error) {
      Swal.fire({ icon: 'error', title: 'Lỗi', text: error.message });
    } finally {
      setAddingToCart(false);
    }
  };

  if (loading) return <div className="text-center mt-5"><div className="spinner-border text-primary"></div></div>;

  const productName = product?.productName || product?.product_name;

  return (
    <div className="container mt-4 mb-5">
      {/* SECTION 1: THÔNG TIN CHI TIẾT SẢN PHẨM */}
      <nav aria-label="breadcrumb"><ol className="breadcrumb"><li className="breadcrumb-item"><Link to="/">Trang chủ</Link></li><li className="breadcrumb-item active">{productName}</li></ol></nav>

      <div className="row g-4 bg-white p-3 rounded shadow-sm">
        <div className="col-md-5">
          <img src={`${IMAGE_BASE_URL}/${product.image}`} alt={productName} className="img-fluid rounded border" style={{ width: '100%', height: '400px', objectFit: 'contain' }} />
        </div>
        <div className="col-md-7 ps-md-4">
          <h1 className="h3 fw-bold">{productName}</h1>
          <div className="text-warning mb-2 small"><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-half"></i> <span className="text-muted">(4.5 đánh giá)</span></div>
          <div className="h2 text-danger fw-bold my-3">{product.special_price?.toLocaleString() || product.price?.toLocaleString()} ₫</div>
          <p className="text-muted small border-top pt-3">{product.description || "Đang cập nhật mô tả..."}</p>
          <div className="mt-4 d-grid gap-2 d-md-flex">
            <button className="btn btn-primary btn-lg px-5" onClick={handleAddToCart} disabled={product.quantity === 0 || addingToCart}><i className="bi bi-cart-plus me-2"></i>Thêm vào giỏ</button>
            <button className="btn btn-outline-danger btn-lg px-4"><i className="bi bi-heart"></i></button>
          </div>
        </div>
      </div>

      <hr className="my-5" />

      {/* SECTION 2: ĐÁNH GIÁ TỪ KHÁCH HÀNG */}
      <div className="row mt-5">
        <div className="col-12"><h4 className="fw-bold mb-4">Khách hàng đánh giá</h4></div>
        <div className="col-md-4 text-center border-end">
          <h1 className="display-4 fw-bold text-primary">4.5</h1>
          <div className="text-warning mb-2"><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-half"></i></div>
          <p className="text-muted small">Dựa trên 128 đánh giá</p>
        </div>
        <div className="col-md-8 ps-md-5">
          <div className="review-item mb-4 border-bottom pb-3">
            <h6 className="fw-bold mb-1">Nguyễn Văn A <span className="badge bg-success ms-2 small" style={{fontSize: '10px'}}>Đã mua hàng</span></h6>
            <div className="text-warning small mb-2"><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i></div>
            <p className="small mb-0">Sản phẩm rất tốt, đóng gói cẩn thận, giao hàng nhanh. Sẽ ủng hộ shop dài dài.</p>
          </div>
          <div className="review-item mb-3 border-bottom pb-3">
            <h6 className="fw-bold mb-1">Trần Thị B</h6>
            <div className="text-warning small mb-2"><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star-fill"></i><i className="bi bi-star"></i></div>
            <p className="small mb-0">Mùi thơm nhẹ, dùng khá mướt tóc. Giá cả hợp lý.</p>
          </div>
          <button className="btn btn-sm btn-outline-secondary">Xem thêm đánh giá</button>
        </div>
      </div>

      <hr className="my-5" />

      {/* SECTION 3: SẢN PHẨM LIÊN QUAN (Cùng category) */}
      <div className="mt-5">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h4 className="fw-bold">Sản phẩm liên quan</h4>
          <Link to="/products" className="text-decoration-none small">Xem tất cả</Link>
        </div>
        <div className="row row-cols-2 row-cols-md-4 g-3">
          {relatedProducts.map(item => (
            <ProductCard key={item.productId} product={item} />
          ))}
        </div>
      </div>

      {/* SECTION 4: GỢI Ý CHO BẠN */}
      <div className="mt-5 bg-light p-4 rounded">
        <h4 className="fw-bold mb-4 text-center">Có thể bạn sẽ thích</h4>
        <div className="row row-cols-2 row-cols-md-4 g-3">
          {suggestedProducts.map(item => (
            <ProductCard key={item.productId} product={item} />
          ))}
        </div>
      </div>
    </div>
  );
};

// COMPONENT CON: Card sản phẩm để tái sử dụng
const ProductCard = ({ product }) => (
  <div className="col">
    <div className="card h-100 border-0 shadow-sm hover-shadow transition">
      <Link to={`/products/${product.productId}`}>
        <img src={`${IMAGE_BASE_URL}/${product.image}`} className="card-img-top p-2" alt={product.productName} style={{ height: '180px', objectFit: 'contain' }} />
      </Link>
      <div className="card-body p-2 text-center">
        <Link to={`/products/${product.productId}`} className="text-decoration-none text-dark">
          <h6 className="card-title small fw-bold text-truncate">{product.productName || product.product_name}</h6>
        </Link>
        <div className="text-danger fw-bold small">{product.special_price?.toLocaleString() || product.price?.toLocaleString()} ₫</div>
      </div>
    </div>
  </div>
);

export default ProductDetail;
