import api from "./api";

const bannerService = {
  // Lấy tất cả banner
  async getAllBanners() {
    try {
      const { data } = await api.get('/public/banners');
      return data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy danh sách banner:', error);
      throw error;
    }
  },

  // Lấy banner đang hoạt động
  async getActiveBanners() {
    try {
      const { data } = await api.get('/public/banners/active');
      return data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy banner active:', error);
      throw error;
    }
  },

  // Lấy banner theo ID
  async getBannerById(bannerId) {
    try {
      const { data } = await api.get(`/public/banners/${bannerId}`);
      return data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy banner:', error);
      throw error;
    }
  }
};

export default bannerService;
