import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobileNumber: "",
  });
  const [originalForm, setOriginalForm] = useState({});
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [userId, setUserId] = useState(null);
  const [hasChanges, setHasChanges] = useState(false);

  // ‚úÖ KI·ªÇM TRA AUTH V√Ä LOAD D·ªÆ LI·ªÜU
  useEffect(() => {
    const loadUserData = async () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return;
        }

        const user = JSON.parse(userData);
        const userId = user.userId;

        console.log("üë§ User ID from localStorage:", userId);

        if (userId === undefined || userId === null) {
          alert("‚ö†Ô∏è Kh√¥ng t√¨m th·∫•y th√¥ng tin ng∆∞·ªùi d√πng!");
          navigate("/login");
          return;
        }

        setUserId(userId);
        setLoading(true);

        // ‚úÖ LU√îN G·ªåI API ƒê·ªÇ L·∫§Y D·ªÆ LI·ªÜU M·ªöI NH·∫§T
        console.log("üì° Loading user data from API for ID:", userId);
        const userProfile = await userService.getUserById(userId);
        console.log("‚úÖ User data loaded from API:", userProfile);

        const userFormData = {
          firstName: userProfile.firstName || user.firstName || "",
          lastName: userProfile.lastName || user.lastName || "",
          email: userProfile.email || user.email || "",
          mobileNumber: userProfile.mobileNumber || user.mobileNumber || ""
        };

        setForm(userFormData);
        setOriginalForm(userFormData); // L∆∞u d·ªØ li·ªáu g·ªëc ƒë·ªÉ so s√°nh

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);

        // Fallback to localStorage data
        const user = JSON.parse(localStorage.getItem("user") || "{}");
        const userFormData = {
          firstName: user.firstName || "",
          lastName: user.lastName || "",
          email: user.email || "",
          mobileNumber: user.mobileNumber || ""
        };

        setForm(userFormData);
        setOriginalForm(userFormData);
        alert("‚ö†Ô∏è Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu t·ª´ server. S·ª≠ d·ª•ng d·ªØ li·ªáu t·∫°m th·ªùi.");
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  // ‚úÖ KI·ªÇM TRA THAY ƒê·ªîI
  useEffect(() => {
    const changed = JSON.stringify(form) !== JSON.stringify(originalForm);
    setHasChanges(changed);
  }, [form, originalForm]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ‚úÖ Validation
    if (!form.firstName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.lastName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    // ‚úÖ KI·ªÇM TRA C√ì THAY ƒê·ªîI KH√îNG
    if (!hasChanges) {
      alert("‚ÑπÔ∏è Kh√¥ng c√≥ thay ƒë·ªïi n√†o ƒë·ªÉ c·∫≠p nh·∫≠t!");
      return;
    }

    setSaving(true);

    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      const currentUserId = userData.userId;

      console.log("üíæ Updating user data for ID:", currentUserId);

      // ‚úÖ Chu·∫©n b·ªã d·ªØ li·ªáu update
      const updateData = {
        firstName: form.firstName.trim(),
        lastName: form.lastName.trim(),
        email: form.email.trim(),
        mobileNumber: form.mobileNumber.trim()
      };

      console.log("üì§ Sending update data:", updateData);

      // ‚úÖ G·ªçi API update
      const response = await userService.updateUser(currentUserId, updateData);
      console.log("‚úÖ UPDATE SUCCESS:", response);

      // ‚úÖ C·∫¨P NH·∫¨T LOCALSTORAGE V·ªöI D·ªÆ LI·ªÜU M·ªöI
      const updatedUser = { 
        ...userData,
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      // ‚úÖ C·∫¨P NH·∫¨T ORIGINAL FORM ƒê·ªÇ RESET TR·∫†NG TH√ÅI
      setOriginalForm(form);

      alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");

      // ‚úÖ KH√îNG C·∫¶N ƒêƒÇNG NH·∫¨P L·∫†I - QUAY V·ªÄ TRANG TR∆Ø·ªöC
      navigate(-1);

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      let errorMessage = "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t th√¥ng tin";

      if (error.response) {
        const status = error.response.status;
        const data = error.response.data;

        console.log("üö® API Error:", { status, data });

        switch (status) {
          case 500:
            errorMessage = "L·ªói server. Vui l√≤ng th·ª≠ l·∫°i sau.";
            break;
          case 404:
            errorMessage = "Kh√¥ng t√¨m th·∫•y ng∆∞·ªùi d√πng";
            break;
          case 401:
