import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import { Eye, Package, CreditCard, MapPin, Calendar, User, Clock, CheckCircle, Truck, XCircle } from "lucide-react";
import Swal from "sweetalert2";

const OrderListAD = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0, totalElements: 0 });

  const fetchOrders = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const res = await api.get(`/admin/orders?pageNumber=${page}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(res.data.content || []);
      setPageConfig({
        pageNumber: res.data.pageNumber,
        totalPages: res.data.totalPages,
        totalElements: res.data.totalElements
      });
    } catch (err) {
      console.error("Lỗi tải đơn hàng:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { fetchOrders(0); }, [fetchOrders]);

const handleViewOrder = async (orderId) => {
  try {
    const token = localStorage.getItem("token");
    const res = await api.get(`/admin/debug/orders/${orderId}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setSelectedOrder(res.data);
    setShowModal(true);
  } catch (err) {
    alert("Không thể lấy chi tiết đơn hàng!");
  }
};

const updateStatus = async (orderId, newStatus) => {
  try {
    const token = localStorage.getItem("token");
    // Sửa param thành orderStatus cho khớp với @RequestParam bên Spring
    await api.put(`/admin/orders/${orderId}/status?orderStatus=${newStatus}`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });
    Swal.fire("Thành công", `Đơn hàng đã chuyển sang: ${newStatus}`, "success");
    fetchOrders(pageConfig.pageNumber);
    setShowModal(false);
  } catch (err) {
    Swal.fire("Lỗi", "Không thể cập nhật trạng thái đơn hàng", "error");
  }
};

  const getStatusBadge = (status) => {
    const statuses = {
      'PENDING': 'bg-warning text-dark',
      'SHIPPED': 'bg-info text-white',
      'DELIVERED': 'bg-success text-white',
      'CANCELLED': 'bg-danger text-white'
    };
    return <span className={`badge rounded-pill ${statuses[status] || 'bg-secondary'}`}>{status}</span>;
  };

  return (
    <div className="card border-0 shadow-sm text-start">
      <div className="card-header bg-white py-3">
        <h5 className="mb-0 fw-bold text-uppercase text-primary"><Package className="me-2"/>Quản lý đơn hàng</h5>
      </div>

      <div className="table-responsive">
        <table className="table table-hover align-middle mb-0">
          <thead className="bg-light small">
            <tr>
              <th className="ps-4">ID</th>
              <th>Khách hàng</th>
              <th>Ngày đặt</th>
              <th>Tổng tiền</th>
              <th>Trạng thái</th>
              <th className="text-center">Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6" className="text-center py-5">Đang tải...</td></tr>
            ) : orders.map(o => (
              <tr key={o.orderId}>
                <td className="ps-4 fw-bold">#{o.orderId}</td>
                <td className="small">{o.email}</td>
                <td className="small">{o.orderDate}</td>
                <td className="fw-bold text-danger">{o.totalAmount?.toLocaleString()}đ</td>
                <td>{getStatusBadge(o.orderStatus)}</td>
                <td className="text-center">
                  <button className="btn btn-sm btn-outline-primary" onClick={() => handleViewOrder(o.orderId)}>
                    <Eye size={16}/>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

{/* MODAL CHI TIẾT */}
{/* MODAL CHI TIẾT */}
{showModal && selectedOrder && (
  <div className="modal d-block" style={{ background: 'rgba(0,0,0,0.6)', zIndex: 1050 }}>
    <div className="modal-dialog modal-lg modal-dialog-centered">
      <div className="modal-content border-0 shadow-lg">
        <div className="modal-header bg-dark text-white p-3">
          <h5 className="modal-title fw-bold"><Package className="me-2"/> Đơn hàng #{selectedOrder.orderId}</h5>
          <button type="button" className="btn-close btn-close-white" onClick={() => setShowModal(false)}></button>
        </div>
        
        <div className="modal-body p-4 text-start">
          <div className="row g-3 mb-4 bg-light p-3 rounded shadow-sm">
            <DetailBox icon={<User size={16}/>} label="Khách hàng" value={selectedOrder.email} />
            <DetailBox icon={<Calendar size={16}/>} label="Ngày đặt" value={selectedOrder.orderDate} />
            <DetailBox icon={<MapPin size={16}/>} label="Địa chỉ" value={selectedOrder.shippingAddress} />
          </div>

          <h6 className="fw-bold text-uppercase mb-3" style={{letterSpacing: '1px'}}>Danh sách sản phẩm</h6>
          <div className="table-responsive border rounded">
            <table className="table table-hover align-middle mb-0">
              <thead className="table-secondary small text-uppercase">
                <tr>
                  <th className="ps-3">Sản phẩm</th>
                  <th className="text-center">SL</th>
                  <th className="text-center">Đơn giá</th>
                  <th className="text-end pe-3">Thành tiền</th>
                </tr>
              </thead>
              <tbody>
                {selectedOrder.orderItems?.map((item, i) => (
                  <tr key={i}>
                    <td className="ps-3 py-3">
                      <div className="d-flex align-items-center gap-3">
                        <img 
                          src={`http://localhost:8080/api/public/products/image/${item.image}`} 
                          alt="" 
                          style={{ width: '50px', height: '50px', objectFit: 'cover' }} 
                          className="rounded border"
                          onError={(e) => e.target.src = 'https://placehold.co/50?text=No+Img'} 
                        />
                        <div>
                          {/* HIỂN THỊ TÊN SẢN PHẨM Ở ĐÂY */}
                          <div className="fw-bold text-primary">{item.productName}</div>
                          <div className="text-muted" style={{fontSize: '11px'}}>Mã SP: {item.productId}</div>
                        </div>
                      </div>
                    </td>
                    <td className="text-center fw-bold">x{item.quantity}</td>
                    <td className="text-center text-muted">{item.orderedProductPrice?.toLocaleString()}đ</td>
                    <td className="text-end pe-3 fw-bold">
                      {(item.quantity * item.orderedProductPrice)?.toLocaleString()}đ
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="d-flex flex-column align-items-end mt-4 pt-3 border-top">
             <div className="text-muted small">Tổng giá trị: <span className="fw-bold">{selectedOrder.totalAmount?.toLocaleString()}đ</span></div>
             <div className="h4 fw-bold text-danger mt-1">THANH TOÁN: {selectedOrder.totalAmount?.toLocaleString()}đ</div>
          </div>

          <div className="d-flex gap-2 justify-content-center mt-4">
             <button className="btn btn-outline-info" onClick={() => updateStatus(selectedOrder.orderId, 'SHIPPED')}><Truck size={18}/> Giao hàng</button>
             <button className="btn btn-outline-success" onClick={() => updateStatus(selectedOrder.orderId, 'DELIVERED')}><CheckCircle size={18}/> Hoàn tất</button>
             <button className="btn btn-outline-danger" onClick={() => updateStatus(selectedOrder.orderId, 'CANCELLED')}><XCircle size={18}/> Hủy đơn</button>
          </div>
        </div>
      </div>
    </div>
  </div>
)}
    </div>
  );
};

const DetailBox = ({ icon, label, value }) => (
  <div className="col-md-4">
    <div className="p-2 bg-light rounded shadow-sm h-100">
      <div className="d-flex align-items-center gap-1 text-muted fw-bold" style={{fontSize: '10px'}}>
        {icon} {label.toUpperCase()}
      </div>
      <div className="small fw-semibold text-truncate">{value}</div>
    </div>
  </div>
);

export default OrderListAD;
