import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";

// ‚úÖ CSS & h√¨nh ·∫£nh
import "bootstrap/dist/css/bootstrap.min.css";
import "../assets/fonts/fontawesome/css/all.min.css";
import logo from "../assets/images/logo.png";
import flagVN from "../assets/images/icons/flags/VN.webp";

const Header = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ‚úÖ L·∫•y userId t·ª´ localStorage v√† fetch th√¥ng tin user
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (!storedUser) return;

    try {
      const parsedUser = JSON.parse(storedUser);
      const userId = parsedUser.userId || parsedUser.user_id;

      if (userId) {
        setLoading(true);
        userService
          .getUserById(userId)
          .then((data) => {
            console.log("‚úÖ User info loaded:", data);
            setUserInfo(data);
          })
          .catch((err) => {
            console.error("‚ùå Kh√¥ng th·ªÉ l·∫•y user:", err);
            setUserInfo(parsedUser); // fallback n·∫øu l·ªói
          })
          .finally(() => setLoading(false));
      }
    } catch (error) {
      console.error("‚ùå Parse localStorage user l·ªói:", error);
    }
  }, []);

  // ‚úÖ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    navigate("/login");
  };

  // ‚úÖ Hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng
  const renderUserName = () => {
    if (loading) return "Loading...";

    if (userInfo) {
      const firstName = userInfo.firstName || userInfo.first_name || "";
      const lastName = userInfo.lastName || userInfo.last_name || "";
      const fullName = `${firstName} ${lastName}`.trim();

      if (fullName) return fullName;
      if (userInfo.email) return userInfo.email;
    }

    return "My profile";
  };

  // ‚úÖ Hi·ªÉn th·ªã l·ªùi ch√†o
  const renderGreeting = () => {
    if (!userInfo) return null;
    const firstName = userInfo.firstName || userInfo.first_name;
    const email = userInfo.email;

    if (firstName) return `üëã Hi, ${firstName}`;
    if (email) return `üëã Hi, ${email.split("@")[0]}`;
    return "üëã Hi, User";
  };

  return (
    <header className="section-header">
      {/* ===== THANH TR√äN ===== */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          <div className="collapse navbar-collapse show">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {userInfo ? (
                  <span className="nav-link">
                    {renderGreeting()} |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
            </ul>

            <ul className="navbar-nav">
              <li>
                <a href="#" className="nav-link">
                  <img src={flagVN} height="24" alt="flag" /> Ship to
                </a>
              </li>
              <li>
                <a href="#" className="nav-link">
                  <i className="fa fa-bell"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ===== THANH CH√çNH ===== */}
      <div className="container">
        <section className="header-main border-bottom">
          <div className="row row-sm align-items-center">
            {/* Logo */}
            <div className="col-6 col-sm col-md-2 flex-grow-0">
              <Link to="/" className="brand-wrap">
                <img src={logo} alt="Logo" height={56} />
              </Link>
            </div>

            {/* Search bar */}
            <div className="col-lg-6 col-md-5 col-sm-12 flex-grow-1">
              <form className="search-header">
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Search..."
                  />
                </div>
              </form>
            </div>

            {/* Icons b√™n ph·∫£i */}
            <div className="col-xl-4 col-lg-5 col-md-6">
              <div className="widgets-wrap float-md-right">
                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-user"></i>
                    </div>
                    <small className="text">{renderUserName()}</small>
                  </a>
                </div>

                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-shopping-cart"></i>
                    </div>
                    <small className="text">Cart</small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Menu ch√≠nh */}
        <nav className="navbar navbar-main navbar-expand pl-0">
          <ul className="navbar-nav flex-wrap">
            <li className="nav-item">
              <Link to="/" className="nav-link">
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/product" className="nav-link">
                Products
              </Link>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
