import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await authService.login(form);
      console.log("LOGIN RESULT:", res.data);

      // ✅ Hỗ trợ cả 2 trường hợp: có hoặc không có res.data.user
      const userData = res.data.user || res.data || {};
      const token = res.data.token || userData.token || "";

      // ✅ Lưu vào localStorage cho Header đọc
      localStorage.setItem("user", JSON.stringify(userData));
      if (token) localStorage.setItem("token", token);

      alert(`✅ Chào mừng ${userData.first_name || userData.firstName || "bạn"} đã đăng nhập!`);

      // ✅ Quay về trang chủ sau khi đăng nhập
      navigate("/");
      window.location.reload();
    } catch (err) {
      console.error("LOGIN ERROR:", err);
      alert("❌ Đăng nhập thất bại! Vui lòng kiểm tra lại.");
    }
  };

  return (
    <div
      className="card mx-auto"
      style={{
        maxWidth: "400px",
        marginTop: "100px",
        padding: "20px",
        boxShadow: "0 0 10px rgba(0,0,0,0.1)",
        borderRadius: "10px",
      }}
    >
      <div className="card-body">
        <h3 className="card-title mb-4 text-center">Sign In</h3>

        <form onSubmit={handleSubmit}>
          <div className="form-group mb-3">
            <input
              name="email"
              className="form-control"
              placeholder="email..."
              type="email"
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group mb-4">
            <input
              name="password"
              className="form-control"
              placeholder="password..."
              type="password"
              onChange={handleChange}
              required
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary btn-block w-100"
            style={{
              padding: "10px",
              fontWeight: "bold",
              borderRadius: "25px",
            }}
          >
            Login
          </button>
        </form>

        <div className="text-center mt-3">
          <span>Chưa có tài khoản? </span>
          <a href="/register">Đăng ký ngay</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
