import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom"; // Thêm Link
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    remember: true,
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await authService.login({
        email: formData.email,
        password: formData.password
      });

      const loginData = res.data;
      const token = loginData.token || loginData['jwt-token'] || loginData.jwt;

      if (!token) throw new Error("Server không trả về token");
      localStorage.setItem("token", token);

      let userInfo = null;
      let realUserId = null;

      try {
        userInfo = await userService.getUserByEmail(formData.email);
        realUserId = userInfo.user_id || userInfo.userId || userInfo.id;
        if (realUserId) realUserId = parseInt(realUserId);
      } catch (err) { console.warn(err); }

      if (!realUserId) {
        try {
          const payload = JSON.parse(atob(token.split('.')[1]));
          realUserId = parseInt(payload.userId || payload.sub || payload.user_id);
        } catch (err) { console.warn(err); }
      }

      if (!realUserId) throw new Error("Không thể xác định user ID.");

      const userToStore = {
        userId: realUserId,
        email: formData.email,
        firstName: userInfo?.firstName || userInfo?.first_name || "",
        lastName: userInfo?.lastName || userInfo?.last_name || "",
      };

      localStorage.setItem("user", JSON.stringify(userToStore));

      // Sử dụng window.dispatchEvent để Header nhận biết sự thay đổi ngay lập tức
      window.dispatchEvent(new Event("cartUpdated"));

      navigate("/");
    } catch (err) {
      alert("❌ Đăng nhập thất bại: " + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-5 col-lg-4">
            <div className="card login-card">
              {/* Header của Card */}
              <div className="login-header">
                <h3 className="fw-bold mb-1">Chào mừng trở lại!</h3>
                <p className="mb-0 opacity-75">Đăng nhập để tiếp tục mua sắm</p>
              </div>

              <div className="card-body p-4">
                <form onSubmit={handleSubmit}>
                  {/* Email */}
                  <div className="mb-3">
                    <label className="form-label fw-semibold">Email</label>
                    <div className="input-group">
                      <span className="input-group-text bg-light border-end-0">
                        <i className="fa fa-envelope text-muted"></i>
                      </span>
                      <input
                        name="email"
                        type="email"
                        className="form-control border-start-0 ps-0 bg-light"
                        placeholder="example@gmail.com"
                        value={formData.email}
                        onChange={handleChange}
                        required
                        disabled={loading}
                      />
                    </div>
                  </div>

                  {/* Password */}
                  <div className="mb-3">
                    <label className="form-label fw-semibold">Mật khẩu</label>
                    <div className="input-group">
                      <span className="input-group-text bg-light border-end-0">
                        <i className="fa fa-lock text-muted"></i>
                      </span>
                      <input
                        name="password"
                        type="password"
                        className="form-control border-start-0 ps-0 bg-light"
                        placeholder="••••••••"
                        value={formData.password}
                        onChange={handleChange}
                        required
                        disabled={loading}
                      />
                    </div>
                  </div>

                  {/* Remember & Forgot */}
                  <div className="d-flex justify-content-between align-items-center mb-4">
                    <div className="form-check">
                      <input
                        className="form-check-input"
                        type="checkbox"
                        id="rememberMe"
                        name="remember"
                        checked={formData.remember}
                        onChange={handleChange}
                      />
                      <label className="form-check-label small" htmlFor="rememberMe">
                        Ghi nhớ
                      </label>
                    </div>
                    <Link to="/forgot-password" size="small" className="text-decoration-none small text-muted">
                      Quên mật khẩu?
                    </Link>
                  </div>

                  {/* Nút Đăng nhập */}
                  <button 
                    type="submit" 
                    className="btn btn-primary w-100 login-btn shadow-sm mb-3"
                    disabled={loading}
                  >
                    {loading ? (
                      <span className="spinner-border spinner-border-sm"></span>
                    ) : (
                      "ĐĂNG NHẬP"
                    )}
                  </button>

                  {/* Nút Đăng ký */}
                  <div className="text-center mt-4">
                    <p className="mb-0 text-muted">
                      Bạn chưa có tài khoản?{" "}
                      <Link to="/register" className="register-link">
                        Đăng ký ngay
                      </Link>
                    </p>
                  </div>
                </form>
              </div>
            </div>

            <div className="text-center mt-4">
              <Link to="/" className="text-muted text-decoration-none small">
                <i className="fa fa-arrow-left me-1"></i> Quay lại trang chủ
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
