import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [pagination, setPagination] = useState({
    page: 0,
    perPage: 4,
  });
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);

  const totalPages = Math.ceil(total / pagination.perPage);

  // üß© H√†m t·∫£i s·∫£n ph·∫©m c√≥ ph√¢n trang - TH√äM DEBUG CHI TI·∫æT
  const fetchProducts = async (page = 0, perPage = 4) => {
    try {
      setLoading(true);

      console.log("üîÑ === B·∫ÆT ƒê·∫¶U G·ªåI API ===");
      console.log("üì§ G·ª≠i request v·ªõi:", { 
        page, 
        perPage,
        url: `/public/products?page=${page}&limit=${perPage}`
      });

      const response = await productService.getAllProducts(page, perPage);

      console.log("üì• Nh·∫≠n response:", response);
      console.log("üìä Chi ti·∫øt response:", {
        content: response?.content,
        totalElements: response?.totalElements,
        totalPages: response?.totalPages,
        number: response?.number, // Page number t·ª´ backend
        size: response?.size, // Page size t·ª´ backend
        first: response?.first,
        last: response?.last
      });

      const data = response || {};
      const content = data.content || [];
      const totalElements = data.totalElements || 0;

      console.log("üéØ D·ªØ li·ªáu s·∫£n ph·∫©m nh·∫≠n ƒë∆∞·ª£c:", content.map(p => ({
        id: p.productId,
        name: p.productName,
        price: p.price
      })));

      setProducts(content);
      setTotal(totalElements);

    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts(pagination.page, pagination.perPage);
  }, [pagination.page, pagination.perPage]);

  // üß≠ X·ª≠ l√Ω khi chuy·ªÉn trang
  const handlePageChange = (newPage) => {
    console.log("üéØ === CHUY·ªÇN TRANG ===");
    console.log("T·ª´ trang:", pagination.page, "‚Üí Trang m·ªõi:", newPage);

    if (newPage >= 0 && newPage < totalPages) {
      setPagination(prev => ({
        ...prev,
        page: newPage
      }));
    }
  };

  return (
    <section className="padding-bottom container mt-5">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase fw-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
      </header>

      {/* Debug info chi ti·∫øt */}
      <div className="alert alert-warning">
        <strong>üîç DEBUG INFO:</strong><br/>
        - Trang hi·ªán t·∫°i: <strong>{pagination.page}</strong><br/>
        - S·ªë s·∫£n ph·∫©m/trang: {pagination.perPage}<br/>
        - T·ªïng s·∫£n ph·∫©m: {total}<br/>
        - T·ªïng trang: {totalPages}<br/>
        - S·∫£n ph·∫©m hi·ªán t·∫°i: {products.length}
      </div>

      {/* Th√¥ng tin ph√¢n trang */}
      <div className="d-flex justify-content-between align-items-center mb-3">
        <div className="text-muted small">
          {total > 0 ? (
            <>üìÑ Trang {pagination.page + 1} / {totalPages} ‚Ä¢ {products.length} s·∫£n ph·∫©m</>
          ) : (
            "üìÑ ƒêang t·∫£i..."
          )}
        </div>
      </div>

      {/* üß© Hi·ªÉn th·ªã s·∫£n ph·∫©m */}
      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status"></div>
          <p className="mt-2 text-muted">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      ) : (
        <div className="row">
          {products.map((p, index) => {
            const productId = p.productId || p.product_id;

            return (
              <div
                key={productId}
                className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4"
              >
                <div className="card card-product-grid shadow-sm h-100">
                  {/* Th√™m ID s·∫£n ph·∫©m ƒë·ªÉ debug */}
                  <div className="card-header bg-light py-1">
                    <small className="text-muted">
                      ID: <strong>{productId}</strong> | V·ªã tr√≠: {index}
                    </small>
                  </div>

                  <div className="img-wrap">
                    <Link
                      to={`/product/${productId}`}
                      className="text-decoration-none"
                    >
                      <img
                        src={`${IMAGE_BASE_URL}/${p.image}`}
                        alt={p.productName || p.product_name}
                        onError={(e) =>
                          (e.target.src =
                            "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                        }
                        style={{
                          width: "100%",
                          height: "250px",
                          objectFit: "cover",
                          borderRadius: "10px",
                        }}
                      />
                    </Link>
                  </div>

                  <div className="info-wrap p-3 d-flex flex-column">
                    <Link
                      to={`/product/${productId}`}
                      className="text-decoration-none"
                    >
                      <h6 className="text-truncate text-dark fw-bold">
                        {p.productName || p.product_name}
                      </h6>
                    </Link>

                    <p className="text-muted small mb-2 flex-grow-1">
                      {p.description?.substring(0, 40) ||
                        "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£..."}
                    </p>

                    <div className="price-wrapper mb-1">
                      {p.specialPrice ? (
                        <>
                          <span className="price h5 text-danger mb-0">
                            {p.specialPrice?.toLocaleString()} ‚Ç´
                          </span>
                          <span className="text-muted text-decoration-line-through small ms-2">
                            {p.price?.toLocaleString()} ‚Ç´
                          </span>
                        </>
                      ) : (
                        <span className="price h5 text-danger mb-0">
                          {p.price?.toLocaleString()} ‚Ç´
                        </span>
                      )}
                    </div>

                    {p.discount > 0 && (
                      <small className="text-success mt-1">
                        üî• Gi·∫£m {p.discount}%
                      </small>
                    )}

                    <small
                      className={`mt-1 ${
                        p.quantity === 0 ? "text-danger" : "text-success"
                      }`}
                    >
                      {p.quantity === 0
                        ? "‚ùå H·∫øt h√†ng"
                        : `üì¶ C√≤n ${p.quantity} s·∫£n ph·∫©m`}
                    </small>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* üìë PH√ÇN TRANG */}
      {totalPages > 1 && (
        <div className="d-flex justify-content-center align-items-center mt-4">
          <button
            className="btn btn-outline-primary me-2"
            disabled={pagination.page === 0}
            onClick={() => handlePageChange(pagination.page - 1)}
          >
            ‚Üê Tr∆∞·ªõc
          </button>

          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i}
              className={`btn mx-1 ${
                i === pagination.page
                  ? "btn-primary text-white"
                  : "btn-outline-primary"
              }`}
              onClick={() => handlePageChange(i)}
            >
              {i + 1}
            </button>
          ))}

          <button
            className="btn btn-outline-primary ms-2"
            disabled={pagination.page === totalPages - 1}
            onClick={() => handlePageChange(pagination.page + 1)}
          >
            Sau ‚Üí
          </button>
        </div>
      )}
    </section>
  );
};

export default ProductList;
