import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

// Import CSS (Đảm bảo đường dẫn đúng với cấu trúc thư mục của bạn)
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";
import "../assets/css/style.css"; 

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState(""); // State cho ô tìm kiếm

  const navigate = useNavigate();

  // 1. Hàm khởi tạo/cập nhật user từ LocalStorage
  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);

      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) {
          console.error("Lỗi lấy thông tin user:", err);
        }
      }
    } else {
      setUser(null);
      setUserInfo(null);
    }
  }, []);

  // 2. Lấy danh mục sản phẩm
  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } catch (err) {
      console.error("Lỗi danh mục:", err);
    } finally {
      setLoadingCategories(false);
    }
  };

  // 3. Cập nhật số lượng giỏ hàng
  const loadCartCount = useCallback(async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      if (currentUser?.email) {
        const count = await cartService.getCartCount(currentUser.email);
        setCartCount(count);
      } else {
        const localCart = cartService.getCartFromStorage();
        setCartCount(cartService.getTotalItems(localCart?.products || []));
      }
    } catch (error) {
      setCartCount(0);
    }
  }, []);

  // 4. useEffect quản lý sự kiện và dữ liệu ban đầu
  useEffect(() => {
    initUser();
    fetchCategories();
    loadCartCount();

    window.addEventListener("authUpdated", initUser);
    window.addEventListener('cartUpdated', loadCartCount);

    return () => {
      window.removeEventListener("authUpdated", initUser);
      window.removeEventListener('cartUpdated', loadCartCount);
    };
  }, [initUser, loadCartCount]);

  // 5. Xử lý Tìm kiếm
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      // Chuyển hướng sang trang Product và truyền keyword qua state
      navigate("/product", { 
        state: { searchKeyword: searchTerm.trim() } 
      });
    }
  };

  // 6. Xử lý click Danh mục
  const handleCategoryClick = (category) => {
    navigate(`/product`, { 
      state: { 
        selectedCategory: category.categoryId || category.id, 
        categoryName: category.categoryName || category.name 
      } 
    });
  };

  const handleLogout = () => {
    Swal.fire({
      title: 'Bạn muốn đăng xuất?',
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#ff6a00',
      confirmButtonText: 'Đăng xuất',
      cancelButtonText: 'Hủy'
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.removeItem("user");
        localStorage.removeItem("token");
        setUser(null);
        setUserInfo(null);
        setCartCount(0);
        window.dispatchEvent(new Event("authUpdated"));
        navigate("/login");
      }
    });
  };

  const renderUserName = () => {
    if (!user) return "Tài khoản";
    return userInfo?.firstName || user.firstName || user.email?.split('@')[0] || "User";
  };

  return (
    <header className="section-header shadow-sm sticky-top bg-white">
      {/* Top Banner */}
      <div className="top-banner-ads py-1 text-center small text-white" style={{ background: '#222' }}>
        Nội thành Hà Nội - TP. Hồ Chí Minh | GIAO SIÊU TỐC 2H - FREESHIP 0Đ
      </div>

      {/* Main Header */}
      <section className="header-main py-3" style={{ backgroundColor: '#ff6a00' }}>
        <div className="container">
          <div className="row align-items-center">

            {/* Logo */}
            <div className="col-lg-2 col-md-3 col-6">
              <Link to="/" className="text-decoration-none">
                <h2 className="text-white fw-bold mb-0" style={{ letterSpacing: '-1px' }}>ShopBeAnh</h2>
              </Link>
            </div>

            {/* Thanh tìm kiếm */}
            <div className="col-lg-6 col-md-5 d-none d-md-block">
              <form onSubmit={handleSearchSubmit} className="search-bar-wrap">
                <div className="input-group bg-white rounded-pill px-3 py-1">
                  <input 
                    type="text" 
                    className="form-control border-0 shadow-none" 
                    placeholder="Tìm kiếm mỹ phẩm, thương hiệu..." 
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                  <button type="submit" className="btn btn-link text-warning p-0 border-0">
                    <i className="fa fa-search fa-lg"></i>
                  </button>
                </div>
              </form>
            </div>

            {/* Icons Giao diện (User & Cart) */}
            <div className="col-lg-4 col-md-4 col-6 d-flex justify-content-end align-items-center gap-4">

              {/* Dropdown User */}
              <div className="dropdown">
                <div className="text-white text-center dropdown-toggle" data-bs-toggle="dropdown" style={{ cursor: 'pointer' }}>
                  <i className="fa fa-user-circle fa-lg d-block mb-1"></i>
                  <small>{renderUserName()}</small>
                </div>
                <ul className="dropdown-menu dropdown-menu-end shadow border-0 mt-2 rounded-3">
                  {!user ? (
                    <>
                      <li><Link to="/login" className="dropdown-item py-2"><i className="fa fa-sign-in-alt me-2 text-muted"></i> Đăng nhập</Link></li>
                      <li><Link to="/register" className="dropdown-item py-2"><i className="fa fa-user-plus me-2 text-muted"></i> Đăng ký</Link></li>
                    </>
                  ) : (
                    <>
                      <li><Link to="/profile" className="dropdown-item py-2"><i className="fa fa-id-card me-2 text-muted"></i> Trang cá nhân</Link></li>
                      <li><Link to="/orders" className="dropdown-item py-2"><i className="fa fa-list-alt me-2 text-muted"></i> Đơn hàng</Link></li>
                      <li><hr className="dropdown-divider" /></li>
                      <li>
                        <button onClick={handleLogout} className="dropdown-item py-2 text-danger border-0 bg-transparent">
                          <i className="fa fa-power-off me-2"></i> Đăng xuất
                        </button>
                      </li>
                    </>
                  )}
                </ul>
              </div>

              {/* Cart Icon */}
              <Link to="/cart" className="text-white text-center position-relative text-decoration-none">
                <i className="fa fa-shopping-basket fa-lg d-block mb-1"></i>
                <span className="badge rounded-pill bg-white text-danger position-absolute top-0 start-100 translate-middle-x shadow-sm" 
                      style={{ fontSize: '10px', marginTop: '-5px' }}>
                  {cartCount}
                </span>
                <small>Giỏ hàng</small>
              </Link>

            </div>
          </div>
        </div>
      </section>

      {/* Categories Navigation */}
      <nav className="nav-categories border-bottom d-none d-lg-block bg-white">
        <div className="container">
          <ul className="nav d-flex align-items-center">

            {/* Mega Dropdown Danh mục */}
            <li className="nav-item dropdown dropdown-hover">
              <a className="nav-link fw-bold text-dark py-3 dropdown-toggle" href="#" data-bs-toggle="dropdown">
                <i className="fa fa-bars me-2 text-warning"></i>DANH MỤC SẢN PHẨM
              </a>
              <ul className="dropdown-menu shadow border-0 p-2 rounded-3" style={{ minWidth: '250px' }}>
                {loadingCategories ? (
                  <li className="text-center p-3"><div className="spinner-border spinner-border-sm text-warning"></div></li>
                ) : (
                  categories.map((cat, idx) => (
                    <li key={idx}>
                      <button className="dropdown-item rounded-2 py-2" onClick={() => handleCategoryClick(cat)}>
                        {cat.categoryName || cat.name}
                      </button>
                    </li>
                  ))
                )}
              </ul>
            </li>

            <li className="nav-item"><Link to="/brands" className="nav-link text-dark px-3 py-3">Thương hiệu hot</Link></li>
            <li className="nav-item"><Link to="/deals" className="nav-link text-dark px-3 py-3">Khuyến mãi</Link></li>
            <li className="nav-item"><Link to="/flash-sale" className="nav-link text-danger fw-bold px-3 py-3">Flash Sale</Link></li>
          </ul>
        </div>
      </nav>

      <style>{`
        .search-bar-wrap input::placeholder { color: #ccc; font-size: 14px; }
        .dropdown-hover:hover > .dropdown-menu { display: block; margin-top: 0; }
        .nav-link:hover { color: #ff6a00 !important; }
        .dropdown-item:hover { background-color: #fff5ed; color: #ff6a00; }
        .header-main { transition: all 0.3s ease; }
      `}</style>
    </header>
  );
};

export default Header;
