import api from "./api";

const bannerService = {
  // Lấy tất cả banner
  async getAllBanners() {
    try {
      const response = await api.get('/public/banners');
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy danh sách banner:', error);
      throw error;
    }
  },

  // Lấy banner đang hoạt động
  async getActiveBanners() {
    try {
      const response = await api.get('/public/banners/active');
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy banner active:', error);
      throw error;
    }
  },

  // Lấy banner theo ID
  async getBannerById(bannerId) {
    try {
      const response = await api.get(`/public/banners/${bannerId}`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy banner:', error);
      throw error;
    }
  },

  // Lấy ảnh banner
  async getBannerImage(fileName) {
    try {
      const response = await api.get(`/public/banners/image/${fileName}`, {
        responseType: 'blob'
      });
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy ảnh banner:', error);
      throw error;
    }
  }
};

export default bannerService;
