import api from "./api";
const userService = {
  // üü¢ L·∫•y th√¥ng tin user theo ID
  async getUserById(userId) {
    try {
      console.log(`üì° Calling API: /public/users/${userId}`);
      const response = await api.get(`/public/users/${userId}`);
      console.log("‚úÖ User data received:", response.data);
      return response.data;
    } catch (error) {
      console.error("‚ùå Error getting user by ID:", error);
      throw error;
    }
  },
  // src/services/userService.js
  async getUserByEmail(email) {
    try {
      console.log(`üì° Getting user by email: ${email}`);
      const response = await api.get(`/public/users/email/${email}`);
      console.log("‚úÖ User by email - FULL RESPONSE:", response);
      console.log("‚úÖ User by email - DATA:", response.data);

      // ‚úÖ DEBUG: Ki·ªÉm tra c·∫•u tr√∫c response
      if (response.data) {
        console.log("üîç Response structure:", Object.keys(response.data));
        console.log("üîç User ID fields:", {
          user_id: response.data.user_id,
          userId: response.data.userId,
          id: response.data.id
        });
      }
      return response.data;
    } catch (error) {
      console.error("‚ùå Error getting user by email:", error);
      throw error;
    }
  },
  // src/services/userService.js
  async updateUser(userId, userData) {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("No authentication token found");
      }
      const config = {
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        }
      };

      // ‚úÖ S·ª¨A: D√πng camelCase theo backend
      const updateData = {
        firstName: userData.firstName,
        lastName: userData.lastName,
        email: userData.email,
        mobileNumber: userData.mobileNumber
      };
      console.log("üíæ Sending camelCase data to API:", {
        userId: userId,
        data: updateData
      });
      const response = await api.put(`/public/users/${userId}`, updateData, config);
      console.log("‚úÖ Database update successful:", response.data);
      return response.data;
    } catch (error) {
      console.error("‚ùå Database update failed:", error);
      throw error;
    }
  },
  // üü¢ L·∫•y th√¥ng tin user profile (compatibility)
  async getUserProfile(userId) {
    return this.getUserById(userId);
  }
};
export default userService;

